import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

const CardDemandeConge = () => {
  // √âtats du formulaire
  const [formData, setFormData] = useState({
    startDate: '',
    endDate: '',
    reason: '',
    type: 'cong√© pay√©',
    notes: '',
    medicalFile: null
  });
  
  // √âtats de l'interface
  const [fileName, setFileName] = useState('');
  const fileInputRef = useRef(null);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [confirmationMessage, setConfirmationMessage] = useState('');
  const [userData, setUserData] = useState(null);
  const [token, setToken] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const [alertType, setAlertType] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  // Types de cong√©s disponibles
  const congeTypes = [
    { type: 'cong√© pay√©', title: 'Cong√© Pay√© Standard', description: 'Cong√© annuel r√©mun√©r√©', icon: 'üèñÔ∏è' },
    { type: 'rtt', title: 'RTT', description: 'R√©duction du temps de travail', icon: '‚è±Ô∏è' },
    { type: 'cong√© maladie', title: 'Cong√© Maladie', description: 'Absence pour raison m√©dicale', icon: 'üè•' },
    { type: 'cong√© parental', title: 'Cong√© Parental', description: 'Cong√© pour enfant', icon: 'üë∂' },
    { type: 'cong√© familial', title: '√âv√©nement Familial', description: 'Mariage, naissance, d√©c√®s', icon: 'üë™' },
    { type: 'cong√© sans solde', title: 'Cong√© Sans Solde', description: 'Absence non r√©mun√©r√©e', icon: 'üìÖ' },
    { type: 't√©l√©travail', title: 'T√©l√©travail Exceptionnel', description: 'Travail √† distance', icon: 'üè†' }
  ];

  // Chargement des donn√©es utilisateur au montage
  useEffect(() => {
    const userDataStr = localStorage.getItem('userData');
    const authToken = localStorage.getItem('authToken') || 
                     localStorage.getItem('token') || 
                     localStorage.getItem('jwt');

    if (userDataStr) {
      try {
        const userData = JSON.parse(userDataStr);
        setUserData(userData);
        setIsAuthenticated(!!userData.email);
        
        if (authToken) {
          setToken(authToken);
        } else {
          setIsAuthenticated(false);
        }
      } catch (e) {
        console.error("Erreur de parsing userData:", e);
        setIsAuthenticated(false);
      }
    } else {
      setIsAuthenticated(false);
    }
  }, []);

  // Gestion des changements de formulaire
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  // Gestion des fichiers joints
  const handleFileChange = (e) => {
    if (e.target.files?.[0]) {
      const file = e.target.files[0];
      setFormData(prev => ({ ...prev, medicalFile: file }));
      setFileName(file.name);
    }
  };

  // Suppression du fichier
  const removeFile = () => {
    setFormData(prev => ({ ...prev, medicalFile: null }));
    setFileName('');
    if (fileInputRef.current) fileInputRef.current.value = '';
  };

  // Calcul du nombre de jours de cong√©
  const calculateDays = () => {
    if (!formData.startDate || !formData.endDate) return 0;
    const start = new Date(formData.startDate);
    const end = new Date(formData.endDate);
    return Math.ceil((end - start) / (1000 * 60 * 60 * 24)) + 1;
  };

  // Soumission du formulaire
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!isAuthenticated) {
      showAlertMessage('error', 'Veuillez vous connecter pour soumettre une demande');
      return;
    }
    
    if (formData.type === 'cong√© maladie' && !formData.medicalFile) {
      showAlertMessage('error', 'Veuillez joindre un justificatif m√©dical');
      return;
    }

    setIsLoading(true);

    try {
      const formDataToSend = new FormData();
      formDataToSend.append('startDate', formData.startDate);
      formDataToSend.append('endDate', formData.endDate);
      formDataToSend.append('type', formData.type);
      formDataToSend.append('reason', formData.reason || '');
      formDataToSend.append('notes', formData.notes || '');
      formDataToSend.append('nom', userData.nom || '');
      formDataToSend.append('prenom', userData.prenom || '');
      formDataToSend.append('email', userData.email || '');
      if (formData.medicalFile) formDataToSend.append('medicalFile', formData.medicalFile);

      const response = await axios.post('/adddemandeconge', formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'Authorization': token ? `Bearer ${token}` : ''
        }
      });

      if (response.data.success) {
        showAlertMessage('success', 'Demande envoy√©e avec succ√®s');
        resetForm();
        setConfirmationMessage(`Demande de ${formData.type} enregistr√©e !`);
        setShowConfirmation(true);
        setTimeout(() => setShowConfirmation(false), 3000);
      }
    } catch (error) {
      handleSubmissionError(error);
    } finally {
      setIsLoading(false);
    }
  };

  // Affichage des messages d'alerte
  const showAlertMessage = (type, message) => {
    setAlertType(type);
    setAlertMessage(message);
    setShowAlert(true);
  };

  // R√©initialisation du formulaire
  const resetForm = () => {
    setFormData({
      startDate: '',
      endDate: '',
      reason: '',
      type: 'cong√© pay√©',
      notes: '',
      medicalFile: null
    });
    setFileName('');
  };

  // Gestion des erreurs de soumission
  const handleSubmissionError = (error) => {
    console.error('Erreur:', error);
    let errorMsg = "Erreur lors de l'envoi";
    
    if (error.response) {
      if (error.response.status === 401) {
        errorMsg = "Session expir√©e. Veuillez vous reconnecter.";
        setIsAuthenticated(false);
      } else {
        errorMsg = error.response.data.message || error.response.data.error || errorMsg;
      }
    }
    
    showAlertMessage('error', errorMsg);
  };

  // Composant d'alerte
  const Alert = ({ type, message, onClose }) => (
    <div style={{
      padding: '15px',
      borderRadius: '6px',
      marginBottom: '20px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      backgroundColor: type === 'error' ? '#fee2e2' : '#dcfce7',
      color: type === 'error' ? '#b91c1c' : '#16a34a',
      borderLeft: `4px solid ${type === 'error' ? '#b91c1c' : '#16a34a'}`
    }}>
      <div>
        <span style={{ marginRight: '10px' }}>
          {type === 'error' ? '‚ö†Ô∏è' : '‚úÖ'}
        </span>
        {message}
      </div>
      <button 
        onClick={onClose}
        style={{
          background: 'none',
          border: 'none',
          cursor: 'pointer',
          fontSize: '18px',
          color: 'inherit'
        }}
      >
        √ó
      </button>
    </div>
  );

  // Styles
  const styles = {
    container: {
      backgroundColor: '#f0f4f8',
      minHeight: '100vh',
      padding: '20px',
      fontFamily: '"Segoe UI", Roboto, sans-serif'
    },
    mainContent: {
      maxWidth: '1200px',
      margin: '0 auto',
      paddingTop: '3rem'
    },
    header: {
      textAlign: 'center',
      marginBottom: '40px'
    },
    title: {
      fontSize: '2.2rem',
      fontWeight: '700',
      color: '#1e40af',
      marginBottom: '10px',
      position: 'relative'
    },
    subtitle: {
      fontSize: '17px',
      color: '#64748b'
    },
    formContainer: {
      backgroundColor: '#ffffff',
      borderRadius: '12px',
      padding: '30px',
      boxShadow: '0 4px 10px rgba(0,0,0,0.06)'
    },
    formGroup: {
      marginBottom: '20px'
    },
    label: {
      display: 'block',
      fontSize: '15px',
      fontWeight: '600',
      color: '#475569',
      marginBottom: '8px'
    },
    input: {
      width: '100%',
      padding: '12px',
      border: '1px solid #e2e8f0',
      borderRadius: '8px',
      fontSize: '15px'
    },
    select: {
      width: '100%',
      padding: '12px',
      border: '1px solid #e2e8f0',
      borderRadius: '8px',
      fontSize: '15px'
    },
    textarea: {
      width: '100%',
      padding: '12px',
      border: '1px solid #e2e8f0',
      borderRadius: '8px',
      minHeight: '100px',
      resize: 'vertical'
    },
    button: {
      width: '100%',
      padding: '14px',
      backgroundColor: '#2563eb',
      color: '#ffffff',
      border: 'none',
      borderRadius: '8px',
      fontSize: '16px',
      fontWeight: '600',
      cursor: 'pointer'
    },
    fileInputLabel: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '12px',
      border: '1px dashed #3b82f6',
      borderRadius: '8px',
      backgroundColor: '#f1f5f9',
      cursor: 'pointer'
    },
    toastMessage: {
      position: 'fixed',
      bottom: '30px',
      left: '50%',
      transform: 'translateX(-50%)',
      backgroundColor: '#34d399',
      color: 'white',
      borderRadius: '12px',
      padding: '15px 20px',
      zIndex: '1000'
    }
  };

  const selectedCongeType = congeTypes.find(c => c.type === formData.type);
  const needsJustification = ['cong√© maladie', 'cong√© familial'].includes(formData.type);
  const daysRequested = calculateDays();

  return (
    <div style={styles.container}>
      <div style={styles.mainContent}>
        <div style={styles.header}>
          <h1 style={styles.title}>Demande de Cong√©</h1>
          <p style={styles.subtitle}>
            {isAuthenticated 
              ? `Bonjour ${userData?.prenom || ''} ${userData?.nom || ''}, soumettez votre demande`
              : "Veuillez vous connecter pour soumettre une demande"}
          </p>
        </div>

        {showAlert && (
          <Alert 
            type={alertType} 
            message={alertMessage}
            onClose={() => setShowAlert(false)} 
          />
        )}

        {!isAuthenticated && (
          <div style={{
            backgroundColor: '#fff9db',
            borderRadius: '8px',
            padding: '20px',
            marginBottom: '25px',
            borderLeft: '4px solid #f59f00'
          }}>
            <h3 style={{ color: '#d97706', marginTop: 0 }}>
              ‚ö†Ô∏è Authentification requise
            </h3>
            <p style={{ color: '#92400e' }}>
              Veuillez vous connecter pour acc√©der √† cette fonctionnalit√©.
            </p>
          </div>
        )}

        <div style={styles.formContainer}>
          <form onSubmit={handleSubmit}>
            <div style={styles.formGroup}>
              <label style={styles.label}>Type de cong√©</label>
              <select
                name="type"
                value={formData.type}
                onChange={handleChange}
                style={styles.select}
                required
              >
                {congeTypes.map((conge, index) => (
                  <option key={index} value={conge.type}>
                    {conge.icon} {conge.title}
                  </option>
                ))}
              </select>
              <p style={{ fontSize: '14px', color: '#64748b', marginTop: '5px' }}>
                {selectedCongeType?.description}
              </p>
            </div>

            {formData.type === 'cong√© maladie' ? (
              <div style={styles.formGroup}>
                <label style={styles.label}>Justificatif m√©dical *</label>
                <div>
                  <label style={styles.fileInputLabel}>
                    {formData.medicalFile ? 'Changer de fichier' : 'Joindre un justificatif'}
                    <input
                      type="file"
                      ref={fileInputRef}
                      style={{ display: 'none' }}
                      accept=".pdf,.jpg,.jpeg,.png"
                      onChange={handleFileChange}
                      required
                    />
                  </label>
                  {formData.medicalFile && (
                    <div style={{ 
                      display: 'flex', 
                      alignItems: 'center', 
                      marginTop: '10px',
                      padding: '10px',
                      backgroundColor: '#f1f5f9',
                      borderRadius: '6px'
                    }}>
                      <span style={{ marginRight: '10px' }}>üìÑ</span>
                      <span style={{ flex: 1 }}>{fileName}</span>
                      <button 
                        type="button" 
                        onClick={removeFile}
                        style={{
                          background: 'none',
                          border: 'none',
                          color: '#ef4444',
                          cursor: 'pointer'
                        }}
                      >
                        ‚úï
                      </button>
                    </div>
                  )}
                </div>
              </div>
            ) : (
              <div style={styles.formGroup}>
                <label style={styles.label}>
                  Motif {needsJustification && '*'}
                </label>
                <input
                  type="text"
                  name="reason"
                  value={formData.reason}
                  onChange={handleChange}
                  style={styles.input}
                  placeholder={needsJustification 
                    ? "Pr√©cisez le motif (obligatoire)" 
                    : "Pr√©cisez le motif (facultatif)"}
                  required={needsJustification}
                />
              </div>
            )}

            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px', marginBottom: '20px' }}>
              <div style={styles.formGroup}>
                <label style={styles.label}>Date de d√©but *</label>
                <input
                  type="date"
                  name="startDate"
                  value={formData.startDate}
                  onChange={handleChange}
                  style={styles.input}
                  required
                  min={new Date().toISOString().split('T')[0]}
                />
              </div>
              <div style={styles.formGroup}>
                <label style={styles.label}>Date de fin *</label>
                <input
                  type="date"
                  name="endDate"
                  value={formData.endDate}
                  onChange={handleChange}
                  style={styles.input}
                  required
                  min={formData.startDate || new Date().toISOString().split('T')[0]}
                />
              </div>
            </div>

            {formData.startDate && formData.endDate && (
              <div style={{ 
                backgroundColor: '#e0f2fe',
                borderRadius: '8px',
                padding: '14px',
                marginBottom: '20px'
              }}>
                <p style={{ color: '#0369a1', margin: 0 }}>
                  Dur√©e: <strong>{daysRequested} jours</strong>
                </p>
              </div>
            )}

            <div style={styles.formGroup}>
              <label style={styles.label}>Notes compl√©mentaires</label>
              <textarea
                name="notes"
                value={formData.notes}
                onChange={handleChange}
                style={styles.textarea}
                placeholder="Informations additionnelles (facultatif)"
              />
            </div>

            <div style={{ marginTop: '25px' }}>
              <button 
                type="submit" 
                style={{
                  ...styles.button,
                  opacity: isLoading || !isAuthenticated ? 0.7 : 1,
                  cursor: isLoading || !isAuthenticated ? 'not-allowed' : 'pointer'
                }}
                disabled={isLoading || !isAuthenticated}
              >
                {isLoading ? 'Envoi en cours...' : 'Soumettre la demande'}
              </button>
            </div>
          </form>
        </div>
      </div>

      {showConfirmation && (
        <div style={styles.toastMessage}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
            <span style={{
              backgroundColor: 'white',
              color: '#34d399',
              width: '30px',
              height: '30px',
              borderRadius: '50%',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}>
              ‚úì
            </span>
            <p>{confirmationMessage}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default CardDemandeConge;
