import React, { useState } from "react";

export default function ProfileForm() {
  const [formData, setFormData] = useState({
    firstName: "Marine",
    lastName: "Durand",
    email: "marine@example.com",
    phone: "06 12 34 56 78",
    password: "",
    confirmPassword: ""
  });

  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  const handleChange = (e) => {
    const { id, value } = e.target;
    setFormData({
      ...formData,
      [id]: value
    });
    
    // Clear error when field is edited
    if (errors[id]) {
      setErrors({
        ...errors,
        [id]: ""
      });
    }
  };

  const validateForm = () => {
    const newErrors = {};
    
    // Required fields
    ["firstName", "lastName", "email"].forEach(field => {
      if (!formData[field].trim()) {
        newErrors[field] = "Ce champ est obligatoire";
      }
    });
    
    // Email format
    if (formData.email && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = "Format d'email invalide";
    }
    
    // Password confirmation
    if (formData.password && formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = "Les mots de passe ne correspondent pas";
    }
    
    // Password strength
    if (formData.password && formData.password.length < 8 && formData.password.length > 0) {
      newErrors.password = "Le mot de passe doit contenir au moins 8 caract√®res";
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSuccessMessage("");
    
    if (validateForm()) {
      // Simuler une requ√™te API
      setTimeout(() => {
        setSuccessMessage("Profil mis √† jour avec succ√®s!");
        // Faire d√©filer vers le haut pour voir le message
        window.scrollTo({ top: 0, behavior: "smooth" });
      }, 800);
    } else {
      // Faire d√©filer jusqu'√† la premi√®re erreur
      const firstErrorField = document.getElementById(Object.keys(errors)[0]);
      if (firstErrorField) {
        firstErrorField.scrollIntoView({ behavior: "smooth", block: "center" });
        firstErrorField.focus();
      }
    }
  };

  const togglePasswordVisibility = (field) => {
    if (field === 'password') {
      setShowPassword(!showPassword);
    } else {
      setShowConfirmPassword(!showConfirmPassword);
    }
  };

  return (
    <>
      <div className="form-container">
        <div className="form-header">
          <h1 className="header-title">Profil Utilisateur</h1>
        </div>
        
        <div className="form-body">
          {successMessage && (
            <div className="message success-message">
              <span className="message-icon">‚úì</span>
              {successMessage}
            </div>
          )}
          
          <form onSubmit={handleSubmit}>
            <div className="form-section">
              <div className="form-grid">
                <div className="form-column">
                  <div className={`input-group ${errors.firstName ? "has-error" : ""}`}>
                    <label className="input-label" htmlFor="firstName">
                      Pr√©nom <span className="required">*</span>
                    </label>
                    <input
                      id="firstName"
                      type="text"
                      className="input-field"
                      value={formData.firstName}
                      onChange={handleChange}
                    />
                    {errors.firstName && <div className="error-message">{errors.firstName}</div>}
                  </div>
                </div>
                
                <div className="form-column">
                  <div className={`input-group ${errors.lastName ? "has-error" : ""}`}>
                    <label className="input-label" htmlFor="lastName">
                      Nom <span className="required">*</span>
                    </label>
                    <input
                      id="lastName"
                      type="text"
                      className="input-field"
                      value={formData.lastName}
                      onChange={handleChange}
                    />
                    {errors.lastName && <div className="error-message">{errors.lastName}</div>}
                  </div>
                </div>
              </div>

              <div className="form-grid">
                <div className="form-column">
                  <div className={`input-group ${errors.email ? "has-error" : ""}`}>
                    <label className="input-label" htmlFor="email">
                      Adresse email <span className="required">*</span>
                    </label>
                    <input
                      id="email"
                      type="email"
                      className="input-field"
                      value={formData.email}
                      onChange={handleChange}
                    />
                    {errors.email && <div className="error-message">{errors.email}</div>}
                  </div>
                </div>
                
                <div className="form-column">
                  <div className={`input-group ${errors.phone ? "has-error" : ""}`}>
                    <label className="input-label" htmlFor="phone">
                      T√©l√©phone
                    </label>
                    <input
                      id="phone"
                      type="tel"
                      className="input-field"
                      value={formData.phone}
                      onChange={handleChange}
                      placeholder="Ex: 06 12 34 56 78"
                    />
                    {errors.phone && <div className="error-message">{errors.phone}</div>}
                  </div>
                </div>
              </div>

              <div className="form-divider"></div>
              
              <div className="form-grid">
                <div className="form-column">
                  <div className={`input-group ${errors.password ? "has-error" : ""}`}>
                    <label className="input-label" htmlFor="password">
                      Mot de passe
                    </label>
                    <div className="password-container">
                      <input
                        id="password"
                        type={showPassword ? "text" : "password"}
                        className="input-field password-field"
                        value={formData.password}
                        onChange={handleChange}
                        placeholder="Laissez vide pour conserver l'actuel"
                      />
                      <button 
                        type="button" 
                        className="password-toggle" 
                        onClick={() => togglePasswordVisibility('password')}
                        aria-label={showPassword ? "Cacher le mot de passe" : "Montrer le mot de passe"}
                      >
                        {showPassword ? "üëÅÔ∏è" : "üëÅÔ∏è‚Äçüó®Ô∏è"}
                      </button>
                    </div>
                    {errors.password && <div className="error-message">{errors.password}</div>}
                  </div>
                </div>
                
                <div className="form-column">
                  <div className={`input-group ${errors.confirmPassword ? "has-error" : ""}`}>
                    <label className="input-label" htmlFor="confirmPassword">
                      Confirmer le mot de passe
                    </label>
                    <div className="password-container">
                      <input
                        id="confirmPassword"
                        type={showConfirmPassword ? "text" : "password"}
                        className="input-field password-field"
                        value={formData.confirmPassword}
                        onChange={handleChange}
                        placeholder="Confirmer le mot de passe"
                      />
                      <button 
                        type="button" 
                        className="password-toggle" 
                        onClick={() => togglePasswordVisibility('confirmPassword')}
                        aria-label={showConfirmPassword ? "Cacher le mot de passe" : "Montrer le mot de passe"}
                      >
                        {showConfirmPassword ? "üëÅÔ∏è" : "üëÅÔ∏è‚Äçüó®Ô∏è"}
                      </button>
                    </div>
                    {errors.confirmPassword && <div className="error-message">{errors.confirmPassword}</div>}
                  </div>
                </div>
              </div>
            </div>

            <div className="form-actions">
              <button type="button" className="btn-cancel">
                Annuler
              </button>
              <button type="submit" className="btn-save">
                Enregistrer
              </button>
            </div>
          </form>
        </div>
      </div>
      
      <style jsx>{`
        /* Variables */
        :root {
          --primary-color: #3cb878;
          --primary-hover: #2a9e61;
          --primary-light: #def4ea;
          --text-primary: #2c3e50;
          --text-secondary: #546e7a;
          --danger-color: #e53935;
          --success-color: #2e7d32;
          --bg-white: #ffffff;
          --bg-light: #f9fafb;
          --bg-gray: #f4f6f8;
          --border-color: #e0e6ed;
          --border-focus: #3cb878;
          --shadow-sm: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
          --shadow: 0 2px 4px rgba(0, 0, 0, 0.08);
          --shadow-md: 0 4px 8px rgba(0, 0, 0, 0.12);
          --font-sans: 'Inter', -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif;
          --transition: all 0.2s ease;
        }

        /* Base Styles */
        * {
          box-sizing: border-box;
          margin: 0;
          padding: 0;
        }

        /* Container */
        .form-container {
          font-family: var(--font-sans);
          background-color: var(--bg-white);
          border-radius: 8px;
          box-shadow: var(--shadow-md);
          margin: 2rem auto;
          max-width: 900px;
          width: 100%;
          position: relative;
          transition: var(--transition);
          border: 1px solid var(--border-color);
        }

        /* Header */
        .form-header {
          background: linear-gradient(to right, #3cb878, #4ecca3);
          color: var(--bg-white);
          padding: 1.75rem 2rem;
          border-radius: 8px 8px 0 0;
        }

        .header-title {
          font-size: 1.5rem;
          font-weight: 600;
          margin: 0;
          letter-spacing: -0.01em;
        }

        /* Form Body */
        .form-body {
          padding: 2rem;
        }

        /* Message */
        .message {
          margin-bottom: 2rem;
          padding: 1rem 1.25rem;
          border-radius: 6px;
          display: flex;
          align-items: center;
          animation: fadeIn 0.3s ease-out;
        }

        .success-message {
          background-color: rgba(60, 184, 120, 0.1);
          color: var(--success-color);
          border-left: 4px solid var(--success-color);
        }

        .message-icon {
          margin-right: 0.75rem;
          font-weight: bold;
        }

        /* Grid Layout */
        .form-grid {
          display: grid;
          grid-template-columns: repeat(1, 1fr);
          gap: 1.5rem;
          margin-bottom: 1.5rem;
        }

        @media (min-width: 640px) {
          .form-grid {
            grid-template-columns: repeat(2, 1fr);
          }
        }

        .form-column {
          width: 100%;
        }

        /* Input Group */
        .input-group {
          margin-bottom: 0.5rem;
        }

        .input-label {
          display: block;
          margin-bottom: 0.5rem;
          font-size: 0.875rem;
          font-weight: 500;
          color: var(--text-secondary);
        }

        .required {
          color: var(--danger-color);
          margin-left: 2px;
        }

        .input-field {
          width: 100%;
          padding: 0.75rem 1rem;
          border: 1px solid var(--border-color);
          border-radius: 6px;
          font-size: 0.95rem;
          color: var(--text-primary);
          background-color: var(--bg-light);
          transition: var(--transition);
        }

        .input-field:hover {
          border-color: #bdc8d5;
        }

        .input-field:focus {
          outline: none;
          border-color: var(--border-focus);
          box-shadow: 0 0 0 3px rgba(60, 184, 120, 0.2);
          background-color: var(--bg-white);
        }

        .input-field::placeholder {
          color: var(--text-secondary);
          opacity: 0.6;
        }

        /* Password Field */
        .password-container {
          position: relative;
          display: flex;
          align-items: center;
        }

        .password-field {
          padding-right: 2.5rem;
        }

        .password-toggle {
          position: absolute;
          right: 0.75rem;
          background: none;
          border: none;
          cursor: pointer;
          font-size: 1.25rem;
          display: flex;
          align-items: center;
          justify-content: center;
          color: var(--text-secondary);
          transition: var(--transition);
        }

        .password-toggle:hover {
          color: var(--primary-color);
        }

        /* Error Styling */
        .has-error .input-field {
          border-color: var(--danger-color);
          background-color: rgba(229, 57, 53, 0.05);
        }

        .error-message {
          color: var(--danger-color);
          font-size: 0.75rem;
          margin-top: 0.375rem;
          font-weight: 500;
        }

        /* Divider */
        .form-divider {
          height: 1px;
          background-color: var(--border-color);
          margin: 1.5rem 0 2rem;
        }

        /* Buttons */
        .form-actions {
          display: flex;
          justify-content: flex-end;
          gap: 1rem;
          margin-top: 2rem;
        }

        .btn-cancel, .btn-save {
          padding: 0.75rem 1.5rem;
          border-radius: 6px;
          font-weight: 600;
          font-size: 0.875rem;
          cursor: pointer;
          transition: var(--transition);
          border: none;
          box-shadow: var(--shadow-sm);
          letter-spacing: 0.01em;
        }

        .btn-cancel {
          background-color: var(--bg-white);
          color: var(--text-secondary);
          border: 1px solid var(--border-color);
        }

        .btn-cancel:hover {
          background-color: var(--bg-light);
          color: var(--text-primary);
        }

        .btn-save {
          background-color: var(--primary-color);
          color: white;
        }

        .btn-save:hover {
          background-color: var(--primary-hover);
          box-shadow: var(--shadow);
        }

        /* Animations */
        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(10px); }
          to { opacity: 1; transform: translateY(0); }
        }

        .form-container {
          animation: fadeIn 0.3s ease-out;
        }
      `}</style>
    </>
  );
}
