import { useState, useEffect } from 'react';
import { Search, Edit, Trash, Plus, X, Check, Filter, Eye } from 'lucide-react';

// Injection CSS personnalisée 
const styles = `
  .personnel-container {
    background: white;
    border-radius: 8px;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
    overflow: hidden;
  }
  
  .header-section {
    padding: 1.5rem;
    border-bottom: 1px solid #e2e8f0;
  }
  
  .search-container {
    display: flex;
    flex-wrap: wrap;
    gap: 1rem;
    margin-bottom: 1.5rem;
  }
  
  .search-input {
    flex: 1;
    position: relative;
  }
  
  .search-icon {
    position: absolute;
    left: 10px;
    top: 50%;
    transform: translateY(-50%);
    color: #9ca3af;
  }
  
  .tab-button {
    padding: 0.5rem 1rem;
    border-bottom: 2px solid transparent;
    color: #6b7280;
    font-weight: 500;
    transition: all 0.2s ease;
  }
  
  .tab-button.active {
    border-bottom-color: #14b8a6;
    color: #14b8a6;
  }
  
  .tab-button:hover:not(.active) {
    color: #374151;
  }
  
  .table-container {
    width: 100%;
    overflow-x: auto;
  }
  
  table {
    width: 100%;
    border-collapse: collapse;
  }
  
  th {
    text-align: left;
    padding: 0.75rem 1rem;
    font-size: 0.75rem;
    font-weight: 600;
    text-transform: uppercase;
    color: #6b7280;
    background-color: #f9fafb;
    border-bottom: 1px solid #e5e7eb;
  }
  
  td {
    padding: 1rem;
    vertical-align: middle;
    color: #374151;
    border-bottom: 1px solid #f3f4f6;
  }
  
  tr:hover {
    background-color: #f9fafb;
  }
  
  .department-badge {
    background-color: #e6fffa;
    color: #047857;
    padding: 0.25rem 0.5rem;
    border-radius: 9999px;
    font-size: 0.75rem;
    font-weight: 500;
  }
  
  .status-badge {
    background-color: #dcfce7;
    color: #166534;
    padding: 0.25rem 0.5rem;
    border-radius: 9999px;
    font-size: 0.75rem;
    font-weight: 500;
  }
  
  .action-button {
    padding: 0.375rem;
    border-radius: 0.375rem;
    transition: all 0.2s ease;
  }
  
  .view-button {
    background-color: #e6fffa;
    color: #0d9488;
  }
  
  .view-button:hover {
    background-color: #ccfbf1;
  }
  
  .edit-button {
    background-color: #dbeafe;
    color: #3b82f6;
  }
  
  .edit-button:hover {
    background-color: #bfdbfe;
  }
  
  .delete-button {
    background-color: #fee2e2;
    color: #ef4444;
  }
  
  .delete-button:hover {
    background-color: #fecaca;
  }
  
  .add-button {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    background-color: #14b8a6;
    color: white;
    padding: 0.5rem 1rem;
    border-radius: 0.375rem;
    font-weight: 500;
    transition: all 0.2s ease;
  }
  
  .add-button:hover {
    background-color: #0d9488;
  }
  
  .pagination {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-top: 1.5rem;
    padding: 0 1rem;
  }
  
  .page-button {
    padding: 0.375rem 0.75rem;
    border: 1px solid #e5e7eb;
    border-radius: 0.375rem;
    font-size: 0.875rem;
    transition: all 0.2s ease;
  }
  
  .page-button.active {
    background-color: #14b8a6;
    color: white;
    border-color: #14b8a6;
  }
  
  .page-button:hover:not(.active) {
    background-color: #f9fafb;
  }
  
  .employee-image {
    width: 2.5rem;
    height: 2.5rem;
    border-radius: 9999px;
    object-fit: cover;
    border: 2px solid #99f6e4;
  }
`;

// Composant Modal pour les détails
const DetailsModal = ({ employe, onClose }) => {
  if (!employe) return null;
  
  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg shadow-lg p-6 w-full max-w-md">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-xl font-bold text-gray-800">Détails de l'employé</h2>
          <button onClick={onClose} className="text-gray-500 hover:text-gray-700">
            <X size={24} />
          </button>
        </div>
        
        <div className="flex flex-col items-center mb-6">
          <img src={employe.image} alt="Employé" className="w-24 h-24 rounded-full mb-3" />
          <h3 className="text-xl font-bold text-gray-800">{employe.nom}</h3>
          <p className="text-gray-500">{employe.departement}</p>
        </div>
        
        <div className="space-y-4">
          <div className="flex border-b pb-3">
            <span className="font-medium text-gray-700 w-1/3">ID:</span>
            <span className="text-gray-600">{employe.id}</span>
          </div>
          <div className="flex border-b pb-3">
            <span className="font-medium text-gray-700 w-1/3">Sexe:</span>
            <span className="text-gray-600">{employe.sexe}</span>
          </div>
          <div className="flex border-b pb-3">
            <span className="font-medium text-gray-700 w-1/3">Email:</span>
            <span className="text-gray-600">{employe.email}</span>
          </div>
          <div className="flex border-b pb-3">
            <span className="font-medium text-gray-700 w-1/3">Département:</span>
            <span className="text-gray-600">{employe.departement}</span>
          </div>
          <div className="flex border-b pb-3">
            <span className="font-medium text-gray-700 w-1/3">Date d'embauche:</span>
            <span className="text-gray-600">{employe.dateEmbauche || "01/01/2023"}</span>
          </div>
          <div className="flex">
            <span className="font-medium text-gray-700 w-1/3">Statut:</span>
            <span className="px-2 py-1 rounded-full text-xs font-medium bg-green-100 text-green-800">
              Actif
            </span>
          </div>
        </div>
        
        <div className="mt-8 flex justify-end">
          <button 
            onClick={onClose}
            className="py-2 px-4 bg-gray-100 text-gray-700 rounded-md hover:bg-gray-200"
          >
            Fermer
          </button>
        </div>
      </div>
    </div>
  );
};

// Composant de toast pour les notifications
const Toast = ({ message, type, onClose }) => {
  const bgColor = type === 'success' ? 'bg-green-500' : 
                 type === 'error' ? 'bg-red-500' : 
                 type === 'info' ? 'bg-blue-500' : 'bg-gray-500';
  
  useEffect(() => {
    const timer = setTimeout(() => {
      onClose();
    }, 3000);
    
    return () => clearTimeout(timer);
  }, [onClose]);
  
  return (
    <div className={`fixed bottom-4 right-4 ${bgColor} text-white p-4 rounded-md shadow-lg flex items-center gap-2 z-50`}>
      {type === 'success' && <Check size={20} />}
      {type === 'error' && <X size={20} />}
      {type === 'info' && <Search size={20} />}
      <p>{message}</p>
    </div>
  );
};

// Composant principal de gestion du personnel
export default function GestionPersonnel() {
  // Données des employés
  const [employes, setEmployes] = useState([
    { id: 1, image: "/api/placeholder/50/50", nom: "Jean Dupont", email: "jean.dupont@example.com", departement: "Marketing", sexe: "Homme", dateEmbauche: "15/02/2022" },
    { id: 2, image: "/api/placeholder/50/50", nom: "Marie Lambert", email: "marie.lambert@example.com", departement: "RH", sexe: "Femme", dateEmbauche: "03/05/2023" },
    { id: 3, image: "/api/placeholder/50/50", nom: "Paul Martin", email: "paul.martin@example.com", departement: "Technique", sexe: "Homme", dateEmbauche: "10/12/2021" },
    { id: 4, image: "/api/placeholder/50/50", nom: "Sophie Bernard", email: "sophie.bernard@example.com", departement: "Finance", sexe: "Femme", dateEmbauche: "22/08/2023" }
  ]);
  
  const [employesAffiches, setEmployesAffiches] = useState([]);
  const [recherche, setRecherche] = useState("");
  const [filtreType, setFiltreType] = useState("nom");
  const [filtreDepartement, setFiltreDepartement] = useState("tous");
  const [modalOuvert, setModalOuvert] = useState(false);
  const [detailsModalOuvert, setDetailsModalOuvert] = useState(false);
  const [employeActuel, setEmployeActuel] = useState(null);
  const [employeDetails, setEmployeDetails] = useState(null);
  const [toast, setToast] = useState({ affiche: false, message: "", type: "" });

  // Nouvel employé vide pour le formulaire d'ajout
  const employeVide = { 
    id: "", 
    image: "/api/placeholder/50/50", 
    nom: "", 
    email: "", 
    departement: "", 
    sexe: "",
    dateEmbauche: new Date().toLocaleDateString('fr-FR')
  };
  
  // Filtrer les employés en fonction de la recherche et du département
  useEffect(() => {
    let filtres = [...employes];
    
    // Filtre par département
    if (filtreDepartement !== "tous") {
      filtres = filtres.filter(e => e.departement === filtreDepartement);
    }
    
    // Filtre par recherche
    if (recherche !== "") {
      filtres = filtres.filter(employe => {
        if (filtreType === "nom") {
          return employe.nom.toLowerCase().includes(recherche.toLowerCase());
        } else {
          return employe.id.toString().includes(recherche);
        }
      });
    }
    
    setEmployesAffiches(filtres);
  }, [recherche, filtreType, filtreDepartement, employes]);

  // Afficher un toast
  const afficherToast = (message, type) => {
    setToast({ affiche: true, message, type });
  };

  // Fermer le toast
  const fermerToast = () => {
    setToast({ ...toast, affiche: false });
  };

  // Ouvrir le modal pour ajouter/modifier un employé
  const ouvrirModal = (employe = null) => {
    setEmployeActuel(employe || { ...employeVide, id: employes.length > 0 ? Math.max(...employes.map(e => e.id)) + 1 : 1 });
    setModalOuvert(true);
  };

  // Fermer le modal
  const fermerModal = () => {
    setModalOuvert(false);
    setEmployeActuel(null);
  };
  
  // Ouvrir le modal de détails
  const ouvrirDetailsModal = (employe) => {
    setEmployeDetails(employe);
    setDetailsModalOuvert(true);
  };
  
  // Fermer le modal de détails
  const fermerDetailsModal = () => {
    setDetailsModalOuvert(false);
    setEmployeDetails(null);
  };

  // Gérer les changements dans le formulaire
  const handleChange = (e) => {
    const { name, value } = e.target;
    setEmployeActuel(prev => ({ ...prev, [name]: value }));
  };

  // Sauvegarder l'employé (ajout ou modification)
  const sauvegarderEmploye = () => {
    // Vérifier si tous les champs sont remplis
    if (!employeActuel.nom || !employeActuel.email || !employeActuel.departement || !employeActuel.sexe) {
      afficherToast("Veuillez remplir tous les champs", "error");
      return;
    }

    // Vérifier si c'est une modification ou un ajout
    const index = employes.findIndex(e => e.id === employeActuel.id);
    
    if (index !== -1) {
      // Modification
      const nouveauxEmployes = [...employes];
      nouveauxEmployes[index] = employeActuel;
      setEmployes(nouveauxEmployes);
      afficherToast("Employé modifié avec succès", "success");
    } else {
      // Ajout
      setEmployes([...employes, employeActuel]);
      afficherToast("Employé ajouté avec succès", "success");
    }
    
    fermerModal();
  };

  // Supprimer un employé
  const supprimerEmploye = (id) => {
    setEmployes(employes.filter(e => e.id !== id));
    afficherToast("Employé supprimé avec succès", "error");
  };

  return (
    <>
      {/* Injection du CSS */}
      <style>{styles}</style>
      
      <div className="personnel-container">
        <div className="header-section">
          <h1 className="text-2xl font-bold mb-6 text-gray-800">Gestion du Personnel</h1>
          
          {/* Partie recherche et filtration */}
          <div className="search-container">
            <div className="search-input">
              <div className="search-icon">
                <Search size={20} />
              </div>
              <input
                type="text"
                placeholder={`Rechercher par ${filtreType === 'nom' ? 'nom' : 'ID'}`}
                value={recherche}
                onChange={(e) => setRecherche(e.target.value)}
                className="pl-10 pr-4 py-2 border rounded-md w-full focus:outline-none focus:ring-2 focus:ring-teal-500"
              />
            </div>
            
            <div className="flex items-center gap-2">
              <Filter size={20} className="text-gray-400" />
              <select 
                value={filtreType} 
                onChange={(e) => setFiltreType(e.target.value)}
                className="p-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-teal-500"
              >
                <option value="nom">Filtrer par Nom</option>
                <option value="id">Filtrer par ID</option>
              </select>
            </div>
            
            <button 
              onClick={() => ouvrirModal()}
              className="add-button"
            >
              <Plus size={20} />
              Ajouter
            </button>
          </div>
          
          {/* Onglets de filtration */}
          <div className="flex mb-6 border-b">
            <button 
              className={`tab-button ${filtreDepartement === "tous" ? "active" : ""}`}
              onClick={() => setFiltreDepartement("tous")}
            >
              Tous
            </button>
            <button 
              className={`tab-button ${filtreDepartement === "Marketing" ? "active" : ""}`}
              onClick={() => setFiltreDepartement("Marketing")}
            >
              Marketing
            </button>
            <button 
              className={`tab-button ${filtreDepartement === "Technique" ? "active" : ""}`}
              onClick={() => setFiltreDepartement("Technique")}
            >
              Technique
            </button>
            <button 
              className={`tab-button ${filtreDepartement === "RH" ? "active" : ""}`}
              onClick={() => setFiltreDepartement("RH")}
            >
              RH
            </button>
            <button 
              className={`tab-button ${filtreDepartement === "Finance" ? "active" : ""}`}
              onClick={() => setFiltreDepartement("Finance")}
            >
              Finance
            </button>
          </div>
          
          {/* Tableau des employés */}
          <div className="table-container">
            <table>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Image</th>
                  <th>Nom</th>
                  <th>Email</th>
                  <th>Département</th>
                  <th>Sexe</th>
                  <th className="text-center">Statut</th>
                  <th className="text-center">Actions</th>
                </tr>
              </thead>
              <tbody>
                {employesAffiches.length > 0 ? (
                  employesAffiches.map((employe) => (
                    <tr key={employe.id}>
                      <td>{employe.id}</td>
                      <td>
                        <img src={employe.image} alt="Employé" className="employee-image" />
                      </td>
                      <td className="font-medium">{employe.nom}</td>
                      <td>{employe.email}</td>
                      <td>
                        <span className="department-badge">
                          {employe.departement}
                        </span>
                      </td>
                      <td>{employe.sexe}</td>
                      <td className="text-center">
                        <span className="status-badge">
                          Actif
                        </span>
                      </td>
                      <td>
                        <div className="flex justify-center gap-2">
                          <button 
                            onClick={() => ouvrirDetailsModal(employe)} 
                            className="action-button view-button"
                            title="Voir détails"
                          >
                            <Eye size={16} />
                          </button>
                          <button 
                            onClick={() => ouvrirModal(employe)} 
                            className="action-button edit-button"
                            title="Modifier"
                          >
                            <Edit size={16} />
                          </button>
                          <button 
                            onClick={() => supprimerEmploye(employe.id)} 
                            className="action-button delete-button"
                            title="Supprimer"
                          >
                            <Trash size={16} />
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="8" className="py-6 text-center text-gray-500">
                      Aucun employé trouvé
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
          
          {/* Pagination */}
          <div className="pagination">
            <div className="text-sm text-gray-500">
              Affichage de {employesAffiches.length} sur {employes.length} employés
            </div>
            <div className="flex gap-2">
              <button className="page-button">
                Précédent
              </button>
              <button className="page-button active">
                1
              </button>
              <button className="page-button">
                2
              </button>
              <button className="page-button">
                Suivant
              </button>
            </div>
          </div>
        </div>
      </div>
      
      {/* Modal pour ajouter/modifier un employé */}
      {modalOuvert && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg shadow-lg p-6 w-full max-w-md">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-xl font-bold text-gray-800">
                {employeActuel && employes.some(e => e.id === employeActuel.id) 
                  ? "Modifier un employé" 
                  : "Ajouter un employé"
                }
              </h2>
              <button onClick={fermerModal} className="text-gray-500 hover:text-gray-700">
                <X size={24} />
              </button>
            </div>
            
            <div className="space-y-4">
              <div>
                <label className="block text-gray-700 mb-1 text-sm font-medium">ID</label>
                <input 
                  type="number" 
                  name="id" 
                  value={employeActuel?.id || ""} 
                  disabled 
                  className="w-full p-2 border rounded-md bg-gray-100"
                />
              </div>
              
              <div>
                <label className="block text-gray-700 mb-1 text-sm font-medium">Nom</label>
                <input 
                  type="text" 
                  name="nom" 
                  value={employeActuel?.nom || ""} 
                  onChange={handleChange} 
                  className="w-full p-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-teal-500"
                />
              </div>
              
              <div>
                <label className="block text-gray-700 mb-1 text-sm font-medium">Email</label>
                <input 
                  type="email" 
                  name="email" 
                  value={employeActuel?.email || ""} 
                  onChange={handleChange} 
                  className="w-full p-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-teal-500"
                />
              </div>
              
              <div>
                <label className="block text-gray-700 mb-1 text-sm font-medium">Département</label>
                <select 
                  name="departement" 
                  value={employeActuel?.departement || ""} 
                  onChange={handleChange} 
                  className="w-full p-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-teal-500"
                >
                  <option value="">Sélectionner</option>
                  <option value="Marketing">Marketing</option>
                  <option value="RH">RH</option>
                  <option value="Technique">Technique</option>
                  <option value="Finance">Finance</option>
                </select>
              </div>
              
              <div>
                <label className="block text-gray-700 mb-1 text-sm font-medium">Sexe</label>
                <select 
                  name="sexe" 
                  value={employeActuel?.sexe || ""} 
                  onChange={handleChange} 
                  className="w-full p-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-teal-500"
                >
                  <option value="">Sélectionner</option>
                  <option value="Homme">Homme</option>
                  <option value="Femme">Femme</option>
                </select>
              </div>
              
              <div>
                <label className="block text-gray-700 mb-1 text-sm font-medium">Date d'embauche</label>
                <input 
                  type="text" 
                  name="dateEmbauche" 
                  value={employeActuel?.dateEmbauche || ""} 
                  onChange={handleChange} 
                  className="w-full p-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-teal-500"
                  placeholder="JJ/MM/AAAA"
                />
              </div>
              
              <div className="flex justify-end gap-2 mt-6">
                <button 
                  onClick={fermerModal} 
                  className="py-2 px-4 bg-gray-100 text-gray-700 rounded-md hover:bg-gray-200 transition-colors"
                >
                  Annuler
                </button>
                <button 
                  onClick={sauvegarderEmploye} 
                  className="py-2 px-4 bg-teal-500 text-white rounded-md hover:bg-teal-600 transition-colors"
                >
                  Sauvegarder
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
      
      {/* Modal pour afficher les détails */}
      {detailsModalOuvert && (
        <DetailsModal employe={employeDetails} onClose={fermerDetailsModal} />
      )}
      
      {/* Toast notifications */}
      {toast.affiche && (
        <Toast 
          message={toast.message} 
          type={toast.type} 
          onClose={fermerToast} 
        />
      )}
    </>
  );
}
