import React, { useState, useEffect } from "react";
import axios from "axios";

// Components
import CardStats from "components/Cards/CardStats.js";

export default function HeaderStats() {
  const [statistics, setStatistics] = useState({
    departments: "0",
    personnel: "0",
    formations: "0"
  });
  
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStatistics = async () => {
      setLoading(true);
      
      try {
        // Utilisation de Promise.all pour les requêtes parallèles
        const [personnelResponse, departmentResponse, formationResponse] = await Promise.all([
          axios.get('/allpersonnel'),
          axios.get('/alldepartment'),
          axios.get('/getformation')
        ]);
        
        setStatistics({
          personnel: personnelResponse.data.length.toString(),
          departments: departmentResponse.data.length.toString(),
          formations: formationResponse.data.length.toString()
        });
      } catch (error) {
        console.error("Erreur lors de la récupération des statistiques:", error);
        setStatistics({
          personnel: "Erreur",
          departments: "Erreur", 
          formations: "Erreur"
        });
      } finally {
        setLoading(false);
      }
    };

    fetchStatistics();
  }, []);

  // Données pour les cartes statistiques
  const statsCards = [
    {
      title: statistics.personnel,
      subtitle: "Effectif Total",
      trend: "up",
      percentage: "3.2",
      trendColor: "text-emerald-500",
      description: "Depuis le mois dernier",
      icon: "fas fa-users",
      iconBg: "bg-gradient-to-r from-blue-500 to-blue-600"
    },
    {
      title: statistics.departments,
      subtitle: "Départements",
      trend: "up",
      percentage: "2.1",
      trendColor: "text-emerald-500",
      description: "Depuis la semaine dernière",
      icon: "fas fa-building",
      iconBg: "bg-gradient-to-r from-purple-500 to-purple-600"
    },
    {
      title: statistics.formations,
      subtitle: "Formations Actives",
      trend: "up",
      percentage: "1.5",
      trendColor: "text-emerald-500",
      description: "Depuis hier",
      icon: "fas fa-chalkboard-teacher",
      iconBg: "bg-gradient-to-r from-teal-500 to-teal-600"
    }
  ];

  return (
    <>
      {/* Header */}
      <div className="relative bg-gradient-to-r from-cyan-600 to-teal-500 md:pt-32 pb-32 pt-12">
        <div className="px-4 md:px-10 mx-auto w-full">
          <div>
            {/* Titre de section */}
            <div className="mb-6">
              <h2 className="text-white text-2xl font-bold mb-1">Tableau de bord</h2>
              <p className="text-cyan-100 opacity-80">Vue d'ensemble de l'organisation</p>
            </div>
            
            {/* Card stats */}
            <div className="flex flex-wrap">
              {loading ? (
                <div className="w-full text-center py-4">
                  <span className="text-white opacity-80">Chargement des statistiques...</span>
                </div>
              ) : (
                statsCards.map((card, index) => (
                  <div key={index} className="w-full lg:w-6/12 xl:w-4/12 px-4 mb-4 md:mb-0">
                    <CardStats
                      statSubtitle={card.subtitle}
                      statTitle={card.title}
                      statArrow={card.trend}
                      statPercent={card.percentage}
                      statPercentColor={card.trendColor}
                      statDescripiron={card.description}
                      statIconName={card.icon}
                      statIconColor={card.iconBg}
                    />
                  </div>
                ))
              )}
            </div>
          </div>
        </div>
        
        {/* Décoration d'arrière-plan */}
        <div className="absolute bottom-0 left-0 right-0 h-1/2 pointer-events-none">
          <svg className="absolute bottom-0 overflow-hidden" xmlns="http://www.w3.org/2000/svg" preserveAspectRatio="none" version="1.1" viewBox="0 0 2560 100" x="0" y="0">
            <polygon className="text-white fill-current" points="2560 0 2560 100 0 100"></polygon>
          </svg>
        </div>
      </div>
    </>
  );
}
