import React, { useState, useEffect } from 'react';
import { Form, Button, Card, Row, Col, Alert } from 'react-bootstrap';
import axios from 'axios';

const CardDemandeAvance = () => {
  const [formData, setFormData] = useState({
    nom: '',
    prenom: '',
    email: '',
    montant: '',
    type: '',
    motif: ''
  });

  const [errors, setErrors] = useState({});
  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const [alertVariant, setAlertVariant] = useState('success');
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    // Récupérer les données de l'utilisateur connecté
    const user = JSON.parse(localStorage.getItem('user'));
    if (user) {
      setUserData(user);
      setFormData(prev => ({
        ...prev,
        nom: user.nom || '',
        prenom: user.prenom || '',
        email: user.email || ''
      }));
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });

    // Effacer l'erreur quand l'utilisateur tape
    if (errors[name]) {
      setErrors({
        ...errors,
        [name]: null
      });
    }
  };

  const validateForm = () => {
    const newErrors = {};
    if (!formData.nom) newErrors.nom = 'Le nom est requis';
    if (!formData.prenom) newErrors.prenom = 'Le prénom est requis';
    if (!formData.email) newErrors.email = 'L\'email est requis';
    if (!formData.montant) newErrors.montant = 'Le montant est requis';
    if (!formData.type) newErrors.type = 'Le type est requis';
    if (!formData.motif) newErrors.motif = 'Le motif est requis';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    try {
      const response = await axios.post('http://localhost:3000/api/demandeAvance/add', {
        ...formData,
        statut: 'En attente' // Statut par défaut
      });

      if (response.data.message) {
        // Afficher l'alerte de succès
        setAlertMessage('Demande d\'avance soumise avec succès!');
        setAlertVariant('success');
        setShowAlert(true);
        
        // Réinitialiser le formulaire
        setFormData({
          nom: userData?.nom || '',
          prenom: userData?.prenom || '',
          email: userData?.email || '',
          montant: '',
          type: '',
          motif: ''
        });

        // Cacher l'alerte après 5 secondes
        setTimeout(() => {
          setShowAlert(false);
        }, 5000);
      }
    } catch (error) {
      console.error('Erreur lors de la soumission:', error);
      
      // Définir le message d'erreur
      let message = "Une erreur s'est produite lors de la soumission";
      if (error.response?.data?.message) {
        message = error.response.data.message;
      }

      // Afficher l'alerte d'erreur
      setAlertMessage(message);
      setAlertVariant('danger');
      setShowAlert(true);

      // Cacher l'alerte après 5 secondes
      setTimeout(() => {
        setShowAlert(false);
      }, 5000);
    }
  };

  return (
    <div className="container mt-4">
      <Card>
        <Card.Header as="h5">Demande d'avance</Card.Header>
        <Card.Body>
          {/* Alerte pour succès/erreur */}
          {showAlert && (
            <Alert 
              variant={alertVariant} 
              onClose={() => setShowAlert(false)} 
              dismissible
              className="mt-3"
            >
              {alertMessage}
            </Alert>
          )}

          <Form onSubmit={handleSubmit}>
            <Row>
              <Col md={6}>
                <Form.Group controlId="formNom" className="mb-3">
                  <Form.Label>Nom</Form.Label>
                  <Form.Control
                    type="text"
                    name="nom"
                    value={formData.nom}
                    onChange={handleChange}
                    isInvalid={!!errors.nom}
                    readOnly
                  />
                  <Form.Control.Feedback type="invalid">
                    {errors.nom}
                  </Form.Control.Feedback>
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group controlId="formPrenom" className="mb-3">
                  <Form.Label>Prénom</Form.Label>
                  <Form.Control
                    type="text"
                    name="prenom"
                    value={formData.prenom}
                    onChange={handleChange}
                    isInvalid={!!errors.prenom}
                    readOnly
                  />
                  <Form.Control.Feedback type="invalid">
                    {errors.prenom}
                  </Form.Control.Feedback>
                </Form.Group>
              </Col>
            </Row>

            <Form.Group controlId="formEmail" className="mb-3">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                isInvalid={!!errors.email}
                readOnly
              />
              <Form.Control.Feedback type="invalid">
                {errors.email}
              </Form.Control.Feedback>
            </Form.Group>

            <Row>
              <Col md={6}>
                <Form.Group controlId="formMontant" className="mb-3">
                  <Form.Label>Montant</Form.Label>
                  <Form.Control
                    type="number"
                    name="montant"
                    value={formData.montant}
                    onChange={handleChange}
                    isInvalid={!!errors.montant}
                  />
                  <Form.Control.Feedback type="invalid">
                    {errors.montant}
                  </Form.Control.Feedback>
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group controlId="formType" className="mb-3">
                  <Form.Label>Type d'avance</Form.Label>
                  <Form.Select
                    name="type"
                    value={formData.type}
                    onChange={handleChange}
                    isInvalid={!!errors.type}
                  >
                    <option value="">Sélectionner un type</option>
                    <option value="Avance sur salaire">Avance sur salaire</option>
                    <option value="Avance exceptionnelle">Avance exceptionnelle</option>
                  </Form.Select>
                  <Form.Control.Feedback type="invalid">
                    {errors.type}
                  </Form.Control.Feedback>
                </Form.Group>
              </Col>
            </Row>

            <Form.Group controlId="formMotif" className="mb-3">
              <Form.Label>Motif</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                name="motif"
                value={formData.motif}
                onChange={handleChange}
                isInvalid={!!errors.motif}
              />
              <Form.Control.Feedback type="invalid">
                {errors.motif}
              </Form.Control.Feedback>
            </Form.Group>

            <div className="d-grid gap-2">
              <Button variant="primary" type="submit">
                Soumettre la demande
              </Button>
            </div>
          </Form>
        </Card.Body>
      </Card>
    </div>
  );
};

export default CardDemandeAvance;
