import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CardDemandeAvance = () => {
  const [formData, setFormData] = useState({
    montant: '',
    motif: '',
    type: 'standard',

  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [token, setToken] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userSalaire, setUserSalaire] = useState(0);
  const [userData, setUserData] = useState(null);
  const [toast, setToast] = useState(null);

  useEffect(() => {
    // Récupérer les données utilisateur depuis localStorage
    const userDataStr = localStorage.getItem('userData');
    
    if (userDataStr) {
      try {
        const userData = JSON.parse(userDataStr);
        setUserData(userData);
        setIsAuthenticated(!!userData.email);
        
        // Récupérer le token
        const authToken = localStorage.getItem('authToken') || 
                         localStorage.getItem('token') || 
                         localStorage.getItem('jwt');
        if (authToken) {
          setToken(authToken);
        }
        
        // Récupérer le salaire
        if (userData.salaire) {
          setUserSalaire(userData.salaire);
        } else {
          showToast("warning", "Salaire non trouvé", "Impossible de déterminer votre salaire. Contactez l'administration.");
        }
      } catch (e) {
        console.error("Erreur de parsing userData:", e);
      }
    } else {
      setIsAuthenticated(false);
    }
  }, []);

  const showToast = (type, title, message, duration = 5000) => {
    const toastId = Date.now();
    setToast({
      id: toastId,
      type,
      title,
      message
    });
    
    // Fermer automatiquement après la durée spécifiée
    setTimeout(() => {
      setToast(prev => prev?.id === toastId ? null : prev);
    }, duration);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    
    if (name === 'montant') {
      const montant = parseFloat(value);
      
      if (montant > userSalaire) {
        showToast("error", "Montant invalide", `Le montant demandé (${montant} TND) dépasse votre salaire mensuel (${userSalaire} TND)`);
      }
    }
    
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Vérifier si le montant est supérieur au salaire
    if (parseFloat(formData.montant) > userSalaire) {
      showToast("error", "Montant invalide", `Le montant demandé (${formData.montant} TND) dépasse votre salaire mensuel (${userSalaire} TND)`);
      return;
    }
    
    // Vérifier si on a les données utilisateur nécessaires
    if (!userData) {
      showToast("error", "Erreur", "Impossible de récupérer vos informations. Veuillez vous reconnecter.");
      return;
    }
    
    setLoading(true);
    setError(null);
    
    try {
      // Préparer les données à envoyer
      const demandeData = {
        ...formData,
        nom: userData.nom || '',
        prenom: userData.prenom || '',
        email: userData.email || '',
        salaire: userSalaire
      };

      // Envoyer la demande au backend
      const response = await axios.post(
        '/adddemandeavance',
        demandeData,
        {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': token ? `Bearer ${token}` : ''
          }
        }
      );

      if (response.data.success) {
        showToast("success", "Succès", "Votre demande a été soumise avec succès!");
        
        // Réinitialiser le formulaire après succès
        setFormData({
          montant: '',
          motif: '',
          type: 'standard'
        });
      }
    } catch (err) {
      const errorMessage = err.response?.data?.message || err.message || 'Une erreur est survenue';
      showToast("error", "Erreur", errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const styles = {
    container: {
      width: '100%',
      maxWidth: '600px',
      margin: '0 auto',
      padding: '20px',
      backgroundColor: '#f5f7fa',
      fontFamily: 'Arial, sans-serif',
      position: 'relative'
    },
    header: {
      textAlign: 'center',
      marginBottom: '20px',
      padding: '20px 0' 
    },
    title: {
      fontSize: '28px',
      fontWeight: '700',
      color: '#1e40af',
      margin: '0 0 10px 0',
      position: 'relative',
      display: 'inline-block',
      paddingBottom: '15px'
    },
    titleAfter: {
      content: '""',
      position: 'absolute',
      bottom: 0,
      left: '50%',
      transform: 'translateX(-50%)',
      width: '60%',
      height: '4px',
      background: 'linear-gradient(90deg, #1e40af, #3b82f6)',
      borderRadius: '2px'
    },
    subtitle: {
      fontSize: '16px',
      color: '#64748b',
      margin: '10px 0 0 0',
      letterSpacing: '0.3px'
    },
    form: {
      backgroundColor: '#ffffff',
      borderRadius: '8px',
      padding: '25px',
      boxShadow: '0 2px 6px rgba(0,0,0,0.1)'
    },
    formGroup: {
      marginBottom: '20px'
    },
    label: {
      display: 'block',
      marginBottom: '8px',
      fontWeight: '500',
      fontSize: '14px',
      color: '#334155'
    },
    input: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      boxSizing: 'border-box'
    },
    select: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      backgroundColor: '#fff',
      boxSizing: 'border-box'
    },
    textarea: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      minHeight: '100px',
      resize: 'vertical',
      boxSizing: 'border-box'
    },
    infoText: {
      fontSize: '12px',
      color: '#64748b',
      marginTop: '5px',
      fontStyle: 'italic'
    },
    submitBtn: {
      width: '100%',
      padding: '12px',
      backgroundColor: '#2563eb',
      color: '#ffffff',
      borderRadius: '4px',
      fontWeight: '500',
      border: 'none',
      cursor: 'pointer',
      fontSize: '16px',
      transition: 'background-color 0.3s',
      marginTop: '10px'
    },
    submitBtnHover: {
      backgroundColor: '#1d4ed8'
    },
    loading: {
      display: 'inline-block',
      marginLeft: '10px'
    },
    toastContainer: {
      position: 'fixed',
      top: '20px',
      right: '20px',
      zIndex: 1000,
      maxWidth: '350px'
    },
    toast: {
      position: 'relative',
      padding: '16px 20px',
      borderRadius: '8px',
      boxShadow: '0 4px 12px rgba(0,0,0,0.15)',
      display: 'flex',
      alignItems: 'flex-start',
      marginBottom: '16px',
      transform: 'translateX(0)',
      transition: 'all 0.3s ease',
      overflow: 'hidden',
      color: '#fff'
    },
    toastSuccess: {
      backgroundColor: '#4BB543',
      borderLeft: '4px solid #3a9e33'
    },
    toastError: {
      backgroundColor: '#FF3333',
      borderLeft: '4px solid #e60000'
    },
    toastWarning: {
      backgroundColor: '#FFCC00',
      borderLeft: '4px solid #e6b800',
      color: '#333'
    },
    toastIcon: {
      marginRight: '12px',
      fontSize: '20px',
      flexShrink: 0
    },
    toastContent: {
      flex: 1
    },
    toastTitle: {
      fontWeight: '600',
      marginBottom: '4px',
      fontSize: '16px'
    },
    toastMessage: {
      fontSize: '14px',
      lineHeight: '1.4'
    },
    toastClose: {
      background: 'transparent',
      border: 'none',
      color: 'inherit',
      cursor: 'pointer',
      marginLeft: '12px',
      fontSize: '16px',
      opacity: 0.7,
      transition: 'opacity 0.2s',
      padding: 0,
      '&:hover': {
        opacity: 1
      }
    },
    progressBar: {
      position: 'absolute',
      bottom: 0,
      left: 0,
      height: '3px',
      backgroundColor: 'rgba(255,255,255,0.5)',
      width: '100%',
      transformOrigin: 'left',
      animation: 'progressAnimation 5s linear forwards'
    }
  };

  const Toast = ({ toast }) => {
    if (!toast) return null;

    const toastStyles = {
      ...styles.toast,
      ...(toast.type === 'success' ? styles.toastSuccess : 
          toast.type === 'error' ? styles.toastError : 
          styles.toastWarning)
    };

    const getIcon = () => {
      switch(toast.type) {
        case 'success': return '✓';
        case 'error': return '✕';
        case 'warning': return '⚠️';
        default: return 'ℹ️';
      }
    };

    return (
      <div style={toastStyles}>
        <div style={styles.toastIcon}>{getIcon()}</div>
        <div style={styles.toastContent}>
          <div style={styles.toastTitle}>{toast.title}</div>
          <div style={styles.toastMessage}>{toast.message}</div>
        </div>
        <button 
          style={styles.toastClose} 
          onClick={() => setToast(null)}
        >
          ×
        </button>
        <div style={styles.progressBar}></div>
      </div>
    );
  };

  return (
    <div style={styles.container}>
      {/* Toast Container */}
      {toast && (
        <div style={styles.toastContainer}>
          <Toast toast={toast} />
        </div>
      )}

      <div style={styles.header}>
        <h1 style={styles.title}>
          Demande d'avance sur salaire
          <span style={styles.titleAfter}></span>
        </h1>
        <p style={styles.subtitle}>Remplissez ce formulaire pour soumettre votre demande</p>
      </div>

      <form style={styles.form} onSubmit={handleSubmit}>
        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="montant">Montant demandé (TND)</label>
          <input
            type="number"
            id="montant"
            name="montant"
            style={styles.input}
            value={formData.montant}
            onChange={handleChange}
            required
            min="1"
            max={userSalaire}
          />
          <p style={styles.infoText}>
            Le montant maximum est de {userSalaire} TND (votre salaire mensuel).
          </p>
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="type">Type de demande</label>
          <select
            id="type"
            name="type"
            style={styles.select}
            value={formData.type}
            onChange={handleChange}
            required
          >
            <option value="standard">Standard (traitement sous 3 jours)</option>
            <option value="urgent">Urgente (traitement sous 24h)</option>
          </select>
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="motif">Motif de la demande</label>
          <textarea
            id="motif"
            name="motif"
            style={styles.textarea}
            value={formData.motif}
            onChange={handleChange}
            required
            placeholder="Décrivez brièvement la raison de votre demande d'avance"
          />
        </div>

        <button 
          type="submit" 
          style={styles.submitBtn}
          onMouseOver={(e) => e.target.style.backgroundColor = '#1d4ed8'}
          onMouseOut={(e) => e.target.style.backgroundColor = '#2563eb'}
          disabled={loading}
        >
          {loading ? 'Envoi en cours...' : 'Soumettre ma demande'}
        </button>
      </form>
    </div>
  );
};

export default CardDemandeAvance;
