import { useState, useEffect } from "react";
import { Search, AlertCircle, Check, X, Eye } from "lucide-react";
import axios from "axios";

// Styles encapsulés
const styles = `
  .container { max-width: 1200px; margin: 0 auto; padding: 20px; }
  .table { width: 100%; border-collapse: collapse; }
  .th { padding: 12px; text-align: left; background: #f8fafc; }
  .td { padding: 12px; border-bottom: 1px solid #e2e8f0; }
  .badge {
    display: inline-flex;
    align-items: center;
    padding: 4px 8px;
    border-radius: 12px;
    font-size: 12px;
  }
  .badge-accepted { background: #dcfce7; color: #166534; }
  .badge-pending { background: #fef3c7; color: #92400e; }
  .badge-rejected { background: #fee2e2; color: #b91c1c; }
  .action-btn {
    padding: 6px 12px;
    margin: 0 4px;
    border-radius: 6px;
    cursor: pointer;
  }
  .accept-btn { background: #dcfce7; color: #166534; }
  .reject-btn { background: #fee2e2; color: #b91c1c; }
`;

export default function GestionFormations() {
  const [demandes, setDemandes] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Chargement initial des données
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('/alldemandeformation');
        setDemandes(response.data);
        setLoading(false);
      } catch (err) {
        setError("Erreur lors du chargement des demandes");
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Filtrage des demandes
  const filteredDemandes = demandes.filter(demande => {
    const searchLower = searchTerm.toLowerCase();
    return (
      demande.nom.toLowerCase().includes(searchLower) ||
      demande.prenom.toLowerCase().includes(searchLower) ||
      demande.nomFormation.toLowerCase().includes(searchLower)
    );
  });

  // Formatage de date
  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('fr-FR');
  };

  // Mise à jour du statut
  const updateStatut = async (id, newStatus) => {
    try {
      // Conversion des statuts frontend → backend
      const statusMap = {
        accepte: 'Approuvée',
        refuse: 'Rejetée',
        en_cours: 'En attente'
      };

      const backendStatus = statusMap[newStatus];
      if (!backendStatus) throw new Error("Statut invalide");

      // Appel API pour mettre à jour le statut
      await axios.put(`/demandeformation/${id}`, { statut: backendStatus });

      // Mise à jour locale optimiste
      setDemandes(prev => prev.map(item => 
        item._id === id ? { ...item, statut: backendStatus } : item
      ));

      alert(`Statut mis à jour avec succès : ${backendStatus}`);
    } catch (err) {
      console.error("Erreur de mise à jour:", err);
      alert("Échec de la mise à jour: " + (err.response?.data?.message || err.message));
    }
  };

  // Rendu du badge de statut
  const renderStatusBadge = (status) => {
    switch (status) {
      case 'Approuvée':
        return (
          <span className="badge badge-accepted">
            <Check size={14} style={{ marginRight: 4 }} />
            Accepté
          </span>
        );
      case 'En attente':
        return (
          <span className="badge badge-pending">
            <AlertCircle size={14} style={{ marginRight: 4 }} />
            En cours
          </span>
        );
      case 'Rejetée':
        return (
          <span className="badge badge-rejected">
            <X size={14} style={{ marginRight: 4 }} />
            Rejeté
          </span>
        );
      default:
        return <span>{status}</span>;
    }
  };

  return (
    <div className="container">
      <style>{styles}</style>
      
      <h1>Gestion des Demandes de Formation</h1>
      
      <div style={{ marginBottom: 20 }}>
        <div style={{ position: 'relative' }}>
          <Search size={18} style={{ position: 'absolute', left: 10, top: '50%', transform: 'translateY(-50%)' }} />
          <input
            type="text"
            placeholder="Rechercher..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            style={{
              padding: '8px 12px 8px 36px',
              width: '100%',
              maxWidth: 400,
              border: '1px solid #ddd',
              borderRadius: 4
            }}
          />
        </div>
      </div>

      {loading ? (
        <p>Chargement en cours...</p>
      ) : error ? (
        <p style={{ color: 'red' }}>{error}</p>
      ) : (
        <div style={{ overflowX: 'auto' }}>
          <table className="table">
            <thead>
              <tr>
                <th className="th">Nom</th>
                <th className="th">Prénom</th>
                <th className="th">Formation</th>
                <th className="th">Statut</th>
                <th className="th">Date</th>
                <th className="th">Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredDemandes.length === 0 ? (
                <tr>
                  <td colSpan={6} style={{ textAlign: 'center', padding: 20 }}>
                    Aucune demande trouvée
                  </td>
                </tr>
              ) : (
                filteredDemandes.map(demande => (
                  <tr key={demande._id}>
                    <td className="td">{demande.nom}</td>
                    <td className="td">{demande.prenom}</td>
                    <td className="td">{demande.nomFormation}</td>
                    <td className="td">
                      {renderStatusBadge(demande.statut)}
                    </td>
                    <td className="td">
                      {formatDate(demande.dateDemande)}
                    </td>
                    <td className="td">
                      {demande.statut === 'En attente' && (
                        <div>
                          <button
                            className="action-btn accept-btn"
                            onClick={() => updateStatut(demande._id, 'accepte')}
                          >
                            <Check size={14} /> Accepter
                          </button>
                          <button
                            className="action-btn reject-btn"
                            onClick={() => updateStatut(demande._id, 'refuse')}
                          >
                            <X size={14} /> Rejeter
                          </button>
                        </div>
                      )}
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
