import { useState, useRef, useEffect } from 'react';

const CardDemandeConge = () => {
  const [formData, setFormData] = useState({
    startDate: '',
    endDate: '',
    reason: '',
    type: 'cong√© pay√©',
    notes: '',
    medicalFile: null
  });
  
  const [fileName, setFileName] = useState('');
  const fileInputRef = useRef(null);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [confirmationMessage, setConfirmationMessage] = useState('');
  const [userData, setUserData] = useState(null);
  const [token, setToken] = useState('');
  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const [alertType, setAlertType] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const congeTypes = [
    {
      type: 'cong√© pay√©',
      title: 'Cong√© Pay√© Standard',
      description: 'Cong√© annuel r√©mun√©r√©',
      remainingDays: userData?.soldeConge || 0,
      icon: 'üèñÔ∏è'
    },
    {
      type: 'rtt',
      title: 'RTT',
      description: 'R√©duction du temps de travail',
      remainingDays: 8,
      icon: '‚è±Ô∏è'
    },
    {
      type: 'cong√© maladie',
      title: 'Cong√© Maladie',
      description: 'Absence pour raison m√©dicale',
      remainingDays: 'Illimit√©',
      icon: 'üè•'
    },
    {
      type: 'cong√© parental',
      title: 'Cong√© Parental',
      description: 'Cong√© pour s\'occuper d\'un enfant',
      remainingDays: 'Selon droits',
      icon: 'üë∂'
    },
    {
      type: 'cong√© familial',
      title: '√âv√©nement Familial',
      description: 'Mariage, naissance, d√©c√®s',
      remainingDays: 'Selon √©v√©nement',
      icon: 'üë™'
    },
    {
      type: 'cong√© sans solde',
      title: 'Cong√© Sans Solde',
      description: 'Absence autoris√©e non r√©mun√©r√©e',
      remainingDays: 'Illimit√©',
      icon: 'üìÖ'
    },
    {
      type: 't√©l√©travail',
      title: 'T√©l√©travail Exceptionnel',
      description: 'Journ√©e de travail √† distance',
      remainingDays: 'Selon accord',
      icon: 'üè†'
    }
  ];

  useEffect(() => {
    const loadUserData = async () => {
      try {
        const userDataStr = localStorage.getItem('userData');
        
        if (userDataStr) {
          const parsedData = JSON.parse(userDataStr);
          setUserData({
            nom: parsedData.nom || '',
            prenom: parsedData.prenom || '',
            email: parsedData.email || '',
            soldeConge: parsedData.soldeConge || parsedData.soldedeconge || 0,
            _id: parsedData._id || ''
          });
          setIsAuthenticated(!!parsedData.email);
        }
  
        // Ne pas afficher d'alerte ici, seulement stocker le token s'il existe
        const authToken = localStorage.getItem('authToken') || 
                         localStorage.getItem('token') || 
                         localStorage.getItem('jwt');
        if (authToken) {
          setToken(authToken);
        }
      } catch (error) {
        console.error("Erreur lors du chargement des donn√©es:", error);
      }
    };
  
    loadUserData();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };
  
  const handleFileChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      setFormData(prev => ({
        ...prev,
        medicalFile: file
      }));
      setFileName(file.name);
    }
  };
  
  const removeFile = () => {
    setFormData(prev => ({
      ...prev,
      medicalFile: null
    }));
    setFileName('');
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!userData || !userData.email) {
      setShowAlert(true);
      setAlertType('error');
      setAlertMessage("Authentification requise. Veuillez vous connecter.");
      return;
    }
    
    const authToken = token || localStorage.getItem('authToken') || 
                    localStorage.getItem('token') || 
                    localStorage.getItem('jwt');
    
    if (!authToken) {
      setShowAlert(true);
      setAlertType('error');
      setAlertMessage("Authentification requise. Veuillez vous connecter.");
      return;
    }
  
    if (formData.type === 'cong√© maladie' && !formData.medicalFile) {
      setShowAlert(true);
      setAlertType('error');
      setAlertMessage('Veuillez joindre votre justificatif m√©dical');
      return;
    }
  
    setIsLoading(true);
  
    try {
      // Simuler une r√©ponse API r√©ussie apr√®s un d√©lai de 1 seconde
      setTimeout(() => {
        setConfirmationMessage(`Demande de ${formData.type} du ${formData.startDate} au ${formData.endDate} enregistr√©e !`);
        setShowConfirmation(true);
        setShowAlert(true);
        setAlertType('success');
        setAlertMessage('Votre demande de cong√© a √©t√© soumise avec succ√®s!');
        
        // R√©initialiser le formulaire
        setFormData({
          startDate: '',
          endDate: '',
          reason: '',
          type: 'cong√© pay√©',
          notes: '',
          medicalFile: null
        });
        setFileName('');
        setIsLoading(false);
        
        // Cacher la confirmation apr√®s 3 secondes
        setTimeout(() => {
          setShowConfirmation(false);
        }, 3000);
      }, 1000);
    } catch (error) {
      console.error('Erreur:', error);
      
      setShowAlert(true);
      setAlertType('error');
      setAlertMessage("Erreur lors de la soumission");
      setIsLoading(false);
    }
  };

  const calculateDays = () => {
    if (!formData.startDate || !formData.endDate) return 0;
    
    const start = new Date(formData.startDate);
    const end = new Date(formData.endDate);
    const diffTime = Math.abs(end - start);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24)) + 1;
    
    return diffDays;
  };

  const Alert = ({ type, message, onClose }) => {
    return (
      <div className={`flex items-center justify-between p-4 mb-4 rounded-md ${
        type === 'error' ? 'bg-red-100 text-red-700 border-l-4 border-red-700' : 
        'bg-green-100 text-green-700 border-l-4 border-green-700'
      }`}>
        <div className="flex items-center">
          <span className="mr-2">
            {type === 'error' ? '‚ö†Ô∏è' : '‚úÖ'}
          </span>
          {message}
        </div>
        <button 
          className="text-xl bg-transparent border-none cursor-pointer"
          onClick={onClose}
          aria-label="Fermer l'alerte"
        >
          √ó
        </button>
      </div>
    );
  };

  const selectedCongeType = congeTypes.find(conge => conge.type === formData.type);
  const isMaladieType = formData.type === 'cong√© maladie';
  const needsJustification = ['cong√© maladie', 'cong√© familial'].includes(formData.type);
  
  if (!isAuthenticated) {
    return (
      <div className="bg-gray-100 min-h-screen p-6 font-sans text-gray-700">
        <div className="max-w-6xl mx-auto pt-32">
          <div className="text-center mb-10 py-5">
            <h1 className="text-4xl font-bold text-blue-800 mb-2 relative inline-block pb-4">
              Demande de Cong√©
              <span className="absolute bottom-0 left-1/2 transform -translate-x-1/2 w-3/5 h-1 bg-gradient-to-r from-blue-800 to-blue-500 rounded"></span>
            </h1>
            <p className="text-lg text-gray-500 mt-2">
              Authentification requise pour acc√©der √† cette fonctionnalit√©
            </p>
          </div>
          {showAlert && (
            <Alert 
              type={alertType} 
              message={alertMessage}
              onClose={() => setShowAlert(false)}
            />
          )}
        </div>
      </div>
    );
  }
  
  return (
    <div className="bg-gray-100 min-h-screen p-6 font-sans text-gray-700">
      <div className="max-w-6xl mx-auto pt-32">
        <div className="text-center mb-10 py-5">
          <h1 className="text-4xl font-bold text-blue-800 mb-2 relative inline-block pb-4">
            Demande de Cong√©
            <span className="absolute bottom-0 left-1/2 transform -translate-x-1/2 w-3/5 h-1 bg-gradient-to-r from-blue-800 to-blue-500 rounded"></span>
          </h1>
          <p className="text-lg text-gray-500 mt-2">
            {userData?.email
              ? `Bonjour ${userData.prenom} ${userData.nom}, d√©couvrez et soumettez vos demandes de cong√©s en quelques clics`
              : "Bienvenue sur le syst√®me de gestion des cong√©s"}
          </p>
        </div>

        {showAlert && (
          <Alert 
            type={alertType} 
            message={alertMessage}
            onClose={() => setShowAlert(false)}
          />
        )}

        <div className="bg-white rounded-xl p-8 mb-8 shadow-md border border-gray-100">
          <div className="mb-6 border-b border-gray-200 pb-4">
            <h2 className="text-2xl font-bold text-blue-800 mb-2">Formulaire de demande de cong√©</h2>
            <p className="text-gray-500 text-md">Veuillez remplir tous les champs obligatoires</p>
          </div>
          
          <form onSubmit={handleSubmit}>
            <div className="mb-5">
              <label className="block text-gray-600 font-semibold mb-2" htmlFor="type">
                Type de cong√©
              </label>
              <div className="relative">
                <select
                  id="type"
                  name="type"
                  value={formData.type}
                  onChange={handleChange}
                  className="w-full p-3 border border-gray-200 rounded-lg text-md shadow-sm"
                  required
                >
                  {congeTypes.map((conge, index) => (
                    <option key={index} value={conge.type}>
                      {conge.icon} {conge.title}
                    </option>
                  ))}
                </select>
                <div className="absolute right-4 top-1/2 transform -translate-y-1/2 pointer-events-none w-3 h-3 border-l-2 border-b-2 border-gray-400 rotate-45"></div>
              </div>
              <span className="block text-gray-500 text-sm mt-1">
                {selectedCongeType?.description} - 
                {typeof selectedCongeType?.remainingDays === 'number' 
                  ? ` ${selectedCongeType.remainingDays} jours disponibles` 
                  : ` ${selectedCongeType?.remainingDays}`}
              </span>
            </div>
            
            {isMaladieType ? (
              <div className="mb-5">
                <label className="block text-gray-600 font-semibold mb-2" htmlFor="medicalFile">
                  Justificatif m√©dical *
                </label>
                <div className="relative">
                  <label className="flex items-center justify-center p-3 border border-dashed border-blue-500 rounded-lg bg-gray-50 text-gray-600 text-md cursor-pointer min-h-12" htmlFor="medicalFile">
                    {formData.medicalFile ? 'Changer de fichier' : 'Joindre votre justificatif m√©dical'}
                  </label>
                  <input
                    type="file"
                    id="medicalFile"
                    ref={fileInputRef}
                    className="absolute top-0 left-0 opacity-0 w-full h-full cursor-pointer"
                    accept=".pdf,.jpg,.jpeg,.png"
                    onChange={handleFileChange}
                    required={isMaladieType}
                  />
                </div>
                {formData.medicalFile && (
                  <div className="flex items-center gap-2 mt-2 p-2 bg-gray-50 rounded-md text-sm">
                    <span className="text-xl text-blue-500">üìÑ</span>
                    <span className="flex-1 overflow-hidden text-ellipsis whitespace-nowrap">{fileName}</span>
                    <button 
                      type="button" 
                      className="text-red-500 border-none bg-transparent cursor-pointer px-1 text-md"
                      onClick={removeFile}
                      aria-label="Supprimer le fichier"
                    >
                      ‚úï
                    </button>
                  </div>
                )}
              </div>
            ) : (
              <div className="mb-5">
                <label className="block text-gray-600 font-semibold mb-2" htmlFor="reason">
                  Motif {needsJustification && '*'}
                </label>
                <input
                  type="text"
                  id="reason"
                  name="reason"
                  value={formData.reason}
                  onChange={handleChange}
                  className="w-full p-3 border border-gray-200 rounded-lg text-md shadow-sm"
                  placeholder={needsJustification 
                    ? "Pr√©cisez le motif (obligatoire)" 
                    : "Pr√©cisez le motif de votre demande (facultatif)"}
                  required={needsJustification}
                />
              </div>
            )}
            
            <div className="grid grid-cols-1 md:grid-cols-2 gap-5 mb-5">
              <div>
                <label className="block text-gray-600 font-semibold mb-2" htmlFor="startDate">
                  Date de d√©but *
                </label>
                <input
                  type="date"
                  id="startDate"
                  name="startDate"
                  value={formData.startDate}
                  onChange={handleChange}
                  className="w-full p-3 border border-gray-200 rounded-lg text-md shadow-sm"
                  required
                  min={new Date().toISOString().split('T')[0]}
                />
              </div>
              
              <div>
                <label className="block text-gray-600 font-semibold mb-2" htmlFor="endDate">
                  Date de fin *
                </label>
                <input
                  type="date"
                  id="endDate"
                  name="endDate"
                  value={formData.endDate}
                  onChange={handleChange}
                  className="w-full p-3 border border-gray-200 rounded-lg text-md shadow-sm"
                  required
                  min={formData.startDate || new Date().toISOString().split('T')[0]}
                />
              </div>
            </div>
            
            {formData.startDate && formData.endDate && (
              <div className="bg-blue-50 rounded-lg p-4 mb-5 flex items-center gap-2">
                <span className="text-blue-600 text-lg">‚ÑπÔ∏è</span>
                <p className="text-blue-600 font-medium m-0">
                  Dur√©e: <strong>{calculateDays()} jours</strong>
                  {formData.type === 'cong√© pay√©' && userData?.soldeConge && (
                    <span> - Solde restant: {userData.soldeConge - calculateDays()} jours</span>
                  )}
                </p>
              </div>
            )}
            
            <div className="mb-5">
              <label className="block text-gray-600 font-semibold mb-2" htmlFor="notes">
                Notes compl√©mentaires
              </label>
              <textarea
                id="notes"
                name="notes"
                value={formData.notes}
                onChange={handleChange}
                className="w-full p-3 border border-gray-200 rounded-lg text-md shadow-sm min-h-24 resize-y"
                placeholder="Informations additionnelles (facultatif)"
              />
            </div>
            
            <div className="mt-6">
              <button 
                type="submit" 
                className={`w-full p-4 bg-blue-600 text-white border-none rounded-lg text-lg font-semibold cursor-pointer transition-all shadow-md hover:bg-blue-700 ${
                  isLoading ? 'opacity-70 cursor-not-allowed' : ''
                }`}
                disabled={isLoading}
              >
                {isLoading ? 'Envoi en cours...' : 'Soumettre la demande'}
              </button>
            </div>
          </form>
        </div>
      </div>

      {showConfirmation && (
        <div className="fixed bottom-8 left-1/2 transform -translate-x-1/2 bg-green-500 text-white rounded-xl shadow-lg z-10 min-w-72 max-w-lg animate-toast">
          <div className="flex items-center gap-3 p-4">
            <span className="bg-white text-green-500 w-8 h-8 rounded-full flex items-center justify-center text-lg flex-shrink-0">‚úì</span>
            <p className="m-0">{confirmationMessage}</p>
          </div>
        </div>
      )}

      <style jsx>{`
        @keyframes slideUpFadeOut {
          0% { transform: translate(-50%, 100%); opacity: 0; }
          15% { transform: translate(-50%, 0); opacity: 1; }
          85% { transform: translate(-50%, 0); opacity: 1; }
          100% { transform: translate(-50%, 0); opacity: 0; }
        }
        .animate-toast {
          animation: slideUpFadeOut 3s forwards;
        }
      `}</style>
    </div>
  );
};

export default CardDemandeConge;
