import { useState } from "react";
import { Search, AlertCircle, Check, X, Eye } from "lucide-react";

export default function GestionAvances() {
  // Données statiques pour remplacer les appels API
  const donneesStatiques = [
    {
      id: "1",
      personnelId: "101",
      personnel: {
        nom: "Dupont",
        prenom: "Jean",
        role: "Ingénieur"
      },
      montant: 1200,
      createdAt: "2025-03-12T10:30:00",
      type: "standard",
      status: "pending",
      motif: "Besoin d'une avance pour faire face à des dépenses imprévues liées à une réparation urgente de ma voiture qui est essentielle pour mes déplacements professionnels.",
      reponse: null
    },
    {
      id: "2",
      personnelId: "102",
      personnel: {
        nom: "Martin",
        prenom: "Sophie",
        role: "Comptabilité"
      },
      montant: 800,
      createdAt: "2025-04-01T14:15:00",
      type: "urgent",
      status: "approved",
      motif: "Avance sur salaire pour frais médicaux non prévus.",
      reponse: "Demande approuvée le 02/04/2025"
    },
    {
      id: "3",
      personnelId: "103",
      personnel: {
        nom: "Petit",
        prenom: "Marie",
        role: "Marketing"
      },
      montant: 1500,
      createdAt: "2025-04-05T09:45:00",
      type: "standard",
      status: "rejected",
      motif: "Avance pour formation professionnelle complémentaire.",
      reponse: "Demande refusée le 06/04/2025"
    },
    {
      id: "4",
      personnelId: "104",
      personnel: {
        nom: "Leroy",
        prenom: "Thomas",
        role: "Développeur"
      },
      montant: 2000,
      createdAt: "2025-04-10T11:20:00",
      type: "urgent",
      status: "pending",
      motif: "Besoin d'une avance pour couvrir les frais de déménagement suite à ma mutation.",
      reponse: null
    },
    {
      id: "5",
      personnelId: "105",
      personnel: {
        nom: "Girard",
        prenom: "Lucie",
        role: "Ressources Humaines"
      },
      montant: 500,
      createdAt: "2025-04-15T16:00:00",
      type: "standard",
      status: "approved",
      motif: "Avance pour l'achat d'équipement de télétravail.",
      reponse: "Demande approuvée le 16/04/2025"
    }
  ];

  const [demandes, setDemandes] = useState(donneesStatiques);
  const [demandesFiltrees, setDemandesFiltrees] = useState(donneesStatiques);
  const [recherche, setRecherche] = useState("");
  const [demandeSelected, setDemandeSelected] = useState(null);
  const [modalVisible, setModalVisible] = useState(false);

  // Formater la date pour l'affichage
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('fr-FR');
  };

  // Filtrage des demandes basé sur la recherche
  const filtrerDemandes = (terme) => {
    setRecherche(terme);
    if (!terme.trim()) {
      setDemandesFiltrees(demandes);
      return;
    }
    
    const termeLower = terme.toLowerCase();
    const resultats = demandes.filter(demande => {
      return (
        demande.personnel.nom.toLowerCase().includes(termeLower) ||
        demande.personnel.prenom.toLowerCase().includes(termeLower) ||
        demande.montant.toString().includes(terme)
      );
    });
    
    setDemandesFiltrees(resultats);
  };

  // Ouvrir le modal avec les détails de la demande
  const ouvrirModal = (demande) => {
    setDemandeSelected(demande);
    setModalVisible(true);
  };

  // Fermer le modal
  const fermerModal = () => {
    setModalVisible(false);
    setDemandeSelected(null);
  };

  // Actions des boutons
  const handleApprove = (id) => {
    const demandesUpdated = demandes.map(demande => 
      demande.id === id 
        ? {...demande, status: 'approved', reponse: `Demande approuvée le ${new Date().toLocaleDateString('fr-FR')}`} 
        : demande
    );
    
    setDemandes(demandesUpdated);
    setDemandesFiltrees(
      demandesUpdated.filter(demande => {
        if (recherche.trim() === "") return true;
        
        const termeLower = recherche.toLowerCase();
        return (
          demande.personnel.nom.toLowerCase().includes(termeLower) ||
          demande.personnel.prenom.toLowerCase().includes(termeLower) ||
          demande.montant.toString().includes(recherche)
        );
      })
    );
    
    alert(`La demande d'avance a été approuvée.`);
  };

  const handleReject = (id) => {
    const demandesUpdated = demandes.map(demande => 
      demande.id === id 
        ? {...demande, status: 'rejected', reponse: `Demande refusée le ${new Date().toLocaleDateString('fr-FR')}`} 
        : demande
    );
    
    setDemandes(demandesUpdated);
    setDemandesFiltrees(
      demandesUpdated.filter(demande => {
        if (recherche.trim() === "") return true;
        
        const termeLower = recherche.toLowerCase();
        return (
          demande.personnel.nom.toLowerCase().includes(termeLower) ||
          demande.personnel.prenom.toLowerCase().includes(termeLower) ||
          demande.montant.toString().includes(recherche)
        );
      })
    );
    
    alert(`La demande d'avance a été refusée.`);
  };

  // Mapping des statuts pour l'affichage
  const mapStatus = (status) => {
    switch (status) {
      case 'approved':
      case 'completed':
        return 'Complété';
      case 'pending':
        return 'En attente';
      case 'rejected':
        return 'Refusé';
      default:
        return status;
    }
  };

  // Rendu du statut avec l'icône appropriée
  const renderStatus = (status) => {
    const displayStatus = mapStatus(status);
    
    switch (displayStatus) {
      case "Complété":
        return (
          <div className="status-badge status-approved">
            <Check size={14} />
            <span>Approuvé</span>
          </div>
        );
      case "En attente":
        return (
          <div className="status-badge status-pending">
            <AlertCircle size={14} />
            <span>En attente</span>
          </div>
        );
      case "Refusé":
        return (
          <div className="status-badge status-rejected">
            <X size={14} />
            <span>Refusé</span>
          </div>
        );
      default:
        return null;
    }
  };

  // CSS encapsulé
  const styles = {
    container: "bg-white rounded-lg shadow-md overflow-hidden w-full",
    headerSection: "p-6 border-b border-gray-200",
    title: "text-2xl font-bold mb-6 text-gray-800",
    searchContainer: "relative mb-4",
    searchIcon: "absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400",
    searchInput: "w-full py-2 pl-10 pr-3 border border-gray-300 rounded-md transition duration-200 focus:outline-none focus:border-teal-500 focus:ring-2 focus:ring-teal-200 text-sm",
    tableContainer: "w-full overflow-x-auto",
    table: "w-full border-collapse",
    th: "text-left p-3 text-xs font-semibold uppercase text-gray-500 bg-gray-50 border-b border-gray-200",
    td: "p-4 align-middle text-gray-700 border-b border-gray-100",
    tr: "transition duration-200 hover:bg-gray-50",
    employeeName: "text-sm font-medium text-gray-900",
    employeeDept: "text-xs text-gray-500 mt-1",
    amount: "text-sm font-semibold text-gray-900",
    date: "text-xs text-gray-500 mt-1",
    badgeContainer: "inline-flex items-center px-2 py-1 rounded-full text-xs font-medium",
    badgeStandard: "bg-blue-100 text-blue-800",
    badgeUrgent: "bg-purple-100 text-purple-800",
    statusBadge: "flex items-center gap-1 px-2 py-1 rounded-full text-xs font-medium w-fit",
    statusApproved: "bg-green-100 text-green-800",
    statusPending: "bg-yellow-100 text-yellow-800",
    statusRejected: "bg-red-100 text-red-800",
    viewButton: "inline-flex items-center gap-1 px-3 py-1.5 border border-gray-300 rounded-md text-xs font-medium text-gray-600 bg-white hover:bg-gray-50 hover:border-gray-400 transition duration-200",
    actions: "flex gap-2",
    actionButton: "inline-flex items-center justify-center w-7 h-7 rounded-md text-gray-500 bg-white border border-gray-200 hover:bg-gray-50 hover:text-gray-900 hover:border-gray-300 transition duration-200",
    actionButtonApprove: "text-green-700 hover:bg-green-50 hover:border-green-200",
    actionButtonReject: "text-red-700 hover:bg-red-50 hover:border-red-200",
    footer: "p-4 bg-gray-50 border-t border-gray-200 text-sm text-gray-500",
    modalOverlay: "fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50",
    modal: "bg-white rounded-lg shadow-lg w-full max-w-lg max-h-90vh overflow-y-auto z-60",
    modalHeader: "p-4 sm:p-6 border-b border-gray-200 flex justify-between items-center",
    modalTitle: "text-xl font-semibold text-gray-900",
    modalClose: "bg-transparent border-none text-gray-500 hover:bg-gray-100 hover:text-gray-900 w-8 h-8 flex items-center justify-center rounded-md transition duration-200",
    modalBody: "p-6",
    modalSection: "mb-6",
    modalSectionTitle: "text-base font-semibold text-gray-900 mb-2",
    modalSectionContent: "text-sm leading-6 text-gray-600 bg-gray-50 p-4 rounded-md border border-gray-200",
    modalInfoGrid: "grid grid-cols-2 gap-4 mb-6",
    modalInfoItem: "flex flex-col gap-1",
    modalInfoLabel: "text-xs font-medium text-gray-500",
    modalInfoValue: "text-sm font-medium text-gray-900",
    modalFooter: "p-4 sm:p-6 border-t border-gray-200 flex justify-end gap-2",
    modalButton: "px-4 py-2 text-sm font-medium rounded-md cursor-pointer transition duration-200",
    modalButtonSecondary: "bg-white text-gray-600 border border-gray-300 hover:bg-gray-50 hover:border-gray-400",
    modalButtonPrimary: "bg-teal-500 text-white border border-transparent hover:bg-teal-600"
  };

  return (
    <div className={styles.container}>
      {/* En-tête et recherche */}
      <div className={styles.headerSection}>
        <h1 className={styles.title}>
          Gestion des Demandes d'Avances
        </h1>
        
        {/* Barre de recherche */}
        <div className={styles.searchContainer}>
          <div className={styles.searchIcon}>
            <Search size={18} />
          </div>
          <input
            type="text"
            className={styles.searchInput}
            placeholder="Rechercher par nom ou montant..."
            value={recherche}
            onChange={(e) => filtrerDemandes(e.target.value)}
          />
        </div>
      </div>

      {/* Tableau des demandes */}
      <div className={styles.tableContainer}>
        <table className={styles.table}>
          <thead>
            <tr>
              <th className={styles.th}>Employé</th>
              <th className={styles.th}>Montant</th>
              <th className={styles.th}>Type</th>
              <th className={styles.th}>Statut</th>
              <th className={styles.th}>Motif</th>
              <th className={styles.th}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {demandesFiltrees.length === 0 ? (
              <tr>
                <td colSpan="6" className={styles.td} style={{ textAlign: 'center' }}>
                  Aucune demande d'avance trouvée
                </td>
              </tr>
            ) : (
              demandesFiltrees.map((demande) => (
                <tr key={demande.id} className={styles.tr}>
                  <td className={styles.td}>
                    <div className={styles.employeeName}>
                      {demande.personnel.prenom} {demande.personnel.nom}
                    </div>
                    <div className={styles.employeeDept}>{demande.personnel.role}</div>
                  </td>
                  <td className={styles.td}>
                    <div className={styles.amount}>
                      {demande.montant} €
                    </div>
                    <div className={styles.date}>{formatDate(demande.createdAt)}</div>
                  </td>
                  <td className={styles.td}>
                    <span className={`${styles.badgeContainer} ${demande.type === "urgent" ? styles.badgeUrgent : styles.badgeStandard}`}>
                      {demande.type === "urgent" ? "Urgente" : "Standard"}
                    </span>
                  </td>
                  <td className={styles.td}>
                    <div className={`${styles.statusBadge} ${
                      mapStatus(demande.status) === "Complété" ? styles.statusApproved :
                      mapStatus(demande.status) === "En attente" ? styles.statusPending :
                      styles.statusRejected
                    }`}>
                      {mapStatus(demande.status) === "Complété" && <Check size={14} />}
                      {mapStatus(demande.status) === "En attente" && <AlertCircle size={14} />}
                      {mapStatus(demande.status) === "Refusé" && <X size={14} />}
                      <span>{mapStatus(demande.status)}</span>
                    </div>
                  </td>
                  <td className={styles.td}>
                    <button
                      className={styles.viewButton}
                      onClick={() => ouvrirModal(demande)}
                    >
                      <Eye size={14} />
                      <span>Voir motif</span>
                    </button>
                  </td>
                  <td className={styles.td}>
                    <div className={styles.actions}>
                      {mapStatus(demande.status) === "En attente" && (
                        <>
                          <button 
                            className={`${styles.actionButton} ${styles.actionButtonApprove}`} 
                            onClick={() => handleApprove(demande.id)}
                            title="Approuver"
                          >
                            <Check size={16} />
                          </button>
                          <button 
                            className={`${styles.actionButton} ${styles.actionButtonReject}`} 
                            onClick={() => handleReject(demande.id)}
                            title="Refuser"
                          >
                            <X size={16} />
                          </button>
                        </>
                      )}
                    </div>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
      
      {/* Information sur les résultats */}
      <div className={styles.footer}>
        Affichage de {demandesFiltrees.length} sur {demandes.length} demandes d'avance
      </div>

      {/* Modal pour afficher le détail de la demande */}
      {modalVisible && demandeSelected && (
        <div className={styles.modalOverlay} onClick={fermerModal}>
          <div className={styles.modal} onClick={(e) => e.stopPropagation()}>
            <div className={styles.modalHeader}>
              <h3 className={styles.modalTitle}>Détails de la demande</h3>
              <button className={styles.modalClose} onClick={fermerModal}>
                <X size={20} />
              </button>
            </div>
            <div className={styles.modalBody}>
              <div className={styles.modalInfoGrid}>
                <div className={styles.modalInfoItem}>
                  <span className={styles.modalInfoLabel}>Employé</span>
                  <span className={styles.modalInfoValue}>
                    {demandeSelected.personnel.prenom} {demandeSelected.personnel.nom}
                  </span>
                </div>
                <div className={styles.modalInfoItem}>
                  <span className={styles.modalInfoLabel}>Département</span>
                  <span className={styles.modalInfoValue}>
                    {demandeSelected.personnel.role}
                  </span>
                </div>
                <div className={styles.modalInfoItem}>
                  <span className={styles.modalInfoLabel}>Montant</span>
                  <span className={styles.modalInfoValue}>{demandeSelected.montant} €</span>
                </div>
                <div className={styles.modalInfoItem}>
                  <span className={styles.modalInfoLabel}>Date de demande</span>
                  <span className={styles.modalInfoValue}>{formatDate(demandeSelected.createdAt)}</span>
                </div>
                <div className={styles.modalInfoItem}>
                  <span className={styles.modalInfoLabel}>Type</span>
                  <span className={styles.modalInfoValue}>
                    <span className={`${styles.badgeContainer} ${demandeSelected.type === "urgent" ? styles.badgeUrgent : styles.badgeStandard}`}>
                      {demandeSelected.type === "urgent" ? "Urgente" : "Standard"}
                    </span>
                  </span>
                </div>
                <div className={styles.modalInfoItem}>
                  <span className={styles.modalInfoLabel}>Statut</span>
                  <span className={styles.modalInfoValue}>
                    <div className={`${styles.statusBadge} ${
                      mapStatus(demandeSelected.status) === "Complété" ? styles.statusApproved :
                      mapStatus(demandeSelected.status) === "En attente" ? styles.statusPending :
                      styles.statusRejected
                    }`}>
                      {mapStatus(demandeSelected.status) === "Complété" && <Check size={14} />}
                      {mapStatus(demandeSelected.status) === "En attente" && <AlertCircle size={14} />}
                      {mapStatus(demandeSelected.status) === "Refusé" && <X size={14} />}
                      <span>{mapStatus(demandeSelected.status)}</span>
                    </div>
                  </span>
                </div>
              </div>

              <div className={styles.modalSection}>
                <h4 className={styles.modalSectionTitle}>Motif de la demande</h4>
                <div className={styles.modalSectionContent}>
                  {demandeSelected.motif}
                </div>
              </div>

              {demandeSelected.reponse && (
                <div className={styles.modalSection}>
                  <h4 className={styles.modalSectionTitle}>Réponse à la demande</h4>
                  <div className={styles.modalSectionContent}>
                    {demandeSelected.reponse}
                  </div>
                </div>
              )}
            </div>
            <div className={styles.modalFooter}>
              <button className={`${styles.modalButton} ${styles.modalButtonSecondary}`} onClick={fermerModal}>
                Fermer
              </button>
              {demandeSelected.status === "pending" && (
                <>
                  <button 
                    className={styles.modalButton}
                    onClick={() => {
                      handleReject(demandeSelected.id);
                      fermerModal();
                    }}
                    style={{ 
                      backgroundColor: "#fee2e2", 
                      color: "#b91c1c",
                      borderColor: "#fecaca" 
                    }}
                  >
                    Refuser
                  </button>
                  <button 
                    className={styles.modalButton}
                    onClick={() => {
                      handleApprove(demandeSelected.id);
                      fermerModal();
                    }}
                    style={{ 
                      backgroundColor: "#dcfce7", 
                      color: "#166534",
                      borderColor: "#86efac" 
                    }}
                  >
                    Approuver
                  </button>
                </>
              )}
            </div>
          </div>
        </div>
      )}

      {/* CSS global pour les éléments spécifiques */}
      <style jsx>{`
        .status-badge {
          display: flex;
          align-items: center;
          gap: 0.25rem;
          padding: 0.25rem 0.5rem;
          border-radius: 9999px;
          font-size: 0.75rem;
          font-weight: 500;
          width: fit-content;
        }
        .status-approved {
          background-color: #dcfce7;
          color: #166534;
        }
        .status-pending {
          background-color: #fef3c7;
          color: #92400e;
        }
        .status-rejected {
          background-color: #fee2e2;
          color: #b91c1c;
        }
      `}</style>
    </div>
  );
}
