import { useState, useEffect } from 'react';
import { Search, Edit, Trash, Plus, X, Check, Filter, Eye, Copy } from 'lucide-react';
import axios from 'axios';
import '../../assets/styles/PersonnelStyle.css';

// Fonction pour générer un mot de passe aléatoire
const generatePassword = () => {
  const length = 10;
  const charset = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!@#$%^&*()';
  let password = '';
  for (let i = 0; i < length; i++) {
    password += charset.charAt(Math.floor(Math.random() * charset.length));
  }
  return password;
};

// Composant Modal pour les détails
const DetailsModal = ({ employe, onClose }) => {
  if (!employe) return null;

  return (
    <div className="gp-modal-overlay">
      <div className="gp-modal-container">
        <div className="gp-modal-header">
          <h2 className="gp-modal-title">DÉTAILS DE L'EMPLOYÉ</h2>
          <button onClick={onClose} className="gp-modal-close">
            <X size={24} />
          </button>
        </div>

        <div className="gp-details-content">
          <div className="gp-employee-header">
            <h3>{employe.nom} {employe.prenom}</h3>
            <span className={`gp-status-badge ${employe.statut || 'actif'}`}>
              {employe.statut ? employe.statut.charAt(0).toUpperCase() + employe.statut.slice(1) : 'Actif'}
            </span>
          </div>

          <div className="gp-details-grid">
            <div className="gp-detail-item">
              <span className="gp-detail-label">ID:</span>
              <span className="gp-detail-value">{employe._id}</span>
            </div>
            <div className="gp-detail-item">
              <span className="gp-detail-label">Email:</span>
              <span className="gp-detail-value">{employe.email}</span>
            </div>
            <div className="gp-detail-item">
              <span className="gp-detail-label">Téléphone:</span>
              <span className="gp-detail-value">{employe.tel}</span>
            </div>
            <div className="gp-detail-item">
              <span className="gp-detail-label">Département:</span>
              <span className="gp-detail-value">{employe.departement}</span>
            </div>
            <div className="gp-detail-item">
              <span className="gp-detail-label">Salaire:</span>
              <span className="gp-detail-value">
                {employe.salaire ? `${employe.salaire} TND` : 'Non spécifié'}
              </span>
            </div>
            <div className="gp-detail-item">
              <span className="gp-detail-label">Solde congé:</span>
              <span className="gp-detail-value">
                {employe.soldeConge || employe.soldedeconge || 30} jours
              </span>
            </div>
          </div>

          <div className="gp-modal-footer">
            <button onClick={onClose} className="gp-btn gp-btn-primary">
              Fermer
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

// Composant Toast pour les notifications
const Toast = ({ message, type, onClose }) => {
  useEffect(() => {
    const timer = setTimeout(() => {
      onClose();
    }, 3000);
    return () => clearTimeout(timer);
  }, [onClose]);

  return (
    <div className={`gp-toast gp-toast-${type}`}>
      {type === 'success' ? <Check size={18} /> : <X size={18} />}
      <span>{message}</span>
    </div>
  );
};

// Composant principal
export default function GestionPersonnel() {
  // États
  const [employes, setEmployes] = useState([]);
  const [filteredEmployes, setFilteredEmployes] = useState([]);
  const [departements, setDepartements] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchType, setSearchType] = useState('nom');
  const [selectedDept, setSelectedDept] = useState('tous');
  const [currentPage, setCurrentPage] = useState(1);
  const [employeesPerPage] = useState(10);
  const [showModal, setShowModal] = useState(false);
  const [showDetailsModal, setShowDetailsModal] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [currentEmployee, setCurrentEmployee] = useState(null);
  const [employeeToDelete, setEmployeeToDelete] = useState(null);
  const [toast, setToast] = useState({ show: false, message: '', type: '' });

  // Données initiales pour un nouvel employé
  const initialEmployeeState = {
    _id: '',
    nom: '',
    prenom: '',
    email: '',
    telephone: '',
    departement: '',
    sexe: '',
    salaire: '',
    soldeConge: 30,
    password: generatePassword()
  };

  // Charger les départements
  useEffect(() => {
    const fetchDepartements = async () => {
      try {
        const response = await axios.get('/alldepartment');
        setDepartements(response.data);
      } catch (error) {
        showToast('Erreur lors du chargement des départements', 'error');
      }
    };
    fetchDepartements();
  }, []);

  // Charger les employés
  useEffect(() => {
    const fetchEmployes = async () => {
      try {
        const response = await axios.get('/allpersonnel');
        // Filtrer les DRH
        const filtered = response.data.filter(emp => !emp.role || emp.role.toLowerCase() !== 'drh');
        setEmployes(filtered);
      } catch (error) {
        showToast('Erreur lors du chargement des employés', 'error');
      }
    };
    fetchEmployes();
  }, []);

  // Filtrer les employés
  useEffect(() => {
    let result = [...employes];
    
    // Filtre par département
    if (selectedDept !== 'tous') {
      result = result.filter(emp => emp.departement === selectedDept);
    }
    
    // Filtre par recherche
    if (searchTerm) {
      result = result.filter(emp => {
        if (searchType === 'nom') {
          return (
            emp.nom.toLowerCase().includes(searchTerm.toLowerCase()) ||
            emp.prenom.toLowerCase().includes(searchTerm.toLowerCase())
          );
        } else if (searchType === 'id') {
          return emp._id.includes(searchTerm);
        }
        return true;
      });
    }
    
    setFilteredEmployes(result);
    setCurrentPage(1); // Réinitialiser à la première page
  }, [employes, searchTerm, searchType, selectedDept]);

  // Pagination
  const indexOfLastEmployee = currentPage * employeesPerPage;
  const indexOfFirstEmployee = indexOfLastEmployee - employeesPerPage;
  const currentEmployees = filteredEmployes.slice(indexOfFirstEmployee, indexOfLastEmployee);
  const totalPages = Math.ceil(filteredEmployes.length / employeesPerPage);

  // Fonctions utilitaires
  const showToast = (message, type) => {
    setToast({ show: true, message, type });
  };

  const closeToast = () => {
    setToast({ ...toast, show: false });
  };

  const openModal = (employee = null) => {
    setCurrentEmployee(employee || initialEmployeeState);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setCurrentEmployee(null);
  };

  const openDetailsModal = (employee) => {
    setCurrentEmployee(employee);
    setShowDetailsModal(true);
  };

  const closeDetailsModal = () => {
    setShowDetailsModal(false);
    setCurrentEmployee(null);
  };

  const openDeleteModal = (employee) => {
    setEmployeeToDelete(employee);
    setShowDeleteModal(true);
  };

  const closeDeleteModal = () => {
    setShowDeleteModal(false);
    setEmployeeToDelete(null);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCurrentEmployee(prev => ({ ...prev, [name]: value }));
  };

  const generateNewPassword = () => {
    setCurrentEmployee(prev => ({ ...prev, password: generatePassword() }));
  };

  const copyPassword = () => {
    navigator.clipboard.writeText(currentEmployee.password);
    showToast('Mot de passe copié!', 'success');
  };

  // Sauvegarder un employé
  const saveEmployee = async () => {
    // Validation
    if (!currentEmployee.nom || !currentEmployee.prenom || !currentEmployee.email || 
        !currentEmployee.telephone || !currentEmployee.departement || !currentEmployee.sexe) {
      showToast('Veuillez remplir tous les champs obligatoires', 'error');
      return;
    }

    try {
      const employeeData = {
        nom: currentEmployee.nom,
        prenom: currentEmployee.prenom,
        email: currentEmployee.email,
        tel: currentEmployee.telephone,
        password: currentEmployee.password,
        departement: currentEmployee.departement,
        sexe: currentEmployee.sexe.toLowerCase(),
        soldedeconge: currentEmployee.soldeConge,
        salaire: currentEmployee.salaire
      };

      if (currentEmployee._id) {
        // Mise à jour
        await axios.put(`/updatepersonnel/${currentEmployee._id}`, employeeData);
        showToast('Employé modifié avec succès', 'success');
      } else {
        // Création
        await axios.post('/addPersonnel', employeeData);
        showToast('Employé ajouté avec succès', 'success');
      }

      // Rafraîchir la liste
      const response = await axios.get('/allpersonnel');
      const filtered = response.data.filter(emp => !emp.role || emp.role.toLowerCase() !== 'drh');
      setEmployes(filtered);
      closeModal();
    } catch (error) {
      showToast(error.response?.data?.error || 'Erreur lors de la sauvegarde', 'error');
    }
  };

  // Supprimer un employé
  const deleteEmployee = async () => {
    if (!employeeToDelete) return;

    try {
      await axios.delete(`/deletepersonnel/${employeeToDelete._id}`);
      showToast('Employé supprimé avec succès', 'success');
      
      // Rafraîchir la liste
      const response = await axios.get('/allpersonnel');
      const filtered = response.data.filter(emp => !emp.role || emp.role.toLowerCase() !== 'drh');
      setEmployes(filtered);
      
      closeDeleteModal();
    } catch (error) {
      showToast(error.response?.data?.error || 'Erreur lors de la suppression', 'error');
    }
  };

  return (
    <div className="gp-container">
      <div className="gp-header">
        <h1>Gestion du Personnel</h1>
        
        <div className="gp-controls">
          <div className="gp-search">
            <div className="gp-search-icon">
              <Search size={18} />
            </div>
            <input
              type="text"
              placeholder={`Rechercher par ${searchType === 'nom' ? 'nom' : 'ID'}`}
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <select
              value={searchType}
              onChange={(e) => setSearchType(e.target.value)}
            >
              <option value="nom">Nom</option>
              <option value="id">ID</option>
            </select>
          </div>
          
          <button onClick={() => openModal()} className="gp-add-btn">
            <Plus size={18} />
            Ajouter un employé
          </button>
        </div>
        
        <div className="gp-filters">
          <button
            className={`gp-filter-btn ${selectedDept === 'tous' ? 'active' : ''}`}
            onClick={() => setSelectedDept('tous')}
          >
            Tous
          </button>
          {departements.map(dept => (
            <button
              key={dept._id}
              className={`gp-filter-btn ${selectedDept === dept.nom ? 'active' : ''}`}
              onClick={() => setSelectedDept(dept.nom)}
            >
              {dept.nom}
            </button>
          ))}
        </div>
      </div>
      
      <div className="gp-table-wrapper">
        <table className="gp-table">
          <thead>
            <tr>
              <th>Nom</th>
              <th>Prénom</th>
              <th>Email</th>
              <th>Téléphone</th>
              <th>Département</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {currentEmployees.length > 0 ? (
              currentEmployees.map(emp => (
                <tr key={emp._id}>
                  <td>{emp.nom}</td>
                  <td>{emp.prenom}</td>
                  <td>{emp.email}</td>
                  <td>{emp.tel}</td>
                  <td>
                    <span className="gp-dept-badge">{emp.departement}</span>
                  </td>
                  <td>
                    <div className="gp-actions">
                      <button onClick={() => openDetailsModal(emp)} className="gp-action-btn view">
                        <Eye size={16} />
                      </button>
                      <button onClick={() => openModal(emp)} className="gp-action-btn edit">
                        <Edit size={16} />
                      </button>
                      <button onClick={() => openDeleteModal(emp)} className="gp-action-btn delete">
                        <Trash size={16} />
                      </button>
                    </div>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="6" className="gp-no-data">
                  Aucun employé trouvé
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
      
      <div className="gp-pagination">
        <div className="gp-pagination-info">
          Affichage {indexOfFirstEmployee + 1}-{Math.min(indexOfLastEmployee, filteredEmployes.length)} sur {filteredEmployes.length} employés
        </div>
        <div className="gp-pagination-controls">
          <button
            onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
            disabled={currentPage === 1}
          >
            Précédent
          </button>
          
          {Array.from({ length: totalPages }, (_, i) => i + 1).map(page => (
            <button
              key={page}
              onClick={() => setCurrentPage(page)}
              className={currentPage === page ? 'active' : ''}
            >
              {page}
            </button>
          ))}
          
          <button
            onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
            disabled={currentPage === totalPages}
          >
            Suivant
          </button>
        </div>
      </div>
      
      {/* Modal d'ajout/modification */}
      {showModal && (
        <div className="gp-modal-overlay">
          <div className="gp-modal">
            <div className="gp-modal-header">
              <h2>{currentEmployee._id ? 'Modifier employé' : 'Ajouter un employé'}</h2>
              <button onClick={closeModal} className="gp-modal-close">
                <X size={20} />
              </button>
            </div>
            
            <div className="gp-modal-body">
              <div className="gp-form-group">
                <label>Nom *</label>
                <input
                  type="text"
                  name="nom"
                  value={currentEmployee.nom}
                  onChange={handleInputChange}
                  required
                />
              </div>
              
              <div className="gp-form-group">
                <label>Prénom *</label>
                <input
                  type="text"
                  name="prenom"
                  value={currentEmployee.prenom}
                  onChange={handleInputChange}
                  required
                />
              </div>
              
              <div className="gp-form-group">
                <label>Email *</label>
                <input
                  type="email"
                  name="email"
                  value={currentEmployee.email}
                  onChange={handleInputChange}
                  required
                />
              </div>
              
              <div className="gp-form-group">
                <label>Téléphone *</label>
                <input
                  type="tel"
                  name="telephone"
                  value={currentEmployee.telephone}
                  onChange={handleInputChange}
                  required
                />
              </div>
              
              <div className="gp-form-group">
                <label>Département *</label>
                <select
                  name="departement"
                  value={currentEmployee.departement}
                  onChange={handleInputChange}
                  required
                >
                  <option value="">Sélectionnez un département</option>
                  {departements.map(dept => (
                    <option key={dept._id} value={dept.nom}>{dept.nom}</option>
                  ))}
                </select>
              </div>
              
              <div className="gp-form-group">
                <label>Sexe *</label>
                <select
                  name="sexe"
                  value={currentEmployee.sexe}
                  onChange={handleInputChange}
                  required
                >
                  <option value="">Sélectionnez</option>
                  <option value="Homme">Homme</option>
                  <option value="Femme">Femme</option>
                </select>
              </div>
              
              <div className="gp-form-group">
                <label>Salaire (TND)</label>
                <input
                  type="number"
                  name="salaire"
                  value={currentEmployee.salaire}
                  onChange={handleInputChange}
                />
              </div>
              
              <div className="gp-form-group">
                <label>Solde congé (jours)</label>
                <input
                  type="number"
                  name="soldeConge"
                  value={currentEmployee.soldeConge}
                  onChange={handleInputChange}
                />
              </div>
              
              <div className="gp-form-group">
                <label>Mot de passe</label>
                <div className="gp-password-field">
                  <input
                    type="text"
                    value={currentEmployee.password}
                    readOnly
                  />
                  <button onClick={copyPassword} className="gp-copy-btn">
                    <Copy size={16} />
                  </button>
                </div>
                <button onClick={generateNewPassword} className="gp-generate-btn">
                  Générer un nouveau mot de passe
                </button>
              </div>
            </div>
            
            <div className="gp-modal-footer">
              <button onClick={closeModal} className="gp-btn gp-btn-cancel">
                Annuler
              </button>
              <button onClick={saveEmployee} className="gp-btn gp-btn-save">
                Enregistrer
              </button>
            </div>
          </div>
        </div>
      )}
      
      {/* Modal de détails */}
      {showDetailsModal && (
        <DetailsModal 
          employe={currentEmployee} 
          onClose={closeDetailsModal} 
        />
      )}
      
      {/* Modal de suppression */}
      {showDeleteModal && (
        <div className="gp-modal-overlay">
          <div className="gp-confirm-modal">
            <div className="gp-modal-header">
              <h2>Confirmer la suppression</h2>
              <button onClick={closeDeleteModal} className="gp-modal-close">
                <X size={20} />
              </button>
            </div>
            
            <div className="gp-modal-body">
              <p>
                Êtes-vous sûr de vouloir supprimer l'employé{' '}
                <strong>{employeeToDelete?.prenom} {employeeToDelete?.nom}</strong> ?
                Cette action est irréversible.
              </p>
            </div>
            
            <div className="gp-modal-footer">
              <button onClick={closeDeleteModal} className="gp-btn gp-btn-cancel">
                Annuler
              </button>
              <button onClick={deleteEmployee} className="gp-btn gp-btn-danger">
                Supprimer
              </button>
            </div>
          </div>
        </div>
      )}
      
      {/* Toast notification */}
      {toast.show && (
        <Toast 
          message={toast.message} 
          type={toast.type} 
          onClose={closeToast} 
        />
      )}
    </div>
  );
}
