import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const CardDemandeAvance = () => {
  const [formData, setFormData] = useState({
    montant: '',
    motif: '',
    type: 'standard'
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Vérifier l'authentification au chargement du composant
    checkAuthentication();
  }, []);

  const checkAuthentication = () => {
    const storedToken = localStorage.getItem('token');
    if (storedToken) {
      setIsAuthenticated(true);
      // Configuration pour les futures requêtes
      axios.defaults.headers.common['Authorization'] = `Bearer ${storedToken}`;
    } else {
      setIsAuthenticated(false);
      setError('Vous devez être connecté pour soumettre une demande');
    }
  };

  const handleLogin = () => {
    // Rediriger vers la page de connexion
    navigate('/login', { state: { returnUrl: window.location.pathname } });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    
    try {
      // Vérifier que l'utilisateur est authentifié
      if (!isAuthenticated) {
        throw new Error('Vous devez être connecté pour soumettre une demande');
      }

      // Valider les données
      if (!formData.montant || !formData.motif) {
        throw new Error('Le montant et le motif sont obligatoires');
      }

      // Récupérer le token à nouveau pour s'assurer qu'il est toujours valide
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Session expirée, veuillez vous reconnecter');
      }

      const config = {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        }
      };

      // Envoyer la demande au backend
      const response = await axios.post(
        '/demandes/avance',
        formData,
        config
      );

      if (response.data.success) {
        setSuccess(true);
        // Réinitialiser le formulaire après succès
        setFormData({
          montant: '',
          motif: '',
          type: 'standard'
        });
      }
    } catch (err) {
      // Gérer spécifiquement les erreurs d'authentification
      if (err.response?.status === 401) {
        localStorage.removeItem('token'); // Supprimer le token invalide
        setIsAuthenticated(false);
        setError('Votre session a expiré, veuillez vous reconnecter');
      } else {
        setError(err.response?.data?.message || err.message || 'Une erreur est survenue');
      }
    } finally {
      setLoading(false);
    }
  };

  const styles = {
    container: {
      width: '100%',
      maxWidth: '600px',
      margin: '0 auto',
      padding: '20px',
      backgroundColor: '#f5f7fa',
      fontFamily: 'Arial, sans-serif'
    },
    header: {
      textAlign: 'center',
      marginBottom: '20px',
      padding: '20px 0' 
    },
    title: {
      fontSize: '28px',
      fontWeight: '700',
      color: '#1e40af',
      margin: '0 0 10px 0',
      position: 'relative',
      display: 'inline-block',
      paddingBottom: '15px'
    },
    titleAfter: {
      content: '""',
      position: 'absolute',
      bottom: 0,
      left: '50%',
      transform: 'translateX(-50%)',
      width: '60%',
      height: '4px',
      background: 'linear-gradient(90deg, #1e40af, #3b82f6)',
      borderRadius: '2px'
    },
    subtitle: {
      fontSize: '16px',
      color: '#64748b',
      margin: '10px 0 0 0',
      letterSpacing: '0.3px'
    },
    form: {
      backgroundColor: '#ffffff',
      borderRadius: '8px',
      padding: '25px',
      boxShadow: '0 2px 6px rgba(0,0,0,0.1)'
    },
    formGroup: {
      marginBottom: '20px'
    },
    label: {
      display: 'block',
      marginBottom: '8px',
      fontWeight: '500',
      fontSize: '14px',
      color: '#334155'
    },
    input: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      boxSizing: 'border-box'
    },
    select: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      backgroundColor: '#fff',
      boxSizing: 'border-box'
    },
    textarea: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      minHeight: '100px',
      resize: 'vertical',
      boxSizing: 'border-box'
    },
    infoText: {
      fontSize: '12px',
      color: '#64748b',
      marginTop: '5px',
      fontStyle: 'italic'
    },
    submitBtn: {
      width: '100%',
      padding: '12px',
      backgroundColor: '#2563eb',
      color: '#ffffff',
      borderRadius: '4px',
      fontWeight: '500',
      border: 'none',
      cursor: 'pointer',
      fontSize: '16px',
      transition: 'background-color 0.3s',
      marginTop: '10px'
    },
    submitBtnHover: {
      backgroundColor: '#1d4ed8'
    },
    loginBtn: {
      width: '100%',
      padding: '12px',
      backgroundColor: '#4b5563',
      color: '#ffffff',
      borderRadius: '4px',
      fontWeight: '500',
      border: 'none',
      cursor: 'pointer',
      fontSize: '16px',
      transition: 'background-color 0.3s',
      marginTop: '10px'
    },
    loginBtnHover: {
      backgroundColor: '#374151'
    },
    loading: {
      display: 'inline-block',
      marginLeft: '10px'
    },
    error: {
      color: '#dc2626',
      margin: '10px 0',
      fontSize: '14px',
      textAlign: 'center',
      padding: '10px',
      backgroundColor: '#fee2e2',
      borderRadius: '4px',
      border: '1px solid #fecaca'
    },
    success: {
      color: '#16a34a',
      margin: '10px 0',
      fontSize: '14px',
      textAlign: 'center',
      padding: '10px',
      backgroundColor: '#dcfce7',
      borderRadius: '4px',
      border: '1px solid #bbf7d0'
    },
    authMessage: {
      textAlign: 'center',
      margin: '20px 0',
      padding: '20px',
      backgroundColor: '#fff',
      borderRadius: '8px',
      boxShadow: '0 2px 6px rgba(0,0,0,0.1)'
    }
  };

  // Afficher un message d'erreur et un bouton de connexion si l'utilisateur n'est pas connecté
  if (!isAuthenticated) {
    return (
      <div style={styles.container}>
        <div style={styles.header}>
          <h1 style={styles.title}>
            Demande d'avance sur salaire
            <span style={styles.titleAfter}></span>
          </h1>
        </div>
        
        <div style={styles.authMessage}>
          <p style={{...styles.error, marginBottom: '20px'}}>Vous devez être connecté pour soumettre une demande</p>
          <button 
            style={styles.loginBtn}
            onMouseOver={(e) => e.target.style.backgroundColor = '#374151'}
            onMouseOut={(e) => e.target.style.backgroundColor = '#4b5563'}
            onClick={handleLogin}
          >
            Se connecter
          </button>
        </div>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>
          Demande d'avance sur salaire
          <span style={styles.titleAfter}></span>
        </h1>
        <p style={styles.subtitle}>Remplissez ce formulaire pour soumettre votre demande</p>
      </div>

      {error && <div style={styles.error}>{error}</div>}
      {success && <div style={styles.success}>Votre demande a été soumise avec succès!</div>}

      <form style={styles.form} onSubmit={handleSubmit}>
        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="montant">Montant demandé (TND)</label>
          <input
            type="number"
            id="montant"
            name="montant"
            style={styles.input}
            value={formData.montant}
            onChange={handleChange}
            required
            min="1"
          />
          <p style={styles.infoText}>Le montant maximum est de 30% de votre salaire mensuel</p>
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="type">Type de demande</label>
          <select
            id="type"
            name="type"
            style={styles.select}
            value={formData.type}
            onChange={handleChange}
            required
          >
            <option value="standard">Standard (traitement sous 3 jours)</option>
            <option value="urgent">Urgente (traitement sous 24h)</option>
          </select>
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="motif">Motif de la demande</label>
          <textarea
            id="motif"
            name="motif"
            style={styles.textarea}
            value={formData.motif}
            onChange={handleChange}
            required
            placeholder="Décrivez brièvement la raison de votre demande d'avance"
          />
        </div>

        <button 
          type="submit" 
          style={styles.submitBtn}
          onMouseOver={(e) => e.target.style.backgroundColor = '#1d4ed8'}
          onMouseOut={(e) => e.target.style.backgroundColor = '#2563eb'}
          disabled={loading}
        >
          {loading ? 'Envoi en cours...' : 'Soumettre ma demande'}
        </button>
      </form>
    </div>
  );
};

export default CardDemandeAvance;
