import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import jwtDecode from 'jwt-decode';

const CardDemandeConge = () => {
  // √âtats du composant
  const [formData, setFormData] = useState({
    startDate: '',
    endDate: '',
    reason: '',
    type: 'cong√© pay√©',
    notes: '',
    medicalFile: null
  });
  
  const [fileName, setFileName] = useState('');
  const fileInputRef = useRef(null);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [confirmationMessage, setConfirmationMessage] = useState('');
  const [submitError, setSubmitError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userData, setUserData] = useState({
    nom: '',
    prenom: '',
    email: '',
    soldeConge: 0
  });

  // Styles
  const styles = {
    container: {
      backgroundColor: '#f0f4f8',
      minHeight: '100vh',
      padding: '20px',
      fontFamily: '"Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif',
      color: '#334155'
    },
    authContainer: {
      maxWidth: '500px',
      margin: '100px auto',
      backgroundColor: '#ffffff',
      borderRadius: '12px',
      padding: '40px',
      boxShadow: '0 4px 10px rgba(0,0,0,0.06)',
      textAlign: 'center'
    },
    authTitle: {
      fontSize: '1.5rem',
      fontWeight: '700',
      color: '#1e40af',
      marginBottom: '20px'
    },
    authButton: {
      padding: '12px 24px',
      backgroundColor: '#2563eb',
      color: '#ffffff',
      border: 'none',
      borderRadius: '8px',
      fontSize: '16px',
      fontWeight: '600',
      cursor: 'pointer',
      transition: 'all 0.2s ease',
      marginTop: '20px'
    },
    mainContent: {
      maxWidth: '1200px',
      margin: '0 auto',
      paddingTop: '8rem'
    },
    header: {
      textAlign: 'center',
      marginBottom: '40px',
      padding: '20px 0' 
    },
    title: {
      fontSize: '2.2rem', 
      fontWeight: '700',
      color: '#1e40af',
      margin: '0 0 10px 0',
      position: 'relative',
      display: 'inline-block',
      paddingBottom: '15px'
    },
    titleAfter: {
      content: '""',
      position: 'absolute',
      bottom: 0,
      left: '50%',
      transform: 'translateX(-50%)',
      width: '60%',
      height: '4px',
      background: 'linear-gradient(90deg, #1e40af, #3b82f6)',
      borderRadius: '2px'
    },
    subtitle: {
      fontSize: '17px',
      color: '#64748b',
      marginTop: '10px',
      margin: '0',
      letterSpacing: '0.3px'
    },
    formContainer: {
      backgroundColor: '#ffffff',
      borderRadius: '12px',
      padding: '35px',
      marginBottom: '35px',
      boxShadow: '0 4px 10px rgba(0,0,0,0.06)',
      border: '1px solid rgba(0,0,0,0.04)'
    },
    formHeader: {
      marginBottom: '25px',
      borderBottom: '1px solid #e2e8f0',
      paddingBottom: '15px'
    },
    formTitle: {
      fontSize: '22px',
      fontWeight: '700',
      color: '#1e40af',
      margin: '0 0 8px 0'
    },
    formDescription: {
      fontSize: '15px',
      color: '#64748b',
      margin: '0'
    },
    formGroup: {
      marginBottom: '20px'
    },
    label: {
      display: 'block',
      fontSize: '15px',
      fontWeight: '600',
      color: '#475569',
      marginBottom: '8px'
    },
    input: {
      width: '100%',
      padding: '12px 14px',
      border: '1px solid #e2e8f0',
      borderRadius: '8px',
      fontSize: '15px',
      boxShadow: '0 1px 2px rgba(0,0,0,0.04)',
      transition: 'all 0.2s ease'
    },
    formGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(2, 1fr)',
      gap: '20px',
      marginBottom: '20px'
    },
    selectContainer: {
      position: 'relative'
    },
    select: {
      width: '100%',
      padding: '12px 14px',
      border: '1px solid #e2e8f0',
      borderRadius: '8px',
      fontSize: '15px',
      boxShadow: '0 1px 2px rgba(0,0,0,0.04)',
      transition: 'all 0.2s ease',
      appearance: 'none',
      paddingRight: '40px',
      backgroundColor: '#ffffff'
    },
    selectArrow: {
      position: 'absolute',
      right: '15px',
      top: '50%',
      transform: 'translateY(-50%)',
      color: '#64748b',
      pointerEvents: 'none',
      width: '12px',
      height: '12px',
      borderLeft: '2px solid #64748b',
      borderBottom: '2px solid #64748b',
      transform: 'translateY(-70%) rotate(-45deg)'
    },
    congeInfoTag: {
      display: 'block',
      fontSize: '14px',
      color: '#64748b',
      marginTop: '5px'
    },
    textarea: {
      width: '100%',
      padding: '12px 14px',
      border: '1px solid #e2e8f0',
      borderRadius: '8px',
      fontSize: '15px',
      boxShadow: '0 1px 2px rgba(0,0,0,0.04)',
      transition: 'all 0.2s ease',
      minHeight: '100px',
      resize: 'vertical'
    },
    alert: {
      backgroundColor: '#e0f2fe',
      borderRadius: '8px',
      padding: '14px',
      marginBottom: '20px',
      display: 'flex',
      alignItems: 'center',
      gap: '10px',
      boxShadow: '0 1px 2px rgba(0,0,0,0.04)'
    },
    alertIcon: {
      color: '#0369a1',
      fontSize: '18px'
    },
    alertText: {
      color: '#0369a1',
      fontSize: '15px',
      fontWeight: '500',
      margin: '0'
    },
    buttonContainer: {
      marginTop: '25px'
    },
    button: {
      width: '100%',
      padding: '14px',
      backgroundColor: '#2563eb',
      color: '#ffffff',
      border: 'none',
      borderRadius: '8px',
      fontSize: '16px',
      fontWeight: '600',
      cursor: 'pointer',
      transition: 'all 0.2s ease',
      boxShadow: '0 2px 5px rgba(37, 99, 235, 0.3)'
    },
    buttonDisabled: {
      backgroundColor: '#93c5fd',
      cursor: 'not-allowed'
    },
    fileInputContainer: {
      position: 'relative',
      display: 'flex',
      flexDirection: 'column',
      width: '100%'
    },
    fileInput: {
      position: 'absolute',
      top: 0,
      left: 0,
      opacity: 0,
      width: '100%',
      height: '100%',
      cursor: 'pointer'
    },
    fileInputLabel: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '12px 14px',
      border: '1px dashed #3b82f6',
      borderRadius: '8px',
      backgroundColor: '#f1f5f9',
      color: '#475569',
      fontSize: '15px',
      cursor: 'pointer',
      transition: 'all 0.2s ease',
      minHeight: '50px'
    },
    selectedFile: {
      display: 'flex',
      alignItems: 'center',
      gap: '10px',
      marginTop: '10px',
      padding: '10px',
      backgroundColor: '#f1f5f9',
      borderRadius: '6px',
      fontSize: '14px'
    },
    fileIcon: {
      fontSize: '20px',
      color: '#3b82f6'
    },
    fileInfo: {
      flex: 1,
      overflow: 'hidden',
      textOverflow: 'ellipsis',
      whiteSpace: 'nowrap'
    },
    removeButton: {
      background: 'none',
      border: 'none',
      color: '#ef4444',
      cursor: 'pointer',
      fontSize: '16px',
      padding: '0 5px'
    },
    toastMessage: {
      position: 'fixed',
      bottom: '30px',
      left: '50%',
      transform: 'translateX(-50%)',
      backgroundColor: '#34d399',
      color: 'white',
      borderRadius: '12px',
      padding: '5px',
      boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05)',
      zIndex: '1000',
      animation: 'slideUp 0.3s ease, fadeOut 0.5s ease 2.5s',
      minWidth: '300px',
      maxWidth: '600px'
    },
    toastContent: {
      display: 'flex',
      alignItems: 'center',
      gap: '12px',
      padding: '15px 20px'
    },
    toastIcon: {
      fontSize: '1.2rem',
      backgroundColor: 'white',
      color: '#34d399',
      width: '30px',
      height: '30px',
      borderRadius: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      flexShrink: '0'
    },
    errorMessage: {
      backgroundColor: '#fee2e2',
      color: '#b91c1c',
      padding: '12px',
      borderRadius: '8px',
      marginBottom: '20px',
      border: '1px solid #fca5a5'
    },
    loadingSpinner: {
      display: 'inline-block',
      width: '20px',
      height: '20px',
      border: '3px solid rgba(255,255,255,0.3)',
      borderRadius: '50%',
      borderTopColor: '#fff',
      animation: 'spin 1s ease-in-out infinite',
      marginLeft: '10px'
    }
  };

  // Types de cong√©s
  const congeTypes = [
    {
      type: 'cong√© pay√©',
      title: 'Cong√© Pay√© Standard',
      description: 'Cong√© annuel r√©mun√©r√©',
      remainingDays: userData.soldeConge,
      icon: 'üèñÔ∏è'
    },
    {
      type: 'rtt',
      title: 'RTT',
      description: 'R√©duction du temps de travail',
      remainingDays: 8,
      icon: '‚è±Ô∏è'
    },
    {
      type: 'cong√© maladie',
      title: 'Cong√© Maladie',
      description: 'Absence pour raison m√©dicale',
      remainingDays: 'Illimit√©',
      icon: 'üè•'
    },
    {
      type: 'cong√© parental',
      title: 'Cong√© Parental',
      description: 'Cong√© pour s\'occuper d\'un enfant',
      remainingDays: 'Selon droits',
      icon: 'üë∂'
    },
    {
      type: 'cong√© familial',
      title: '√âv√©nement Familial',
      description: 'Mariage, naissance, d√©c√®s',
      remainingDays: 'Selon √©v√©nement',
      icon: 'üë™'
    },
    {
      type: 'cong√© sans solde',
      title: 'Cong√© Sans Solde',
      description: 'Absence autoris√©e non r√©mun√©r√©e',
      remainingDays: 'Illimit√©',
      icon: 'üìÖ'
    }
  ];

  // V√©rification de l'authentification
  useEffect(() => {
    const token = localStorage.getItem('token');
    const userDataStr = localStorage.getItem('userData');

    if (token && userDataStr) {
      try {
        const decoded = jwtDecode(token);
        const userData = JSON.parse(userDataStr);

        // V√©rification expiration token
        if (decoded.exp * 1000 < Date.now()) {
          localStorage.removeItem('token');
          localStorage.removeItem('userData');
          setIsAuthenticated(false);
          return;
        }

        setUserData({
          nom: userData.nom || decoded.nom || '',
          prenom: userData.prenom || decoded.prenom || '',
          email: userData.email || decoded.email || '',
          soldeConge: userData.soldeConge || decoded.soldeConge || 0
        });
        setIsAuthenticated(true);
      } catch (error) {
        console.error("Erreur d'authentification:", error);
        localStorage.removeItem('token');
        localStorage.removeItem('userData');
        setIsAuthenticated(false);
      }
    } else {
      setIsAuthenticated(false);
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };
  
  const handleFileChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      setFormData(prev => ({
        ...prev,
        medicalFile: file
      }));
      setFileName(file.name);
    }
  };
  
  const removeFile = () => {
    setFormData(prev => ({
      ...prev,
      medicalFile: null
    }));
    setFileName('');
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };
  
  const calculateDays = () => {
    if (!formData.startDate || !formData.endDate) return 0;
    
    const start = new Date(formData.startDate);
    const end = new Date(formData.endDate);
    const diffTime = Math.abs(end - start);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24)) + 1;
    
    return diffDays;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitError(null);
    setLoading(true);
    
    // Validation
    if (!isAuthenticated) {
      setSubmitError('Session expir√©e. Veuillez vous reconnecter.');
      setLoading(false);
      return;
    }

    if (formData.type === 'cong√© maladie' && !formData.medicalFile) {
      setSubmitError('Veuillez joindre votre justificatif m√©dical');
      setLoading(false);
      return;
    }

    const token = localStorage.getItem('token');
    if (!token) {
      setSubmitError("Session expir√©e. Veuillez vous reconnecter.");
      setLoading(false);
      return;
    }
  
    try {
      const formDataToSend = new FormData();
      
      // Ajoutez les champs n√©cessaires
      formDataToSend.append('startDate', formData.startDate);
      formDataToSend.append('endDate', formData.endDate);
      formDataToSend.append('type', formData.type);
      formDataToSend.append('reason', formData.reason || '');
      formDataToSend.append('notes', formData.notes || '');
      
      if (formData.medicalFile) {
        formDataToSend.append('medicalFile', formData.medicalFile);
      }

      const response = await axios.post(
        '/demandesconge', 
        formDataToSend,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
            'Authorization': `Bearer ${token}`
          }
        }
      );

      // Succ√®s
      setConfirmationMessage(`Demande de ${formData.type} enregistr√©e (R√©f: ${response.data.data._id})`);
      setShowConfirmation(true);
      
      // R√©initialisation
      setFormData({
        startDate: '',
        endDate: '',
        reason: '',
        type: 'cong√© pay√©',
        notes: '',
        medicalFile: null
      });
      setFileName('');

      // Masquer la confirmation apr√®s 5 secondes
      setTimeout(() => setShowConfirmation(false), 5000);

    } catch (error) {
      console.error('Erreur:', error);
      let errorMessage = "Erreur lors de la soumission";
      
      if (error.response) {
        errorMessage = error.response.data?.message || 
                      error.response.data?.error || 
                      JSON.stringify(error.response.data);
      } else if (error.request) {
        errorMessage = "Le serveur ne r√©pond pas";
      } else {
        errorMessage = error.message;
      }
      
      setSubmitError(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  // Si non authentifi√©, afficher le message d'authentification
  if (!isAuthenticated) {
    return (
      <div style={styles.container}>
        <div style={styles.authContainer}>
          <h2 style={styles.authTitle}>Authentification requise</h2>
          <p>Veuillez vous connecter pour acc√©der √† cette fonctionnalit√©.</p>
          <button 
            style={styles.authButton}
            onClick={() => window.location.href = '/login'}
          >
            Se connecter
          </button>
        </div>
      </div>
    );
  }

  // Rendu normal si authentifi√©
  const selectedCongeType = congeTypes.find(conge => conge.type === formData.type);
  const isMaladieType = formData.type === 'cong√© maladie';
  const needsJustification = ['cong√© maladie', 'cong√© familial'].includes(formData.type);
  
  return (
    <div style={styles.container}>
      <div style={styles.mainContent}>
        <div style={styles.header}>
          <h1 style={styles.title}>
            Demande de Cong√©
            <span style={styles.titleAfter}></span>
          </h1>
          <p style={styles.subtitle}>Bonjour {userData.prenom} {userData.nom}, d√©couvrez et soumettez vos demandes de cong√©s en quelques clics</p>
        </div>

        <div style={styles.formContainer}>
          <div style={styles.formHeader}>
            <h2 style={styles.formTitle}>Formulaire de demande de cong√©</h2>
            <p style={styles.formDescription}>Veuillez remplir tous les champs obligatoires</p>
          </div>
          
          {submitError && (
            <div style={styles.errorMessage}>
              {submitError}
            </div>
          )}

          <form onSubmit={handleSubmit}>
            <div style={styles.formGroup}>
              <label style={styles.label} htmlFor="type">
                Type de cong√©
              </label>
              <div style={styles.selectContainer}>
                <select
                  id="type"
                  name="type"
                  value={formData.type}
                  onChange={handleChange}
                  style={styles.select}
                  required
                >
                  {congeTypes.map((conge, index) => (
                    <option key={index} value={conge.type}>
                      {conge.icon} {conge.title}
                    </option>
                  ))}
                </select>
                <div style={styles.selectArrow}></div>
              </div>
              <span style={styles.congeInfoTag}>
                {selectedCongeType?.description} - 
                {typeof selectedCongeType?.remainingDays === 'number' 
                  ? ` ${selectedCongeType.remainingDays} jours disponibles` 
                  : ` ${selectedCongeType?.remainingDays}`}
              </span>
            </div>
            
            {isMaladieType ? (
              <div style={styles.formGroup}>
                <label style={styles.label} htmlFor="medicalFile">
                  Justificatif m√©dical *
                </label>
                <div style={styles.fileInputContainer}>
                  <label style={styles.fileInputLabel} htmlFor="medicalFile">
                    {formData.medicalFile ? 'Changer de fichier' : 'Joindre votre justificatif m√©dical'}
                  </label>
                  <input
                    type="file"
                    id="medicalFile"
                    ref={fileInputRef}
                    style={styles.fileInput}
                    accept=".pdf,.jpg,.jpeg,.png"
                    onChange={handleFileChange}
                    required
                  />
                </div>
                {formData.medicalFile && (
                  <div style={styles.selectedFile}>
                    <span style={styles.fileIcon}>üìÑ</span>
                    <span style={styles.fileInfo}>{fileName}</span>
                    <button 
                      type="button" 
                      style={styles.removeButton}
                      onClick={removeFile}
                    >
                      ‚úï
                    </button>
                  </div>
                )}
              </div>
            ) : (
              <div style={styles.formGroup}>
                <label style={styles.label} htmlFor="reason">
                  Motif {needsJustification && '*'}
                </label>
                <input
                  type="text"
                  id="reason"
                  name="reason"
                  value={formData.reason}
                  onChange={handleChange}
                  style={styles.input}
                  placeholder={needsJustification 
                    ? "Pr√©cisez le motif (obligatoire)" 
                    : "Pr√©cisez le motif de votre demande (facultatif)"}
                  required={needsJustification}
                />
              </div>
            )}
            
            <div style={styles.formGrid}>
              <div style={styles.formGroup}>
                <label style={styles.label} htmlFor="startDate">
                  Date de d√©but
                </label>
                <input
                  type="date"
                  id="startDate"
                  name="startDate"
                  value={formData.startDate}
                  onChange={handleChange}
                  style={styles.input}
                  required
                />
              </div>
              
              <div style={styles.formGroup}>
                <label style={styles.label} htmlFor="endDate">
                  Date de fin
                </label>
                <input
                  type="date"
                  id="endDate"
                  name="endDate"
                  value={formData.endDate}
                  onChange={handleChange}
                  style={styles.input}
                  required
                />
              </div>
            </div>
            
            {formData.startDate && formData.endDate && (
              <div style={styles.alert}>
                <span style={styles.alertIcon}>‚ÑπÔ∏è</span>
                <p style={styles.alertText}>
                  Dur√©e: <strong>{calculateDays()} jours</strong>
                  {formData.type === 'cong√© pay√©' && (
                    <span> - Solde restant: {userData.soldeConge - calculateDays()} jours</span>
                  )}
                </p>
              </div>
            )}
            
            <div style={styles.formGroup}>
              <label style={styles.label} htmlFor="notes">
                Notes compl√©mentaires
              </label>
              <textarea
                id="notes"
                name="notes"
                value={formData.notes}
                onChange={handleChange}
                style={styles.textarea}
                placeholder="Informations additionnelles (facultatif)"
              />
            </div>
            
            <div style={styles.buttonContainer}>
              <button 
                type="submit" 
                style={{...styles.button, ...(loading ? styles.buttonDisabled : {})}}
                disabled={loading}
              >
                {loading ? (
                  <>
                    Envoi en cours...
                    <span style={styles.loadingSpinner}></span>
                  </>
                ) : 'Soumettre la demande'}
              </button>
            </div>
          </form>
        </div>
      </div>

      {/* Toast de confirmation */}
      {showConfirmation && (
        <div style={styles.toastMessage}>
          <div style={styles.toastContent}>
            <span style={styles.toastIcon}>‚úì</span>
            <p>{confirmationMessage}</p>
          </div>
        </div>
      )}

      <style>
        {`
        @keyframes slideUp {
          from {
            transform: translate(-50%, 100%);
            opacity: 0;
          }
          to {
            transform: translate(-50%, 0);
            opacity: 1;
          }
        }

        @keyframes fadeOut {
          from {
            opacity: 1;
          }
          to {
            opacity: 0;
          }
        }

        @keyframes spin {
          to { transform: rotate(360deg); }
        }
        `}
      </style>
    </div>
  );
};

export default CardDemandeConge;
