import React, { useState } from "react";

export default function AccountManager() {
  const [showPasswordChange, setShowPasswordChange] = useState(false);
  const [profileImage, setProfileImage] = useState("data:image/png;base64,");

  const handleImageChange = (e) => {
    // Gestion temporaire pour la prévisualisation
    if (e.target.files && e.target.files[0]) {
      // Le backend gérera cette partie plus tard
      setProfileImage("https://via.placeholder.com/150");
    }
  };

  return (
    <>
      <div className="account-container">
        <div className="account-card">
          {/* En-tête */}
          <div className="account-header">
            <h1 className="header-title">Gérer mon compte</h1>
            <p className="header-subtitle">Modifiez vos informations personnelles</p>
          </div>

          {/* Corps du formulaire */}
          <div className="account-body">
            <form>
              {/* Section Photo de profil */}
              <div className="form-section">
                <h2 className="section-title">Photo de profil</h2>
                <div className="profile-image-container">
                  <div className="image-wrapper">
                    <img 
                      src={profileImage} 
                      alt="Photo de profil" 
                      className="profile-image"
                    />
                    <button 
                      type="button"
                      className="image-edit-button"
                    >
                      <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="none" 
                          viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} 
                            d="M3 9a2 2 0 012-2h.93a2 2 0 001.664-.89l.812-1.22A2 2 0 0110.07 4h3.86a2 2 0 011.664.89l.812 1.22A2 2 0 0018.07 7H19a2 2 0 012 2v9a2 2 0 01-2 2H5a2 2 0 01-2-2V9z" />
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} 
                            d="M15 13a3 3 0 11-6 0 3 3 0 016 0z" />
                      </svg>
                    </button>
                  </div>
                  <div className="image-controls">
                    <input 
                      type="file" 
                      id="profile-image" 
                      className="file-input" 
                      accept="image/*"
                      onChange={handleImageChange}
                    />
                    <label 
                      htmlFor="profile-image" 
                      className="upload-button"
                    >
                      Changer la photo
                    </label>
                    <p className="upload-hint">
                      JPG, PNG ou GIF. Taille max 2MB.
                    </p>
                  </div>
                </div>
              </div>

              {/* Informations personnelles */}
              <div className="form-section">
                <h2 className="section-title">Informations personnelles</h2>
                <div className="form-grid">
                  <div className="form-group">
                    <label className="form-label" htmlFor="firstname">
                      Prénom
                    </label>
                    <input
                      id="firstname"
                      type="text"
                      className="form-input"
                      defaultValue="Thomas"
                    />
                  </div>
                  <div className="form-group">
                    <label className="form-label" htmlFor="lastname">
                      Nom
                    </label>
                    <input
                      id="lastname"
                      type="text"
                      className="form-input"
                      defaultValue="Dubois"
                    />
                  </div>
                  <div className="form-group">
                    <label className="form-label" htmlFor="email">
                      Adresse email
                    </label>
                    <input
                      id="email"
                      type="email"
                      className="form-input"
                      defaultValue="thomas.dubois@exemple.com"
                    />
                  </div>
                  <div className="form-group">
                    <label className="form-label" htmlFor="phone">
                      Téléphone
                    </label>
                    <input
                      id="phone"
                      type="tel"
                      className="form-input"
                      defaultValue="06 12 34 56 78"
                    />
                  </div>
                </div>
              </div>

              {/* Section mot de passe */}
              <div className="form-section">
                <div className="password-header">
                  <h2 className="section-title">Sécurité</h2>
                  <button
                    type="button"
                    className="toggle-password"
                    onClick={() => setShowPasswordChange(!showPasswordChange)}
                  >
                    {showPasswordChange ? "Annuler" : "Modifier le mot de passe"}
