import { useState, useEffect } from 'react';
import axios from 'axios';

function CardDemandeFormation() {
  // States
  const [formations, setFormations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filteredFormations, setFilteredFormations] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(3);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [confirmationMessage, setConfirmationMessage] = useState('');
  const [userInfo, setUserInfo] = useState(null);

  // Fetch user data
  useEffect(() => {
    try {
      const userData = localStorage.getItem('userData');
      if (userData) {
        const parsedData = JSON.parse(userData);
        if (parsedData && parsedData.email) {
          setUserInfo(parsedData);
        } else {
          setError("Données utilisateur incomplètes");
        }
      }
    } catch (err) {
      console.error("Erreur de parsing des données utilisateur:", err);
      setError("Erreur de lecture des données utilisateur");
    }
  }, []);

  // Fetch formations
  useEffect(() => {
    const fetchFormations = async () => {
      try {
        setLoading(true);
        const response = await axios.get('/api/getformation'); // Note the /api prefix
        
        if (response.data && Array.isArray(response.data)) {
          const formattedFormations = response.data.map(formation => ({
            id: formation._id,
            titre: formation.nomFormation,
            description: formation.description,
            date: new Date(formation.date).toISOString().split('T')[0],
            duree: formation.durée,
            places: formation.nbplaces,
            inscrits: formation.nbinscrits,
            statut: formation.statut,
            placesRestantes: formation.nbplaces - formation.nbinscrits
          }));
          
          setFormations(formattedFormations);
          setError(null);
        } else {
          throw new Error("Format de données inattendu");
        }
      } catch (err) {
        console.error("Erreur API:", err);
        setError(err.response?.data?.message || err.message || "Erreur serveur");
      } finally {
        setLoading(false);
      }
    };

    fetchFormations();
  }, []);

  // Filter formations
  useEffect(() => {
    const result = formations.filter(formation => 
      formation.statut === 'Programmée' && 
      formation.inscrits < formation.places
    ).filter(formation => 
      searchTerm ? 
        formation.titre.toLowerCase().includes(searchTerm.toLowerCase()) ||
        formation.description.toLowerCase().includes(searchTerm.toLowerCase())
      : true
    );
    
    setFilteredFormations(result);
  }, [formations, searchTerm]);

  // Handle formation request
  const handleRequestFormation = async (formation) => {
    if (!userInfo) {
      showError("Vous devez être connecté pour demander une formation");
      return;
    }

    try {
      const response = await axios.post('/api/adddemandeformation', {
        nom: userInfo.nom,
        prenom: userInfo.prenom,
        email: userInfo.email,
        nomFormation: formation.titre,
        statut: 'En attente'
      }, {
        headers: {
          'Content-Type': 'application/json'
        }
      });

      if (response.data && response.data._id) {
        showSuccess(`Votre demande pour "${formation.titre}" a été envoyée!`);
      } else {
        throw new Error("Réponse serveur invalide");
      }
    } catch (err) {
      console.error("Erreur d'envoi:", err);
      showError(
        err.response?.data?.message || 
        "Erreur lors de l'envoi de votre demande. Veuillez réessayer."
      );
    }
  };

  // Helper functions for notifications
  const showError = (message) => {
    setError(message);
    setConfirmationMessage(message);
    setShowConfirmation(true);
    setTimeout(() => setShowConfirmation(false), 3000);
  };

  const showSuccess = (message) => {
    setError(null);
    setConfirmationMessage(message);
    setShowConfirmation(true);
    setTimeout(() => setShowConfirmation(false), 3000);
  };

  // Render function
  return (
    <div>
      <input
        type="text"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        placeholder="Rechercher une formation"
      />

      <div>
        {loading ? (
          <p>Chargement...</p>
        ) : error ? (
          <p>{error}</p>
        ) : (
          filteredFormations.map((formation) => (
            <div key={formation.id}>
              <h3>{formation.titre}</h3>
              <p>{formation.description}</p>
              <p>{formation.date}</p>
              <p>Durée: {formation.duree} jours</p>
              <p>Places restantes: {formation.placesRestantes}</p>

              <button onClick={() => handleRequestFormation(formation)}>
                Demander
              </button>
            </div>
          ))
        )}
      </div>

      {showConfirmation && (
        <div>
          <p>{confirmationMessage}</p>
        </div>
      )}
    </div>
  );
}

export default CardDemandeFormation;
