import React, { useState, useEffect } from 'react';

const Toast = ({ message, isVisible, onClose }) => {
  useEffect(() => {
    if (isVisible) {
      const timer = setTimeout(() => {
        onClose();
      }, 3000);
      
      return () => clearTimeout(timer);
    }
  }, [isVisible, onClose]);

  if (!isVisible) return null;

  return (
    <div className="fixed top-4 right-4 bg-green-500 text-white px-4 py-3 rounded shadow-lg flex items-center z-50" style={{animation: 'fadeIn 0.5s'}}>
      <style jsx>{`
        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(-20px); }
          to { opacity: 1; transform: translateY(0); }
        }
      `}</style>
      <div className="mr-2">
        <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7"></path>
        </svg>
      </div>
      <p className="font-medium">{message}</p>
      <button onClick={onClose} className="ml-4 text-white">
        <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path>
        </svg>
      </button>
    </div>
  );
};

const CardDemandeAvance = () => {
  const [formData, setFormData] = useState({
    fullName: '',
    employeeId: '',
    amount: '',
    reason: '',
    urgency: 'standard'
  });
  
  const [showToast, setShowToast] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    if (e) e.preventDefault();
    // Ici, vous implémenteriez la logique pour soumettre la demande
    console.log('Demande soumise:', formData);
    setShowToast(true);
    
    // Réinitialiser le formulaire après soumission
    setFormData({
      fullName: '',
      employeeId: '',
      amount: '',
      reason: '',
      urgency: 'standard'
    });
  };

  const closeToast = () => {
    setShowToast(false);
  };

  return (
    <div className="w-full p-4 bg-gray-100 font-sans">
      <Toast 
        message="Votre demande d'avance sur salaire a été soumise avec succès!" 
        isVisible={showToast} 
        onClose={closeToast} 
      />
      
      <div className="text-center mb-10 py-5">
        <h1 className="text-4xl font-bold text-blue-800 inline-block relative pb-4">
          Demande d'avance sur salaire
          <span className="absolute bottom-0 left-1/2 transform -translate-x-1/2 w-3/5 h-1 bg-gradient-to-r from-blue-800 to-blue-500 rounded"></span>
        </h1>
      </div>

      <div className="bg-white rounded-lg p-6 shadow-sm">
        <div className="mb-4">
          <label className="block mb-1 text-sm font-medium text-gray-700" htmlFor="amount">
            Montant demandé (TND)
          </label>
          <input
            type="number"
            id="amount"
            name="amount"
            className="w-full p-2 border border-gray-200 rounded text-sm"
            value={formData.amount}
            onChange={handleChange}
            required
            min="1"
          />
          <p className="text-xs text-gray-500 mt-1">Le montant maximum est de 30% de votre salaire mensuel</p>
        </div>

        <div className="mb-4">
          <label className="block mb-1 text-sm font-medium text-gray-700" htmlFor="urgency">
            Type de demande
          </label>
          <select
            id="urgency"
            name="urgency"
            className="w-full p-2 border border-gray-200 rounded text-sm bg-white"
            value={formData.urgency}
            onChange={handleChange}
            required
          >
            <option value="standard">Standard (traitement sous 3 jours)</option>
            <option value="urgent">Urgente (traitement sous 24h)</option>
            <option value="exceptional">Exceptionnelle (évaluation sous 5 jours)</option>
          </select>
        </div>

        <div className="mb-4">
          <label className="block mb-1 text-sm font-medium text-gray-700" htmlFor="reason">
            Motif de la demande
          </label>
          <textarea
            id="reason"
            name="reason"
            className="w-full p-2 border border-gray-200 rounded text-sm min-h-20 resize-y"
            value={formData.reason}
            onChange={handleChange}
            required
          />
        </div>

        <button 
          onClick={handleSubmit}
          className="w-full py-2 bg-blue-600 hover:bg-blue-700 text-white rounded font-medium text-sm cursor-pointer"
        >
          Soumettre ma demande
        </button>
      </div>
    </div>
  );
};

export default CardDemandeAvance;
