                    <input
                      type="file"
                      accept="image/*"
                      onChange={handleImageUpload}
                      className="gp-form-input"
                    />
                  
                  </div>
                </div>
                
                <div className="gp-form-group">
                  <label className="gp-form-label">Nom *</label>
                  <input
                    type="text"
                    name="nom"
                    value={employeActuel.nom}
                    onChange={handleChange}
