import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CardDemandeAvance = () => {
  const [formData, setFormData] = useState({
    montant: '',
    motif: '',
    type: 'standard'
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [token, setToken] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userSalaire, setUserSalaire] = useState(0);
  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const [alertType, setAlertType] = useState('');
  const [tokenDebugInfo, setTokenDebugInfo] = useState(null); // Pour le déboggage du token

  // Fonction pour décoder le token JWT sans bibliothèque externe
  const decodeJWT = (token) => {
    try {
      // Vérifier si le token a le format attendu (header.payload.signature)
      if (!token || typeof token !== 'string' || !token.includes('.')) {
        console.error("Format de token invalide:", token);
        return null;
      }

      const parts = token.split('.');
      if (parts.length !== 3) {
        console.error("Le token ne contient pas 3 parties:", parts.length);
        return null;
      }

      const base64Url = parts[1];
      const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
      
      // Ajouter le padding si nécessaire
      const padding = '='.repeat((4 - base64.length % 4) % 4);
      const paddedBase64 = base64 + padding;
      
      try {
        const jsonPayload = decodeURIComponent(
          window.atob(paddedBase64)
            .split('')
            .map(c => '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2))
            .join('')
        );
        
        return JSON.parse(jsonPayload);
      } catch (error) {
        console.error("Erreur lors du décodage du token (étape 2):", error);
        // Essayons une méthode alternative pour les tokens non standard
        try {
          const jsonPayload = window.atob(paddedBase64);
          return JSON.parse(jsonPayload);
        } catch (error2) {
          console.error("Erreur avec la méthode alternative:", error2);
          return null;
        }
      }
    } catch (error) {
      console.error("Erreur lors du décodage du token (étape 1):", error);
      return null;
    }
  };

  useEffect(() => {
    // Récupérer les données utilisateur depuis localStorage
    const userDataStr = localStorage.getItem('userData');
    
    if (userDataStr) {
      try {
        const userData = JSON.parse(userDataStr);
        console.log("Données utilisateur:", userData);
        
        // Si le token est dans userData
        if (userData.token) {
          setToken(userData.token);
          setIsAuthenticated(true);
          
          // Décoder le token pour obtenir le salaire
          const decoded = decodeJWT(userData.token);
          setTokenDebugInfo(decoded);
          alert(userData.salaire)
          if (decoded && decoded.salaire) {
            setUserSalaire(decoded.salaire);
          } else {
            // Si le salaire n'est pas dans le token, utilisez la valeur du localStorage
            setUserSalaire(userData.salaire || 0);
          }
        }
      } catch (e) {
        console.error("Erreur de parsing userData:", e);
      }
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    
    if (name === 'montant') {
      const montant = parseFloat(value);
      
      if (montant > userSalaire) {
        setShowAlert(true);
        setAlertType('error');
        setAlertMessage(`Le montant demandé (${montant} TND) dépasse votre salaire mensuel (${userSalaire} TND)`);
      } else if (showAlert && montant <= userSalaire) {
        setShowAlert(false);
      }
    }
    
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Vérifier si le montant est supérieur au salaire
    if (parseFloat(formData.montant) > userSalaire) {
      setShowAlert(true);
      setAlertType('error');
      setAlertMessage(`Le montant demandé (${formData.montant} TND) dépasse votre salaire mensuel (${userSalaire} TND)`);
      return;
    }
    
    setLoading(true);
    setError(null);
    
    try {
      // Valider les données
      if (!formData.montant || !formData.motif) {
        throw new Error('Le montant et le motif sont obligatoires');
      }

      // Envoyer la demande au backend
      const response = await axios.post(
        '/demandes/avance',
        formData,
        {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': token ? `Bearer ${token}` : ''
          }
        }
      );

      if (response.data.success) {
        setSuccess(true);
        setShowAlert(true);
        setAlertType('success');
        setAlertMessage('Votre demande a été soumise avec succès!');
        
        // Réinitialiser le formulaire après succès
        setFormData({
          montant: '',
          motif: '',
          type: 'standard'
        });
      }
    } catch (err) {
      setError(err.response?.data?.message || err.message || 'Une erreur est survenue');
      setShowAlert(true);
      setAlertType('error');
      setAlertMessage(err.response?.data?.message || err.message || 'Une erreur est survenue');
    } finally {
      setLoading(false);
    }
  };

  // Ajout d'un bouton de déboggage pour afficher les informations du token
  const showTokenDebugInfo = () => {
    console.log("Token décodé (debug):", tokenDebugInfo);
    alert(JSON.stringify(tokenDebugInfo, null, 2));
  };

  const styles = {
    container: {
      width: '100%',
      maxWidth: '600px',
      margin: '0 auto',
      padding: '20px',
      backgroundColor: '#f5f7fa',
      fontFamily: 'Arial, sans-serif'
    },
    header: {
      textAlign: 'center',
      marginBottom: '20px',
      padding: '20px 0' 
    },
    title: {
      fontSize: '28px',
      fontWeight: '700',
      color: '#1e40af',
      margin: '0 0 10px 0',
      position: 'relative',
      display: 'inline-block',
      paddingBottom: '15px'
    },
    titleAfter: {
      content: '""',
      position: 'absolute',
      bottom: 0,
      left: '50%',
      transform: 'translateX(-50%)',
      width: '60%',
      height: '4px',
      background: 'linear-gradient(90deg, #1e40af, #3b82f6)',
      borderRadius: '2px'
    },
    subtitle: {
      fontSize: '16px',
      color: '#64748b',
      margin: '10px 0 0 0',
      letterSpacing: '0.3px'
    },
    form: {
      backgroundColor: '#ffffff',
      borderRadius: '8px',
      padding: '25px',
      boxShadow: '0 2px 6px rgba(0,0,0,0.1)'
    },
    formGroup: {
      marginBottom: '20px'
    },
    label: {
      display: 'block',
      marginBottom: '8px',
      fontWeight: '500',
      fontSize: '14px',
      color: '#334155'
    },
    input: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      boxSizing: 'border-box'
    },
    select: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      backgroundColor: '#fff',
      boxSizing: 'border-box'
    },
    textarea: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      minHeight: '100px',
      resize: 'vertical',
      boxSizing: 'border-box'
    },
    infoText: {
      fontSize: '12px',
      color: '#64748b',
      marginTop: '5px',
      fontStyle: 'italic'
    },
    submitBtn: {
      width: '100%',
      padding: '12px',
      backgroundColor: '#2563eb',
      color: '#ffffff',
      borderRadius: '4px',
      fontWeight: '500',
      border: 'none',
      cursor: 'pointer',
      fontSize: '16px',
      transition: 'background-color 0.3s',
      marginTop: '10px'
    },
    submitBtnHover: {
      backgroundColor: '#1d4ed8'
    },
    loading: {
      display: 'inline-block',
      marginLeft: '10px'
    },
    error: {
      color: '#dc2626',
      margin: '10px 0',
      fontSize: '14px'
    },
    success: {
      color: '#16a34a',
      margin: '10px 0',
      fontSize: '14px'
    },
    authError: {
      textAlign: 'center',
      backgroundColor: '#fee2e2',
      padding: '15px',
      borderRadius: '6px',
      margin: '20px 0',
      color: '#b91c1c'
    },
    alert: {
      padding: '15px',
      borderRadius: '6px',
      marginBottom: '20px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      transition: 'opacity 0.3s',
      opacity: 1
    },
    alertError: {
      backgroundColor: '#fee2e2',
      color: '#b91c1c',
      borderLeft: '4px solid #b91c1c'
    },
    alertSuccess: {
      backgroundColor: '#dcfce7',
      color: '#16a34a',
      borderLeft: '4px solid #16a34a'
    },
    alertIcon: {
      fontSize: '18px',
      marginRight: '10px'
    },
    alertContent: {
      flex: 1
    },
    alertClose: {
      backgroundColor: 'transparent',
      border: 'none',
      cursor: 'pointer',
      fontSize: '18px',
      color: 'inherit'
    },
    debugBtn: {
      backgroundColor: '#94a3b8',
      color: 'white',
      border: 'none',
      padding: '5px 10px',
      borderRadius: '4px',
      fontSize: '12px',
      cursor: 'pointer',
      marginTop: '10px'
    }
  };

  const Alert = ({ type, message, onClose }) => {
    return (
      <div style={{
        ...styles.alert,
        ...(type === 'error' ? styles.alertError : styles.alertSuccess)
      }}>
        <div style={styles.alertContent}>
          <span style={styles.alertIcon}>
            {type === 'error' ? '⚠️' : '✅'}
          </span>
          {message}
        </div>
        <button style={styles.alertClose} onClick={onClose}>×</button>
      </div>
    );
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>
          Demande d'avance sur salaire
          <span style={styles.titleAfter}></span>
        </h1>
        <p style={styles.subtitle}>Remplissez ce formulaire pour soumettre votre demande</p>
      </div>

      {showAlert && (
        <Alert 
          type={alertType} 
          message={alertMessage}
          onClose={() => setShowAlert(false)}
        />
      )}

      <form style={styles.form} onSubmit={handleSubmit}>
        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="montant">Montant demandé (TND)</label>
          <input
            type="number"
            id="montant"
            name="montant"
            style={styles.input}
            value={formData.montant}
            onChange={handleChange}
            required
            min="1"
          />
          <p style={styles.infoText}>
            Le montant maximum est de 30% de votre salaire mensuel. 
            Votre salaire actuel est de {userSalaire} TND.
          </p>
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="type">Type de demande</label>
          <select
            id="type"
            name="type"
            style={styles.select}
            value={formData.type}
            onChange={handleChange}
            required
          >
            <option value="standard">Standard (traitement sous 3 jours)</option>
            <option value="urgent">Urgente (traitement sous 24h)</option>
          </select>
        </div>

        <div style={styles.formGroup}>
          <label style={styles.label} htmlFor="motif">Motif de la demande</label>
          <textarea
            id="motif"
            name="motif"
            style={styles.textarea}
            value={formData.motif}
            onChange={handleChange}
            required
            placeholder="Décrivez brièvement la raison de votre demande d'avance"
          />
        </div>

        <button 
          type="submit" 
          style={styles.submitBtn}
          onMouseOver={(e) => e.target.style.backgroundColor = '#1d4ed8'}
          onMouseOut={(e) => e.target.style.backgroundColor = '#2563eb'}
          disabled={loading}
        >
          {loading ? 'Envoi en cours...' : 'Soumettre ma demande'}
        </button>
        
        {/* Bouton de déboggage - à retirer en production */}
        <button 
          type="button" 
          style={styles.debugBtn}
          onClick={showTokenDebugInfo}
        >
          Debug Token
        </button>
      </form>
    </div>
  );
};

export default CardDemandeAvance;
