import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

const CardDemandeConge = () => {
  // États
  const [formData, setFormData] = useState({
    startDate: '',
    endDate: '',
    reason: '',
    type: 'congé payé',
    medicalFile: null
  });
  const [fileName, setFileName] = useState('');
  const fileInputRef = useRef(null);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [confirmationMessage, setConfirmationMessage] = useState('');
  const [userData, setUserData] = useState({
    nom: '',
    prenom: '',
    email: '',
    soldedeconge: 0
  });
  const [isAuthenticated, setIsAuthenticated] = useState(true); // Temporairement désactivé pour test

  // Style minimal
  const styles = {
    container: {
      padding: '20px',
      maxWidth: '600px',
      margin: '0 auto'
    },
    input: {
      width: '100%',
      padding: '10px',
      marginBottom: '15px',
      border: '1px solid #ddd',
      borderRadius: '4px'
    },
    button: {
      backgroundColor: '#2563eb',
      color: 'white',
      padding: '12px',
      border: 'none',
      borderRadius: '4px',
      width: '100%',
      cursor: 'pointer'
    }
  };

  // Temporaire: Contournement de l'authentification pour test
  useEffect(() => {
    // Simuler des données utilisateur pour test
    setUserData({
      nom: 'Doe',
      prenom: 'John',
      email: 'john.doe@example.com',
      soldedeconge: 25
    });
    setIsAuthenticated(true);
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    if (e.target.files[0]) {
      setFormData(prev => ({ ...prev, medicalFile: e.target.files[0] }));
      setFileName(e.target.files[0].name);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      const formDataToSend = new FormData();
      formDataToSend.append('DateDebut', formData.startDate);
      formDataToSend.append('DateFin', formData.endDate);
      formDataToSend.append('type', formData.type);
      formDataToSend.append('motif', formData.reason);
      formDataToSend.append('nom', userData.nom);
      formDataToSend.append('prenom', userData.prenom);
      formDataToSend.append('email', userData.email);
      
      if (formData.medicalFile) {
        formDataToSend.append('medicalFile', formData.medicalFile);
      }

      await axios.post('/adddemandeconge', formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setConfirmationMessage('Demande envoyée avec succès!');
      setShowConfirmation(true);
      
      // Réinitialisation
      setFormData({
        startDate: '',
        endDate: '',
        reason: '',
        type: 'congé payé',
        medicalFile: null
      });
      setFileName('');

    } catch (error) {
      console.error('Erreur:', error);
      alert("Erreur lors de l'envoi: " + (error.response?.data?.message || error.message));
    }
  };

  return (
    <div style={styles.container}>
      <h2>Demande de Congé</h2>
      
      <form onSubmit={handleSubmit}>
        <div>
          <label>Type de congé *</label>
          <select
            name="type"
            value={formData.type}
            onChange={handleChange}
            style={styles.input}
            required
          >
            <option value="congé payé">Congé payé</option>
            <option value="congé maladie">Congé maladie</option>
            <option value="congé sans solde">Congé sans solde</option>
          </select>
        </div>

        {formData.type === 'congé maladie' && (
          <div>
            <label>Justificatif médical *</label>
            <input
              type="file"
              onChange={handleFileChange}
              accept=".pdf,.jpg,.jpeg,.png"
              required
              style={styles.input}
            />
            {fileName && <p>Fichier sélectionné: {fileName}</p>}
          </div>
        )}

        <div>
          <label>Motif {formData.type !== 'congé payé' && '*'}</label>
          <input
            type="text"
            name="reason"
            value={formData.reason}
            onChange={handleChange}
            style={styles.input}
            required={formData.type !== 'congé payé'}
          />
        </div>

        <div style={{ display: 'flex', gap: '15px' }}>
          <div style={{ flex: 1 }}>
            <label>Date de début *</label>
            <input
              type="date"
              name="startDate"
              value={formData.startDate}
              onChange={handleChange}
              style={styles.input}
              required
            />
          </div>
          <div style={{ flex: 1 }}>
            <label>Date de fin *</label>
            <input
              type="date"
              name="endDate"
              value={formData.endDate}
              onChange={handleChange}
              style={styles.input}
              required
            />
          </div>
        </div>

        <button type="submit" style={styles.button}>
          Soumettre la demande
        </button>
      </form>

      {showConfirmation && (
        <div style={{ 
          marginTop: '20px',
          padding: '10px',
          backgroundColor: '#4CAF50',
          color: 'white',
          borderRadius: '4px'
        }}>
          {confirmationMessage}
        </div>
      )}
    </div>
  );
};

export default CardDemandeConge;
