import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

const CardDemandeConge = () => {
  // États
  const [formData, setFormData] = useState({
    startDate: '',
    endDate: '',
    reason: '',
    type: 'congé payé',
    medicalFile: null
  });

  const [fileName, setFileName] = useState('');
  const fileInputRef = useRef(null);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [confirmationMessage, setConfirmationMessage] = useState('');
  const [userData, setUserData] = useState(null);
  const [token, setToken] = useState('');

  // ✅ Récupération des données utilisateur et token (comme CardDemandeAvance)
  useEffect(() => {
    const userDataStr = localStorage.getItem('user');
    const tokenFromStorage =
      localStorage.getItem("authToken") ||
      localStorage.getItem("token") ||
      localStorage.getItem("jwt");

    console.log('Données de localStorage:', { userDataStr, tokenFromStorage });

    if (userDataStr) {
      try {
        const parsedUserData = JSON.parse(userDataStr);
        setUserData(parsedUserData);
      } catch (error) {
        console.error("Erreur de parsing user:", error);
      }
    }

    if (tokenFromStorage) {
      setToken(tokenFromStorage);
    } else {
      console.warn("Aucun token trouvé dans localStorage.");
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formDataToSend = new FormData();
      formDataToSend.append('DateDebut', formData.startDate);
      formDataToSend.append('DateFin', formData.endDate);
      formDataToSend.append('type', formData.type);
      formDataToSend.append('motif', formData.reason);
      formDataToSend.append('nom', userData?.nom || '');
      formDataToSend.append('prenom', userData?.prenom || '');
      formDataToSend.append('email', userData?.email || '');

      if (formData.medicalFile) {
        formDataToSend.append('medicalFile', formData.medicalFile);
      }

      const config = {
        headers: {
          'Content-Type': 'multipart/form-data',
          'Authorization': `Bearer ${token}`
        }
      };

      const response = await axios.post('/adddemandeconge', formDataToSend, config);

      setConfirmationMessage('Demande envoyée avec succès !');
      setShowConfirmation(true);

      // Réinitialisation
      setFormData({
        startDate: '',
        endDate: '',
        reason: '',
        type: 'congé payé',
        medicalFile: null
      });
      setFileName('');

    } catch (error) {
      console.error('Erreur:', error.response?.data || error.message);
      alert(error.response?.data?.message || "Erreur lors de l'envoi");
    }
  };

  return (
    <div style={{ padding: '20px', maxWidth: '600px', margin: '0 auto' }}>
      <h2>Demande de Congé</h2>

      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '15px' }}>
          <label>Type de congé *</label>
          <select
            name="type"
            value={formData.type}
            onChange={(e) => setFormData({ ...formData, type: e.target.value })}
            style={{ width: '100%', padding: '10px', marginBottom: '15px', border: '1px solid #ddd', borderRadius: '4px' }}
            required
          >
            <option value="congé payé">Congé payé</option>
            <option value="congé maladie">Congé maladie</option>
          </select>
        </div>

        <div style={{ marginBottom: '15px' }}>
          <label>Motif *</label>
          <input
            type="text"
            name="reason"
            value={formData.reason}
            onChange={(e) => setFormData({ ...formData, reason: e.target.value })}
            style={{ width: '100%', padding: '10px', border: '1px solid #ddd', borderRadius: '4px' }}
            required
          />
        </div>

        <div style={{ display: 'flex', gap: '15px', marginBottom: '15px' }}>
          <div style={{ flex: 1 }}>
            <label>Date de début *</label>
            <input
              type="date"
              name="startDate"
              value={formData.startDate}
              onChange={(e) => setFormData({ ...formData, startDate: e.target.value })}
              style={{ width: '100%', padding: '10px', border: '1px solid #ddd', borderRadius: '4px' }}
              required
            />
          </div>
          <div style={{ flex: 1 }}>
            <label>Date de fin *</label>
            <input
              type="date"
              name="endDate"
              value={formData.endDate}
              onChange={(e) => setFormData({ ...formData, endDate: e.target.value })}
              style={{ width: '100%', padding: '10px', border: '1px solid #ddd', borderRadius: '4px' }}
              required
            />
          </div>
        </div>

        <button
          type="submit"
          style={{
            backgroundColor: '#2563eb',
            color: 'white',
            padding: '12px',
            border: 'none',
            borderRadius: '4px',
            width: '100%',
            cursor: 'pointer'
          }}
        >
          Soumettre la demande
        </button>
      </form>

      {showConfirmation && (
        <div style={{
          marginTop: '20px',
          padding: '10px',
          backgroundColor: '#4CAF50',
          color: 'white',
          borderRadius: '4px'
        }}>
          {confirmationMessage}
        </div>
      )}

      {/* Section Debug */}
      <div style={{ marginTop: '30px', padding: '15px', backgroundColor: '#f5f5f5', borderRadius: '4px' }}>
        <h3>Informations de Debug:</h3>
        <p><strong>Token:</strong> {token ? 'Présent' : 'Manquant'}</p>
        <p><strong>Données Utilisateur:</strong></p>
        <pre style={{
          backgroundColor: '#fff',
          padding: '10px',
          borderRadius: '4px',
          overflowX: 'auto'
        }}>
          {JSON.stringify(userData, null, 2)}
        </pre>
      </div>
    </div>
  );
};

export default CardDemandeConge;
