import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

const CardDemandeConge = () => {
  // États
  const [formData, setFormData] = useState({
    startDate: '',
    endDate: '',
    reason: '',
    type: 'congé payé',
    notes: '',
    medicalFile: null
  });
  const [fileName, setFileName] = useState('');
  const fileInputRef = useRef(null);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [confirmationMessage, setConfirmationMessage] = useState('');
  const [userData, setUserData] = useState({
    nom: '',
    prenom: '',
    email: '',
    soldedeconge: 0
  });
  const [error, setError] = useState(null);
  const [token, setToken] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Styles
  const styles = {
    container: {
      backgroundColor: '#f0f4f8',
      minHeight: '100vh',
      padding: '20px',
      fontFamily: '"Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif',
      color: '#334155'
    },
    mainContent: {
      maxWidth: '1200px',
      margin: '0 auto',
      paddingTop: '2rem'
    },
    header: {
      textAlign: 'center',
      marginBottom: '40px'
    },
    title: {
      fontSize: '2.2rem',
      fontWeight: '700',
      color: '#1e40af',
      margin: '0 0 10px 0',
      position: 'relative',
      display: 'inline-block',
      paddingBottom: '15px'
    },
    titleAfter: {
      content: '""',
      position: 'absolute',
      bottom: 0,
      left: '50%',
      transform: 'translateX(-50%)',
      width: '60%',
      height: '4px',
      background: 'linear-gradient(90deg, #1e40af, #3b82f6)',
      borderRadius: '2px'
    },
    subtitle: {
      fontSize: '17px',
      color: '#64748b',
      marginTop: '10px',
      margin: '0'
    },
    formContainer: {
      backgroundColor: '#ffffff',
      borderRadius: '12px',
      padding: '35px',
      marginBottom: '35px',
      boxShadow: '0 4px 10px rgba(0,0,0,0.06)'
    },
    formHeader: {
      marginBottom: '25px',
      borderBottom: '1px solid #e2e8f0',
      paddingBottom: '15px'
    },
    formTitle: {
      fontSize: '22px',
      fontWeight: '700',
      color: '#1e40af',
      margin: '0 0 8px 0'
    },
    formDescription: {
      fontSize: '15px',
      color: '#64748b',
      margin: '0'
    },
    formGroup: {
      marginBottom: '20px'
    },
    label: {
      display: 'block',
      fontSize: '15px',
      fontWeight: '600',
      color: '#475569',
      marginBottom: '8px'
    },
    input: {
      width: '100%',
      padding: '12px 14px',
      border: '1px solid #e2e8f0',
      borderRadius: '8px',
      fontSize: '15px',
      boxShadow: '0 1px 2px rgba(0,0,0,0.04)',
      transition: 'all 0.2s ease'
    },
    formGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(2, 1fr)',
      gap: '20px',
      marginBottom: '20px'
    },
    selectContainer: {
      position: 'relative'
    },
    select: {
      width: '100%',
      padding: '12px 14px',
      border: '1px solid #e2e8f0',
      borderRadius: '8px',
      fontSize: '15px',
      boxShadow: '0 1px 2px rgba(0,0,0,0.04)',
      appearance: 'none',
      paddingRight: '40px'
    },
    congeInfoTag: {
      display: 'block',
      fontSize: '14px',
      color: '#64748b',
      marginTop: '5px'
    },
    textarea: {
      width: '100%',
      padding: '12px 14px',
      border: '1px solid #e2e8f0',
      borderRadius: '8px',
      fontSize: '15px',
      minHeight: '100px',
      resize: 'vertical'
    },
    alert: {
      backgroundColor: '#e0f2fe',
      borderRadius: '8px',
      padding: '14px',
      marginBottom: '20px',
      display: 'flex',
      alignItems: 'center',
      gap: '10px'
    },
    alertText: {
      color: '#0369a1',
      fontSize: '15px',
      fontWeight: '500',
      margin: '0'
    },
    buttonContainer: {
      marginTop: '25px'
    },
    button: {
      width: '100%',
      padding: '14px',
      backgroundColor: '#2563eb',
      color: '#ffffff',
      border: 'none',
      borderRadius: '8px',
      fontSize: '16px',
      fontWeight: '600',
      cursor: 'pointer',
      transition: 'all 0.2s ease'
    },
    buttonDisabled: {
      backgroundColor: '#93c5fd',
      cursor: 'not-allowed'
    },
    fileInputContainer: {
      position: 'relative',
      display: 'flex',
      flexDirection: 'column',
      width: '100%'
    },
    fileInputLabel: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '12px 14px',
      border: '1px dashed #3b82f6',
      borderRadius: '8px',
      backgroundColor: '#f1f5f9',
      color: '#475569',
      fontSize: '15px',
      cursor: 'pointer',
      minHeight: '50px'
    },
    selectedFile: {
      display: 'flex',
      alignItems: 'center',
      gap: '10px',
      marginTop: '10px',
      padding: '10px',
      backgroundColor: '#f1f5f9',
      borderRadius: '6px',
      fontSize: '14px'
    },
    removeButton: {
      background: 'none',
      border: 'none',
      color: '#ef4444',
      cursor: 'pointer',
      fontSize: '16px',
      padding: '0 5px'
    },
    errorAlert: {
      backgroundColor: '#fee2e2',
      color: '#b91c1c',
      padding: '15px',
      borderRadius: '8px',
      marginBottom: '20px',
      display: 'flex',
      alignItems: 'center',
      gap: '10px'
    },
    toastMessage: {
      position: 'fixed',
      bottom: '30px',
      left: '50%',
      transform: 'translateX(-50%)',
      backgroundColor: '#34d399',
      color: 'white',
      borderRadius: '12px',
      padding: '5px',
      boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1)',
      zIndex: '1000',
      animation: 'slideUp 0.3s ease, fadeOut 0.5s ease 2.5s',
      minWidth: '300px',
      maxWidth: '600px'
    },
    toastContent: {
      display: 'flex',
      alignItems: 'center',
      gap: '12px',
      padding: '15px 20px'
    },
    toastIcon: {
      fontSize: '1.2rem',
      backgroundColor: 'white',
      color: '#34d399',
      width: '30px',
      height: '30px',
      borderRadius: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    }
  };

  // Types de congés
  const congeTypes = [
    {
      type: 'congé payé',
      title: 'Congé Payé Standard',
      description: 'Congé annuel rémunéré',
      remainingDays: userData.soldedeconge,
      icon: '🏖️'
    },
    {
      type: 'rtt',
      title: 'RTT',
      description: 'Réduction du temps de travail',
      remainingDays: 8,
      icon: '⏱️'
    },
    {
      type: 'congé maladie',
      title: 'Congé Maladie',
      description: 'Absence pour raison médicale',
      remainingDays: 'Illimité',
      icon: '🏥'
    },
    {
      type: 'congé parental',
      title: 'Congé Parental',
      description: 'Congé pour s\'occuper d\'un enfant',
      remainingDays: 'Selon droits',
      icon: '👶'
    },
    {
      type: 'congé familial',
      title: 'Événement Familial',
      description: 'Mariage, naissance, décès',
      remainingDays: 'Selon événement',
      icon: '👪'
    },
    {
      type: 'congé sans solde',
      title: 'Congé Sans Solde',
      description: 'Absence autorisée non rémunérée',
      remainingDays: 'Illimité',
      icon: '📅'
    }
  ];

  // Récupération du token et des données utilisateur
  useEffect(() => {
    const fetchUserData = () => {
      try {
        const authToken = localStorage.getItem('authToken') || 
                         localStorage.getItem('token') || 
                         localStorage.getItem('jwt');
        
        if (!authToken) {
          throw new Error("Aucun token d'authentification trouvé");
        }

        setToken(authToken);

        const decoded = JSON.parse(atob(authToken.split('.')[1]));
        
        if (!decoded.email) {
          throw new Error("Token invalide ou expiré");
        }

        setUserData({
          nom: decoded.nom || '',
          prenom: decoded.prenom || '',
          email: decoded.email,
          soldedeconge: decoded.soldedeconge || 0
        });

      } catch (error) {
        console.error("Erreur d'authentification:", error);
        setError("Erreur d'authentification. Veuillez vous reconnecter.");
      }
    };

    fetchUserData();
  }, []);

  // Gestion des changements de formulaire
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };
  
  // Gestion des fichiers
  const handleFileChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      if (file.size > 5 * 1024 * 1024) { // 5MB max
        setError('Le fichier est trop volumineux (max 5MB)');
        return;
      }
      setFormData(prev => ({
        ...prev,
        medicalFile: file
      }));
      setFileName(file.name);
      setError(null);
    }
  };
  
  // Suppression de fichier
  const removeFile = () => {
    setFormData(prev => ({
      ...prev,
      medicalFile: null
    }));
    setFileName('');
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  // Soumission du formulaire
  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError(null);
    
    if (!token) {
      setError("Erreur d'authentification. Veuillez vous reconnecter.");
      setIsSubmitting(false);
      return;
    }

    // Validation
    if (formData.type === 'congé maladie' && !formData.medicalFile) {
      setError('Veuillez joindre votre justificatif médical');
      setIsSubmitting(false);
      return;
    }

    try {
      const formDataToSend = new FormData();
      
      formDataToSend.append('DateDebut', formData.startDate);
      formDataToSend.append('DateFin', formData.endDate);
      formDataToSend.append('type', formData.type);
      formDataToSend.append('motif', formData.reason || '');
      formDataToSend.append('notes', formData.notes || '');
      formDataToSend.append('nom', userData.nom);
      formDataToSend.append('prenom', userData.prenom);
      formDataToSend.append('email', userData.email);
      
      if (formData.medicalFile) {
        formDataToSend.append('medicalFile', formData.medicalFile);
      }

      const response = await axios.post(
        '/adddemandeconge', 
        formDataToSend,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
            'Authorization': token.startsWith('Bearer ') ? token : `Bearer ${token}`
          }
        }
      );

      setConfirmationMessage(`Demande de ${formData.type} du ${formData.startDate} au ${formData.endDate} enregistrée !`);
      setShowConfirmation(true);
      
      // Réinitialisation
      setFormData({
        startDate: '',
        endDate: '',
        reason: '',
        type: 'congé payé',
        notes: '',
        medicalFile: null
      });
      setFileName('');

    } catch (error) {
      console.error('Erreur:', error);
      let errorMessage = "Erreur lors de la soumission";
      
      if (error.response) {
        if (error.response.status === 401) {
          errorMessage = "Session expirée. Veuillez vous reconnecter.";
        } else {
          errorMessage = error.response.data?.message || 
                       error.response.data?.error || 
                       errorMessage;
        }
      } else if (error.message) {
        errorMessage = error.message;
      }
      
      setError(errorMessage);
    } finally {
      setIsSubmitting(false);
    }
  };
  
  // Calcul des jours de congé
  const calculateDays = () => {
    if (!formData.startDate || !formData.endDate) return 0;
    
    const start = new Date(formData.startDate);
    const end = new Date(formData.endDate);
    const diffTime = Math.abs(end - start);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24)) + 1;
    
    return diffDays;
  };

  const selectedCongeType = congeTypes.find(conge => conge.type === formData.type);
  const isMaladieType = formData.type === 'congé maladie';
  const needsJustification = ['congé maladie', 'congé familial'].includes(formData.type);
  
  return (
    <div style={styles.container}>
      <div style={styles.mainContent}>
        <div style={styles.header}>
          <h1 style={styles.title}>
            Demande de Congé
            <span style={styles.titleAfter}></span>
          </h1>
          <p style={styles.subtitle}>
            Bonjour {userData.prenom} {userData.nom}, découvrez et soumettez vos demandes de congés
          </p>
        </div>

        {error && (
          <div style={styles.errorAlert}>
            <span>⚠️</span>
            <p>{error}</p>
          </div>
        )}

        <div style={styles.formContainer}>
          <div style={styles.formHeader}>
            <h2 style={styles.formTitle}>Formulaire de demande de congé</h2>
            <p style={styles.formDescription}>Veuillez remplir tous les champs obligatoires</p>
          </div>
          
          <form onSubmit={handleSubmit}>
            <div style={styles.formGroup}>
              <label style={styles.label} htmlFor="type">
                Type de congé
              </label>
              <div style={styles.selectContainer}>
                <select
                  id="type"
                  name="type"
                  value={formData.type}
                  onChange={handleChange}
                  style={styles.select}
                  required
                >
                  {congeTypes.map((conge, index) => (
                    <option key={index} value={conge.type}>
                      {conge.icon} {conge.title}
                    </option>
                  ))}
                </select>
              </div>
              <span style={styles.congeInfoTag}>
                {selectedCongeType?.description} - 
                {typeof selectedCongeType?.remainingDays === 'number' 
                  ? ` ${selectedCongeType.remainingDays} jours disponibles` 
                  : ` ${selectedCongeType?.remainingDays}`}
              </span>
            </div>
            
            {isMaladieType ? (
              <div style={styles.formGroup}>
                <label style={styles.label} htmlFor="medicalFile">
                  Justificatif médical *
                </label>
                <div style={styles.fileInputContainer}>
                  <label style={styles.fileInputLabel} htmlFor="medicalFile">
                    {formData.medicalFile ? 'Changer de fichier' : 'Joindre votre justificatif médical (PDF, JPG, PNG - max 5MB)'}
                  </label>
                  <input
                    type="file"
                    id="medicalFile"
                    ref={fileInputRef}
                    style={{ display: 'none' }}
                    accept=".pdf,.jpg,.jpeg,.png"
                    onChange={handleFileChange}
                    required={isMaladieType}
                  />
                </div>
                {formData.medicalFile && (
                  <div style={styles.selectedFile}>
                    <span>📄</span>
                    <span style={{ flex: 1 }}>{fileName}</span>
                    <button 
                      type="button" 
                      style={styles.removeButton}
                      onClick={removeFile}
                    >
                      ✕
                    </button>
                  </div>
                )}
              </div>
            ) : (
              <div style={styles.formGroup}>
                <label style={styles.label} htmlFor="reason">
                  Motif {needsJustification && '*'}
                </label>
                <input
                  type="text"
                  id="reason"
                  name="reason"
                  value={formData.reason}
                  onChange={handleChange}
                  style={styles.input}
                  placeholder={needsJustification 
                    ? "Précisez le motif (obligatoire)" 
                    : "Précisez le motif de votre demande (facultatif)"}
                  required={needsJustification}
                />
              </div>
            )}
            
            <div style={styles.formGrid}>
              <div style={styles.formGroup}>
                <label style={styles.label} htmlFor="startDate">
                  Date de début *
                </label>
                <input
                  type="date"
                  id="startDate"
                  name="startDate"
                  value={formData.startDate}
                  onChange={handleChange}
                  style={styles.input}
                  required
                  min={new Date().toISOString().split('T')[0]}
                />
              </div>
              
              <div style={styles.formGroup}>
                <label style={styles.label} htmlFor="endDate">
                  Date de fin *
                </label>
                <input
                  type="date"
                  id="endDate"
                  name="endDate"
                  value={formData.endDate}
                  onChange={handleChange}
                  style={styles.input}
                  required
                  min={formData.startDate || new Date().toISOString().split('T')[0]}
                />
              </div>
            </div>
            
            {formData.startDate && formData.endDate && (
              <div style={styles.alert}>
                <span>ℹ️</span>
                <p style={styles.alertText}>
                  Durée: <strong>{calculateDays()} jours</strong>
                  {formData.type === 'congé payé' && userData.soldedeconge && (
                    <span> - Solde restant: {userData.soldedeconge - calculateDays()} jours</span>
                  )}
                </p>
              </div>
            )}
            
            <div style={styles.formGroup}>
              <label style={styles.label} htmlFor="notes">
                Notes complémentaires
              </label>
              <textarea
                id="notes"
                name="notes"
                value={formData.notes}
                onChange={handleChange}
                style={styles.textarea}
                placeholder="Informations additionnelles (facultatif)"
              />
            </div>
            
            <div style={styles.buttonContainer}>
              <button 
                type="submit" 
                style={{
                  ...styles.button,
                  ...(isSubmitting ? styles.buttonDisabled : {})
                }}
                disabled={isSubmitting}
              >
                {isSubmitting ? 'Envoi en cours...' : 'Soumettre la demande'}
              </button>
            </div>
          </form>
        </div>
      </div>

      {showConfirmation && (
        <div style={styles.toastMessage}>
          <div style={styles.toastContent}>
            <span style={styles.toastIcon}>✓</span>
            <p>{confirmationMessage}</p>
          </div>
        </div>
      )}

      <style>
        {`
        @keyframes slideUp {
          from {
            transform: translate(-50%, 100%);
            opacity: 0;
          }
          to {
            transform: translate(-50%, 0);
            opacity: 1;
          }
        }

        @keyframes fadeOut {
          from {
            opacity: 1;
          }
          to {
            opacity: 0;
          }
        }
        `}
      </style>
    </div>
  );
};

export default CardDemandeConge;
