import { useState } from "react";
import { Search, AlertCircle, Check, X, Eye } from "lucide-react";

export default function GestionAvances() {
  // Données fictives des demandes d'avance uniquement
  const [demandes, setDemandes] = useState([
    {
      id: 1,
      employeId: 2,
      nom: "Lambert",
      prenom: "Marie",
      departement: "RH",
      typeDemande: "Standard",
      statut: "En attente",
      dateDemande: "05/04/2025",
      montant: "1500",
      motif: "J'ai besoin d'une avance pour l'achat d'un ordinateur nécessaire à mon travail à distance.",
      reponse: "",
    },
    {
      id: 2,
      employeId: 5,
      nom: "Dubois",
      prenom: "Pierre",
      departement: "Ventes",
      typeDemande: "Urgente",
      statut: "Complété",
      dateDemande: "10/03/2025",
      montant: "800",
      motif: "Réparation urgente de ma voiture pour pouvoir continuer à me rendre au travail.",
      reponse: "Demande approuvée le 11/03/2025",
    },
    {
      id: 3,
      employeId: 8,
      nom: "Leroy",
      prenom: "Emilie",
      departement: "Technique",
      typeDemande: "Standard",
      statut: "Refusé",
      dateDemande: "22/03/2025",
      montant: "2000",
      motif: "Avance sur salaire pour frais médicaux imprévus.",
      reponse: "Demande refusée - montant supérieur à la limite autorisée",
    },
    {
      id: 4,
      employeId: 12,
      nom: "Moreau",
      prenom: "Thomas",
      departement: "Finance",
      typeDemande: "Standard",
      statut: "En attente",
      dateDemande: "12/04/2025",
      montant: "1200",
      motif: "Avance pour payer l'acompte de mon nouveau logement suite à ma mutation dans une autre ville.",
      reponse: "",
    },
    {
      id: 5,
      employeId: 7,
      nom: "Garcia",
      prenom: "Laura",
      departement: "Marketing",
      typeDemande: "Urgente",
      statut: "En attente",
      dateDemande: "15/04/2025",
      montant: "650",
      motif: "Achat de matériel pour le prochain événement marketing prévu la semaine prochaine.",
      reponse: "",
    },
  ]);

  const [demandesFiltrees, setDemandesFiltrees] = useState(demandes);
  const [recherche, setRecherche] = useState("");
  const [motifVisible, setMotifVisible] = useState(null);

  // Filtrage des demandes basé sur la recherche
  const filtrerDemandes = (terme) => {
    setRecherche(terme);
    if (!terme.trim()) {
      setDemandesFiltrees(demandes);
      return;
    }
    
    const termeLower = terme.toLowerCase();
    const resultats = demandes.filter(
      demande => 
        demande.nom.toLowerCase().includes(termeLower) ||
        demande.prenom.toLowerCase().includes(termeLower) ||
        demande.montant.includes(terme)
    );
    
    setDemandesFiltrees(resultats);
  };

  // Afficher ou cacher le motif
  const toggleMotif = (id) => {
    setMotifVisible(motifVisible === id ? null : id);
  };

  // Rendu du statut avec l'icône appropriée
  const renderStatus = (statut) => {
    switch (statut) {
      case "Complété":
        return (
          <div className="flex items-center gap-1 rounded-full px-2 py-1 bg-green-100 text-green-800 text-xs font-medium">
            <Check size={14} />
            <span>Approuvé</span>
          </div>
        );
      case "En attente":
        return (
          <div className="flex items-center gap-1 rounded-full px-2 py-1 bg-yellow-100 text-yellow-800 text-xs font-medium">
            <AlertCircle size={14} />
            <span>En attente</span>
          </div>
        );
      case "Refusé":
        return (
          <div className="flex items-center gap-1 rounded-full px-2 py-1 bg-red-100 text-red-800 text-xs font-medium">
            <X size={14} />
            <span>Refusé</span>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="w-full rounded-lg shadow-lg bg-white overflow-hidden">
      {/* En-tête et recherche */}
      <div className="p-6 border-b border-gray-200">
        <h1 className="text-2xl font-bold text-gray-800 mb-4">
          Gestion des Demandes d'Avances
        </h1>
        
        {/* Barre de recherche */}
        <div className="relative">
          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
            <Search size={18} className="text-gray-400" />
          </div>
          <input
            type="text"
            className="pl-10 pr-4 py-2 w-full border border-gray-300 rounded-md focus:ring-2 focus:ring-teal-500 focus:border-teal-500"
            placeholder="Rechercher par nom ou montant..."
            value={recherche}
            onChange={(e) => filtrerDemandes(e.target.value)}
          />
        </div>
      </div>

      {/* Tableau des demandes */}
      <div className="overflow-x-auto">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Employé
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Montant
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Type
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Statut
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Motif
              </th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {demandesFiltrees.map((demande) => (
              <tr key={demande.id} className="hover:bg-gray-50">
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm font-medium text-gray-900">
                    {demande.prenom} {demande.nom}
                  </div>
                  <div className="text-xs text-gray-500">{demande.departement}</div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm font-semibold text-gray-900">
                    {demande.montant} €
                  </div>
                  <div className="text-xs text-gray-500">{demande.dateDemande}</div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                    demande.typeDemande === "Urgente" 
                      ? "bg-purple-100 text-purple-800" 
                      : "bg-blue-100 text-blue-800"
                  }`}>
                    {demande.typeDemande}
                  </span>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  {renderStatus(demande.statut)}
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="relative">
                    <button
                      onClick={() => toggleMotif(demande.id)}
                      className="inline-flex items-center px-3 py-1 border border-gray-300 text-sm leading-5 font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-teal-500"
                    >
                      <Eye size={16} className="mr-1" />
                      Voir motif
                    </button>
                    
                    {motifVisible === demande.id && (
                      <div className="absolute z-10 mt-2 w-72 bg-white border border-gray-200 rounded-md shadow-lg p-4 text-sm text-gray-700">
                        <h4 className="font-semibold mb-2">Motif de la demande:</h4>
                        <p>{demande.motif}</p>
                        {demande.reponse && (
                          <>
                            <h4 className="font-semibold mt-3 mb-1">Réponse:</h4>
                            <p className="italic">{demande.reponse}</p>
                          </>
                        )}
                      </div>
                    )}
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      
      {/* Information sur les résultats */}
      <div className="px-6 py-4 border-t border-gray-200 bg-gray-50">
        <div className="text-sm text-gray-500">
          Affichage de {demandesFiltrees.length} sur {demandes.length} demandes d'avance
        </div>
      </div>
    </div>
  );
}
