import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

const CardDemandeConge = () => {
  const [formData, setFormData] = useState({
    startDate: '',
    endDate: '',
    reason: '',
    type: 'congé payé',
    notes: '',
    medicalFile: null
  });
  
  const [fileName, setFileName] = useState('');
  const fileInputRef = useRef(null);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [confirmationMessage, setConfirmationMessage] = useState('');
  const [submitError, setSubmitError] = useState(null);
  const [userData, setUserData] = useState({
    nom: '',
    prenom: '',
    email: '',
    soldeConge: 0
  });
  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const [alertType, setAlertType] = useState('');
  const [token, setToken] = useState('');

  // Styles
  const styles = {
    container: {
      width: '100%',
      maxWidth: '1200px',
      margin: '0 auto',
      padding: '20px',
      backgroundColor: '#f5f7fa',
      fontFamily: 'Arial, sans-serif'
    },
    header: {
      textAlign: 'center',
      marginBottom: '40px',
      padding: '20px 0' 
    },
    title: {
      fontSize: '28px',
      fontWeight: '700',
      color: '#1e40af',
      margin: '0 0 10px 0',
      position: 'relative',
      display: 'inline-block',
      paddingBottom: '15px'
    },
    titleAfter: {
      content: '""',
      position: 'absolute',
      bottom: 0,
      left: '50%',
      transform: 'translateX(-50%)',
      width: '60%',
      height: '4px',
      background: 'linear-gradient(90deg, #1e40af, #3b82f6)',
      borderRadius: '2px'
    },
    subtitle: {
      fontSize: '16px',
      color: '#64748b',
      margin: '10px 0 0 0',
      letterSpacing: '0.3px'
    },
    formContainer: {
      backgroundColor: '#ffffff',
      borderRadius: '8px',
      padding: '25px',
      boxShadow: '0 2px 6px rgba(0,0,0,0.1)'
    },
    formHeader: {
      marginBottom: '20px',
      borderBottom: '1px solid #e2e8f0',
      paddingBottom: '15px'
    },
    formTitle: {
      fontSize: '22px',
      fontWeight: '600',
      color: '#1e40af',
      margin: '0 0 8px 0'
    },
    formDescription: {
      fontSize: '14px',
      color: '#64748b',
      margin: '0'
    },
    formGroup: {
      marginBottom: '20px'
    },
    label: {
      display: 'block',
      marginBottom: '8px',
      fontWeight: '500',
      fontSize: '14px',
      color: '#334155'
    },
    input: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      boxSizing: 'border-box'
    },
    formGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(2, 1fr)',
      gap: '20px',
      marginBottom: '20px'
    },
    selectContainer: {
      position: 'relative'
    },
    select: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      backgroundColor: '#fff',
      boxSizing: 'border-box'
    },
    congeInfoTag: {
      display: 'block',
      fontSize: '12px',
      color: '#64748b',
      marginTop: '5px',
      fontStyle: 'italic'
    },
    textarea: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      minHeight: '100px',
      resize: 'vertical',
      boxSizing: 'border-box'
    },
    alert: {
      backgroundColor: '#e0f2fe',
      borderRadius: '8px',
      padding: '14px',
      marginBottom: '20px',
      display: 'flex',
      alignItems: 'center',
      gap: '10px'
    },
    alertText: {
      color: '#0369a1',
      fontSize: '14px',
      fontWeight: '500',
      margin: '0'
    },
    buttonContainer: {
      marginTop: '25px'
    },
    button: {
      width: '100%',
      padding: '12px',
      backgroundColor: '#2563eb',
      color: '#ffffff',
      borderRadius: '4px',
      fontWeight: '500',
      border: 'none',
      cursor: 'pointer',
      fontSize: '16px',
      transition: 'background-color 0.3s',
      marginTop: '10px'
    },
    fileInputContainer: {
      position: 'relative',
      display: 'flex',
      flexDirection: 'column',
      width: '100%'
    },
    fileInputLabel: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '12px',
      border: '1px dashed #3b82f6',
      borderRadius: '4px',
      backgroundColor: '#f1f5f9',
      color: '#475569',
      fontSize: '14px',
      cursor: 'pointer'
    },
    selectedFile: {
      display: 'flex',
      alignItems: 'center',
      gap: '10px',
      marginTop: '10px',
      padding: '10px',
      backgroundColor: '#f1f5f9',
      borderRadius: '6px',
      fontSize: '14px'
    },
    removeButton: {
      background: 'none',
      border: 'none',
      color: '#ef4444',
      cursor: 'pointer',
      fontSize: '16px'
    },
    toastMessage: {
      position: 'fixed',
      bottom: '30px',
      left: '50%',
      transform: 'translateX(-50%)',
      backgroundColor: '#34d399',
      color: 'white',
      borderRadius: '8px',
      padding: '15px 20px',
      boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
      zIndex: '1000'
    },
    errorMessage: {
      backgroundColor: '#fee2e2',
      color: '#b91c1c',
      padding: '12px',
      borderRadius: '8px',
      marginBottom: '20px',
      borderLeft: '4px solid #b91c1c'
    }
  };

  // Types de congés
  const congeTypes = [
    { type: 'congé payé', title: 'Congé Payé', remainingDays: userData.soldeConge, icon: '🏖️' },
    { type: 'rtt', title: 'RTT', remainingDays: 8, icon: '⏱️' },
    { type: 'congé maladie', title: 'Congé Maladie', remainingDays: 'Illimité', icon: '🏥' },
    { type: 'congé parental', title: 'Congé Parental', remainingDays: 'Selon droits', icon: '👶' },
    { type: 'congé familial', title: 'Événement Familial', remainingDays: 'Selon événement', icon: '👪' },
    { type: 'congé sans solde', title: 'Congé Sans Solde', remainingDays: 'Illimité', icon: '📅' }
  ];

  useEffect(() => {
    // Récupération des données comme dans CardDemandeAvance
    const userDataStr = localStorage.getItem('userData');
    
    if (userDataStr) {
      try {
        const userData = JSON.parse(userDataStr);
        setUserData({
          nom: userData.nom || '',
          prenom: userData.prenom || '',
          email: userData.email || '',
          soldeConge: userData.soldeConge || 0
        });
        
        // Récupérer le token comme dans CardDemandeAvance
        const authToken = localStorage.getItem('authToken') || 
                         localStorage.getItem('token') || 
                         localStorage.getItem('jwt');
        if (authToken) {
          setToken(authToken);
        }
      } catch (e) {
        console.error("Erreur de parsing userData:", e);
        setShowAlert(true);
        setAlertType('error');
        setAlertMessage("Erreur de chargement des données utilisateur");
      }
    } else {
      setShowAlert(true);
      setAlertType('error');
      setAlertMessage("Authentification requise. Veuillez vous connecter.");
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    if (e.target.files[0]) {
      setFormData(prev => ({ ...prev, medicalFile: e.target.files[0] }));
      setFileName(e.target.files[0].name);
    }
  };

  const removeFile = () => {
    setFormData(prev => ({ ...prev, medicalFile: null }));
    setFileName('');
    if (fileInputRef.current) fileInputRef.current.value = '';
  };

  const calculateDays = () => {
    if (!formData.startDate || !formData.endDate) return 0;
    const start = new Date(formData.startDate);
    const end = new Date(formData.endDate);
    return Math.ceil((end - start) / (1000 * 60 * 60 * 24)) + 1;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitError(null);

    // Validation
    if (formData.type === 'congé maladie' && !formData.medicalFile) {
      setSubmitError('Un justificatif médical est requis');
      return;
    }

    try {
      const formDataToSend = new FormData();
      formDataToSend.append('startDate', formData.startDate);
      formDataToSend.append('endDate', formData.endDate);
      formDataToSend.append('type', formData.type);
      formDataToSend.append('reason', formData.reason);
      formDataToSend.append('notes', formData.notes);
      if (formData.medicalFile) formDataToSend.append('medicalFile', formData.medicalFile);

      const response = await axios.post('/adddemandeconge', formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'Authorization': `Bearer ${token}`
        }
      });

      setConfirmationMessage(`Demande enregistrée (Réf: ${response.data.data._id})`);
      setShowConfirmation(true);
      
      // Réinitialisation
      setFormData({
        startDate: '',
        endDate: '',
        reason: '',
        type: 'congé payé',
        notes: '',
        medicalFile: null
      });
      setFileName('');
      setTimeout(() => setShowConfirmation(false), 5000);

    } catch (error) {
      console.error('Erreur:', error);
      setSubmitError(error.response?.data?.message || error.message || 'Erreur de soumission');
    }
  };

  const Alert = ({ type, message, onClose }) => (
    <div style={{
      ...styles.alert,
      ...(type === 'error' ? styles.errorMessage : { backgroundColor: '#dcfce7', color: '#16a34a' })
    }}>
      <div>{type === 'error' ? '⚠️' : '✅'} {message}</div>
      <button onClick={onClose} style={{ background: 'none', border: 'none', cursor: 'pointer' }}>×</button>
    </div>
  );

  const selectedCongeType = congeTypes.find(c => c.type === formData.type);
  const isMaladieType = formData.type === 'congé maladie';
  const needsJustification = ['congé maladie', 'congé familial'].includes(formData.type);

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>
          Demande de Congé
          <span style={styles.titleAfter}></span>
        </h1>
        <p style={styles.subtitle}>Bonjour {userData.prenom} {userData.nom}, remplissez votre demande</p>
      </div>

      <div style={styles.formContainer}>
        <div style={styles.formHeader}>
          <h2 style={styles.formTitle}>Formulaire de demande</h2>
          <p style={styles.formDescription}>Tous les champs marqués d'un * sont obligatoires</p>
        </div>

        {(submitError || showAlert) && (
          <Alert 
            type={submitError ? 'error' : alertType} 
            message={submitError || alertMessage}
            onClose={() => submitError ? setSubmitError(null) : setShowAlert(false)}
          />
        )}

        <form onSubmit={handleSubmit}>
          <div style={styles.formGroup}>
            <label style={styles.label}>Type de congé</label>
            <select
              name="type"
              value={formData.type}
              onChange={handleChange}
              style={styles.select}
              required
            >
              {congeTypes.map((type, index) => (
                <option key={index} value={type.type}>
                  {type.icon} {type.title} ({type.remainingDays} jours)
                </option>
              ))}
            </select>
          </div>

          {isMaladieType ? (
            <div style={styles.formGroup}>
              <label style={styles.label}>Justificatif médical *</label>
              <div style={styles.fileInputContainer}>
                <label style={styles.fileInputLabel}>
                  {fileName || 'Choisir un fichier'}
                  <input
                    type="file"
                    ref={fileInputRef}
                    style={{ display: 'none' }}
                    onChange={handleFileChange}
                    accept=".pdf,.jpg,.jpeg,.png"
                    required
                  />
                </label>
                {fileName && (
                  <div style={styles.selectedFile}>
                    <span>{fileName}</span>
                    <button type="button" style={styles.removeButton} onClick={removeFile}>
                      ✕
                    </button>
                  </div>
                )}
              </div>
            </div>
          ) : (
            <div style={styles.formGroup}>
              <label style={styles.label}>
                Motif {needsJustification && '*'}
              </label>
              <input
                type="text"
                name="reason"
                value={formData.reason}
                onChange={handleChange}
                style={styles.input}
                placeholder={needsJustification ? "Obligatoire" : "Facultatif"}
                required={needsJustification}
              />
            </div>
          )}

          <div style={styles.formGrid}>
            <div style={styles.formGroup}>
              <label style={styles.label}>Date de début *</label>
              <input
                type="date"
                name="startDate"
                value={formData.startDate}
                onChange={handleChange}
                style={styles.input}
                required
              />
            </div>
            <div style={styles.formGroup}>
              <label style={styles.label}>Date de fin *</label>
              <input
                type="date"
                name="endDate"
                value={formData.endDate}
                onChange={handleChange}
                style={styles.input}
                required
              />
            </div>
          </div>

          {formData.startDate && formData.endDate && (
            <div style={styles.alert}>
              <div style={styles.alertText}>
                Durée: <strong>{calculateDays()} jours</strong>
                {formData.type === 'congé payé' && (
                  <span> - Solde restant: {userData.soldeConge - calculateDays()} jours</span>
                )}
              </div>
            </div>
          )}

          <div style={styles.formGroup}>
            <label style={styles.label}>Notes complémentaires</label>
            <textarea
              name="notes"
              value={formData.notes}
              onChange={handleChange}
              style={styles.textarea}
              placeholder="Informations supplémentaires (facultatif)"
            />
          </div>

          <div style={styles.buttonContainer}>
            <button type="submit" style={styles.button}>
              Soumettre la demande
            </button>
          </div>
        </form>
      </div>

      {showConfirmation && (
        <div style={styles.toastMessage}>
          ✅ {confirmationMessage}
        </div>
      )}
    </div>
  );
};

export default CardDemandeConge;
