import { useState, useEffect } from "react";
import { Search, AlertCircle, CheckCircle2, XCircle, Eye, Filter, ChevronDown, Clock, Check, X } from "lucide-react";
import axios from "axios";

const encapsulatedStyles = `
  .gdc-container {
    background: white;
    border-radius: 8px;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
    overflow: hidden;
    position: relative;
    width: 100%;
  }
  
  .gdc-header-section {
    padding: 1.5rem;
    border-bottom: 1px solid #e2e8f0;
  }
  
  .gdc-title {
    font-size: 1.5rem;
    font-weight: 700;
    margin-bottom: 1.5rem;
    color: #1f2937;
  }
  
  .gdc-search-filter-container {
    display: flex;
    gap: 1rem;
    margin-bottom: 1rem;
  }
  
  .gdc-search-container {
    position: relative;
    flex-grow: 1;
  }
  
  .gdc-search-icon {
    position: absolute;
    left: 10px;
    top: 50%;
    transform: translateY(-50%);
    color: #9ca3af;
  }
  
  .gdc-search-input {
    width: 100%;
    padding: 0.5rem 0.75rem 0.5rem 2.5rem;
    border: 1px solid #d1d5db;
    border-radius: 0.375rem;
    transition: all 0.2s ease;
    font-size: 0.875rem;
  }
  
  .gdc-search-input:focus {
    outline: none;
    border-color: #14b8a6;
    box-shadow: 0 0 0 2px rgba(20, 184, 166, 0.2);
  }
  
  .gdc-filter-container {
    position: relative;
    min-width: 180px;
  }
  
  .gdc-filter-select {
    appearance: none;
    width: 100%;
    padding: 0.5rem 2.5rem 0.5rem 1rem;
    border: 1px solid #d1d5db;
    border-radius: 0.375rem;
    background-color: white;
    font-size: 0.875rem;
    cursor: pointer;
  }
  
  .gdc-filter-icon {
    position: absolute;
    right: 10px;
    top: 50%;
    transform: translateY(-50%);
    color: #9ca3af;
    pointer-events: none;
  }
  
  .gdc-table-container {
    width: 100%;
    overflow-x: auto;
  }
  
  .gdc-table {
    width: 100%;
    border-collapse: collapse;
  }
  
  .gdc-th {
    text-align: left;
    padding: 0.75rem 1rem;
    font-size: 0.75rem;
    font-weight: 600;
    text-transform: uppercase;
    color: #6b7280;
    background-color: #f5f5f5;
    border-bottom: 1px solid #e5e7eb;
  }
  
  .gdc-td {
    padding: 1rem;
    vertical-align: middle;
    color: #374151;
    border-bottom: 1px solid #f3f4f6;
  }
  
  .gdc-tr {
    transition: background-color 0.2s ease;
  }
  
  .gdc-tr:hover {
    background-color: #f9fafb;
  }
  
  .gdc-employee-name {
    font-size: 0.875rem;
    font-weight: 500;
    color: #111827;
  }
  
  .gdc-period {
    font-size: 0.875rem;
    font-weight: 600;
    color: #111827;
  }
  
  .gdc-date {
    font-size: 0.75rem;
    color: #6b7280;
    margin-top: 0.25rem;
  }
  
  .gdc-date-invalid {
    color: #dc2626;
  }
  
  .gdc-date-warning {
    display: inline-flex;
    align-items: center;
    gap: 0.25rem;
    font-size: 0.65rem;
    color: #dc2626;
    margin-top: 0.25rem;
  }
  
  .gdc-badge {
    display: inline-flex;
    align-items: center;
    padding: 0.25rem 0.5rem;
    border-radius: 9999px;
    font-size: 0.75rem;
    font-weight: 500;
  }
  
  .gdc-badge-annuel {
    background-color: #dbeafe;
    color: #1e40af;
  }
  
  .gdc-badge-maladie {
    background-color: #ede9fe;
    color: #5b21b6;
  }
  
  .gdc-badge-exceptionnel {
    background-color: #fef3c7;
    color: #92400e;
  }
  
  .gdc-badge-maternite {
    background-color: #dcfce7;
    color: #166534;
  }
  
  .gdc-status-badge {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    padding: 0.375rem 0.75rem;
    border-radius: 9999px;
    font-size: 0.75rem;
    font-weight: 500;
    width: fit-content;
  }
  
  .gdc-status-approved {
    background-color: #ecfdf5;
    color: #059669;
  }
  
  .gdc-status-pending {
    background-color: #fffbeb;
    color: #d97706;
  }
  
  .gdc-status-rejected {
    background-color: #fef2f2;
    color: #dc2626;
  }
  
  .gdc-view-button {
    display: inline-flex;
    align-items: center;
    gap: 0.25rem;
    padding: 0.375rem 0.75rem;
    border: 1px solid #d1d5db;
    border-radius: 0.375rem;
    font-size: 0.75rem;
    font-weight: 500;
    color: #4b5563;
    background-color: white;
    cursor: pointer;
    transition: all 0.2s ease;
  }
  
  .gdc-view-button:hover {
    background-color: #f9fafb;
    border-color: #9ca3af;
  }
  
  .gdc-action-button {
    padding: 0.375rem;
    border-radius: 0.375rem;
    transition: all 0.2s ease;
    margin-right: 0.5rem;
  }

  .gdc-action-button-approve {
    background-color: #d1fae5;
    color: #059669;
  }

  .gdc-action-button-approve:hover {
    background-color: #a7f3d0;
  }

  .gdc-action-button-reject {
    background-color: #fee2e2;
    color: #dc2626;
  }

  .gdc-action-button-reject:hover {
    background-color: #fecaca;
  }
  
  .gdc-action-button:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }
  
  .gdc-modal-overlay {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: rgba(0, 0, 0, 0.5);
    display: flex;
    justify-content: center;
    align-items: center;
    z-index: 50;
  }
  
  .gdc-modal {
    background-color: white;
    border-radius: 0.5rem;
    box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
    width: 90%;
    max-width: 32rem;
    max-height: 90vh;
    overflow-y: auto;
    z-index: 60;
  }
  
  .gdc-modal-header {
    padding: 1rem 1.5rem;
    border-bottom: 1px solid #e5e7eb;
    display: flex;
    justify-content: space-between;
    align-items: center;
  }
  
  .gdc-modal-title {
    font-size: 1.25rem;
    font-weight: 600;
    color: #111827;
  }
  
  .gdc-modal-close {
    background: none;
    border: none;
    color: #6b7280;
    cursor: pointer;
    width: 2rem;
    height: 2rem;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 0.375rem;
    transition: all 0.2s ease;
  }
  
  .gdc-modal-close:hover {
    background-color: #f3f4f6;
    color: #111827;
  }
  
  .gdc-modal-body {
    padding: 1.5rem;
  }
  
  .gdc-modal-section {
    margin-bottom: 1.5rem;
  }
  
  .gdc-modal-section:last-child {
    margin-bottom: 0;
  }
  
  .gdc-modal-section-title {
    font-size: 1rem;
    font-weight: 600;
    color: #111827;
    margin-bottom: 0.5rem;
  }
  
  .gdc-modal-section-content {
    font-size: 0.875rem;
    line-height: 1.5;
    color: #4b5563;
    background-color: #f9fafb;
    padding: 1rem;
    border-radius: 0.375rem;
    border: 1px solid #e5e7eb;
  }
  
  .gdc-modal-info-grid {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 1rem;
    margin-bottom: 1.5rem;
  }
  
  .gdc-modal-info-item {
    display: flex;
    flex-direction: column;
    gap: 0.25rem;
  }
  
  .gdc-modal-info-label {
    font-size: 0.75rem;
    font-weight: 500;
    color: #6b7280;
  }
  
  .gdc-modal-info-value {
    font-size: 0.875rem;
    font-weight: 500;
    color: #111827;
  }
  
  .gdc-modal-footer {
    padding: 1rem 1.5rem;
    border-top: 1px solid #e5e7eb;
    display: flex;
    justify-content: flex-end;
  }
  
  .gdc-modal-button {
    padding: 0.5rem 1rem;
    font-size: 0.875rem;
    font-weight: 500;
    border-radius: 0.375rem;
    cursor: pointer;
    transition: all 0.2s ease;
  }
  
  .gdc-modal-button-secondary {
    background-color: white;
    color: #4b5563;
    border: 1px solid #d1d5db;
  }
  
  .gdc-modal-button-secondary:hover {
    background-color: #f9fafb;
    border-color: #9ca3af;
  }
  
  .gdc-loading {
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 2rem;
    font-size: 1rem;
    color: #6b7280;
  }

  .gdc-error {
    padding: 1rem;
    background-color: #fee2e2;
    color: #b91c1c;
    border-radius: 0.375rem;
    margin: 1rem;
    text-align: center;
  }

  .gdc-footer {
    padding: 1rem 1.5rem;
    background-color: #f8fafc;
    border-top: 1px solid #e2e8f0;
    font-size: 0.875rem;
    color: #64748b;
    display: flex;
    align-items: center;
    justify-content: space-between;
    border-radius: 0 0 8px 8px;
  }

  .gdc-count {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    font-weight: 500;
    color: #334155;
  }

  .gdc-count-icon {
    color: #14b8a6;
  }

  .gdc-toast-container {
    position: fixed;
    bottom: 20px;
    right: 20px;
    z-index: 1000;
  }

  .gdc-toast {
    padding: 8px 16px;
    border-radius: 4px;
    color: white;
    font-weight: 500;
    font-size: 12px;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
    display: flex;
    align-items: center;
    gap: 6px;
    animation: slideIn 0.2s ease-out;
    margin-bottom: 8px;
  }

  .gdc-toast-success {
    background-color: #10b981;
  }

  .gdc-toast-error {
    background-color: #ef4444;
  }

  @keyframes slideIn {
    from {
      transform: translateX(100%);
      opacity: 0;
    }
    to {
      transform: translateX(0);
      opacity: 1;
    }
  }

  .gdc-pagination {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 1rem 1.5rem;
    background-color: #f8fafc;
    border-top: 1px solid #e2e8f0;
  }

  .gdc-pagination-controls {
    display: flex;
    gap: 0.5rem;
  }

  .gdc-page-button {
    padding: 0.5rem 1rem;
    border: 1px solid #d1d5db;
    border-radius: 0.375rem;
    background-color: white;
    color: #4b5563;
    cursor: pointer;
    transition: all 0.2s ease;
    font-size: 0.875rem;
  }

  .gdc-page-button:hover {
    background-color: #f3f4f6;
  }

  .gdc-page-button:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }

  .gdc-page-button.active {
    background-color: #14b8a6;
    color: white;
    border-color: #14b8a6;
  }

  .gdc-date-input {
    padding: 0.5rem 0.75rem;
    border: 1px solid #d1d5db;
    border-radius: 0.375rem;
    font-size: 0.875rem;
    width: 100%;
    margin-top: 0.25rem;
  }
  
  .gdc-date-input:focus {
    outline: none;
    border-color: #14b8a6;
    box-shadow: 0 0 0 2px rgba(20, 184, 166, 0.2);
  }

  .gdc-add-button {
    padding: 0.5rem 1rem;
    background-color: #14b8a6;
    color: white;
    border: none;
    border-radius: 0.375rem;
    font-size: 0.875rem;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.2s ease;
  }

  .gdc-add-button:hover {
    background-color: #0d9488;
  }

  .gdc-form-group {
    margin-bottom: 1rem;
  }

  .gdc-form-label {
    display: block;
    font-size: 0.875rem;
    font-weight: 500;
    color: #374151;
    margin-bottom: 0.25rem;
  }
`;

export default function GestionConges() {
  const [demandes, setDemandes] = useState([]);
  const [demandesFiltrees, setDemandesFiltrees] = useState([]);
  const [recherche, setRecherche] = useState("");
  const [filtreStatut, setFiltreStatut] = useState("Tous");
  const [demandeSelected, setDemandeSelected] = useState(null);
  const [modalVisible, setModalVisible] = useState(false);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [toasts, setToasts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10);
  const [newDemande, setNewDemande] = useState({
    nom: "",
    prenom: "",
    email: "",
    type: "Annuel",
    DateDebut: "",
    DateFin: "",
    motif: ""
  });
  const [showAddModal, setShowAddModal] = useState(false);

const getTodayDateString = () => {
  const today = new Date();
  const year = today.getFullYear();
  const month = String(today.getMonth() + 1).padStart(2, '0');
  const day = String(today.getDate()).padStart(2, '0');
  return `${year}-${month}-${day}`;
};
  useEffect(() => {
    const fetchDemandes = async () => {
      try {
        const response = await axios.get('/api/alldemandeConge');
        setDemandes(response.data);
        setDemandesFiltrees(response.data);
      } catch (err) {
        setError('Erreur lors de la récupération des demandes');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchDemandes();
  }, []);

  useEffect(() => {
    let resultats = demandes;
    
    if (recherche.trim()) {
      const termeLower = recherche.toLowerCase();
      resultats = resultats.filter(demande => 
        (demande.nom && demande.nom.toLowerCase().includes(termeLower)) ||
        (demande.prenom && demande.prenom.toLowerCase().includes(termeLower)) ||
        (demande.type && demande.type.toLowerCase().includes(termeLower))
      );
    }
    
    if (filtreStatut !== "Tous") {
      resultats = resultats.filter(demande => demande.statut === filtreStatut);
    }
    
    setDemandesFiltrees(resultats);
    setCurrentPage(1);
  }, [recherche, filtreStatut, demandes]);

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = demandesFiltrees.slice(indexOfFirstItem, indexOfLastItem);
  const totalPages = Math.ceil(demandesFiltrees.length / itemsPerPage);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    const date = new Date(dateString);
    return date.toLocaleDateString('fr-FR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    });
  };

  const validateDates = (dateDebut, dateFin) => {
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    
    const debut = new Date(dateDebut);
    debut.setHours(0, 0, 0, 0);
    
    const fin = new Date(dateFin);
    fin.setHours(0, 0, 0, 0);
    
    const errors = [];
    
    if (isNaN(debut.getTime())) {
      errors.push("Date de début invalide");
    } else if (debut < today) {
      errors.push("La date de début doit être supérieure ou égale à aujourd'hui");
    }
    
    if (isNaN(fin.getTime())) {
      errors.push("Date de fin invalide");
    } else if (fin < debut) {
      errors.push("La date de fin doit être postérieure à la date de début");
    }
    
    return {
      isValid: errors.length === 0,
      errors: errors.join(", ")
    };
  };

  const calculerPeriode = (dateDebut, dateFin) => {
    const validation = validateDates(dateDebut, dateFin);
    if (!validation.isValid) return validation.errors;

    const debut = new Date(dateDebut);
    const fin = new Date(dateFin);
    const diffTime = Math.abs(fin - debut);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24)) + 1;
    
    return `${diffDays} jour${diffDays > 1 ? 's' : ''}`;
  };

  const calculerJoursConges = (dateDebut, dateFin) => {
    if (!dateDebut || !dateFin) return 0;
    
    const debut = new Date(dateDebut);
    const fin = new Date(dateFin);
    const diffTime = Math.abs(fin - debut);
    return Math.ceil(diffTime / (1000 * 60 * 60 * 24)) + 1;
  };

  const isDateInPast = (dateString) => {
    if (!dateString) return false;
    const date = new Date(dateString);
    date.setHours(0, 0, 0, 0);
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    return date < today;
  };

  const showToast = (message, type = 'success') => {
    const id = Date.now();
    const newToast = { id, message, type };
    
    setToasts(prev => [...prev, newToast]);
    
    setTimeout(() => {
      removeToast(id);
    }, 3000);
  };

  const removeToast = (id) => {
    setToasts(prev => prev.filter(toast => toast.id !== id));
  };

  const ouvrirModal = (demande) => {
    setDemandeSelected(demande);
    setModalVisible(true);
  };

  const fermerModal = () => {
    setModalVisible(false);
    setDemandeSelected(null);
  };

  const updateSoldeConge = async (idPersonnel, joursConges) => {
    try {
      const response = await axios.put(`/api/updatepersonnel/${idPersonnel}`, { 
        decrementSolde: joursConges
      });
      
      return true;
    } catch (err) {
      console.error('Error in updateSoldeConge:', err);
      throw err;
    }
  };

  const handleStatusUpdate = async (id, newStatus) => {
    try {
      const demandeToUpdate = demandes.find(demande => demande._id === id);
      if (!demandeToUpdate) throw new Error('Demande non trouvée');

      if (newStatus === "Approuvée") {
        const validation = validateDates(demandeToUpdate.DateDebut, demandeToUpdate.DateFin);
        if (!validation.isValid) {
          throw new Error(validation.errors);
        }

        if (demandeToUpdate.idpersonnel) {
          const joursConges = calculerJoursConges(demandeToUpdate.DateDebut, demandeToUpdate.DateFin);
          await updateSoldeConge(demandeToUpdate.idpersonnel, joursConges);
        }
      }

      const response = await axios.put(`/api/updatedemandeconge/${id}`, { 
        statut: newStatus 
      });
      
      const demandesUpdated = demandes.map(demande => {
        if (demande._id === id) {
          return { ...demande, statut: newStatus };
        }
        return demande;
      });
      
      setDemandes(demandesUpdated);
      showToast(`Demande ${newStatus === 'Approuvée' ? 'approuvée' : 'refusée'}`, 'success');
    } catch (err) {
      showToast(err.message, 'error');
      console.error("Erreur complète:", err);
    }
  };

  const handleApprove = (id) => {
    handleStatusUpdate(id, 'Approuvée');
  };

  const handleReject = (id) => {
    handleStatusUpdate(id, 'Rejetée');
  };

 const handleAddDemande = async () => {
  try {
    // Validation des champs obligatoires
    if (!newDemande.nom || !newDemande.prenom) {
      throw new Error("Le nom et le prénom sont obligatoires");
    }

    if (!newDemande.DateDebut) {
      throw new Error("La date de début est obligatoire");
    }

    // Validation des dates
    const today = new Date(getTodayDateString());
    today.setHours(0, 0, 0, 0);

    const dateDebut = new Date(newDemande.DateDebut);
    dateDebut.setHours(0, 0, 0, 0);

    if (dateDebut < today) {
      throw new Error("La date de début ne peut pas être dans le passé");
    }

    if (!newDemande.DateFin) {
      throw new Error("La date de fin est obligatoire");
    }

    const dateFin = new Date(newDemande.DateFin);
    dateFin.setHours(0, 0, 0, 0);

    if (dateFin < dateDebut) {
      throw new Error("La date de fin doit être postérieure à la date de début");
    }

    // Validation du motif si nécessaire
    if (!newDemande.motif || newDemande.motif.trim() === "") {
      throw new Error("Veuillez saisir un motif pour votre demande");
    }

    // Calcul du nombre de jours de congé
    const joursConges = calculerJoursConges(newDemande.DateDebut, newDemande.DateFin);
    if (joursConges <= 0) {
      throw new Error("La durée du congé doit être d'au moins 1 jour");
    }

    // Envoi de la demande au serveur
    const response = await axios.post('/api/createdemandeconge', {
      ...newDemande,
      statut: "En attente",
      joursConges: joursConges // Ajout du nombre de jours calculé
    });

    // Mise à jour de l'état local
    setDemandes([...demandes, response.data]);
    setShowAddModal(false);
    
    // Réinitialisation du formulaire
    setNewDemande({
      nom: "",
      prenom: "",
      email: "",
      type: "Annuel",
      DateDebut: "",
      DateFin: "",
      motif: ""
    });
    
    // Notification de succès
    showToast('Demande de congé créée avec succès', 'success');

  } catch (err) {
    // Gestion des erreurs
    console.error("Erreur lors de la création de la demande:", err);
    
    // Affichage d'un message d'erreur approprié
    const errorMessage = err.response?.data?.message || err.message || "Une erreur est survenue";
    showToast(errorMessage, 'error');
  }
};

  const renderStatus = (status) => {
    switch (status) {
      case "Approuvée":
        return (
          <div className="gdc-status-badge gdc-status-approved">
            <CheckCircle2 size={16} />
            <span>Approuvé</span>
          </div>
        );
      case "En attente":
        return (
          <div className="gdc-status-badge gdc-status-pending">
            <Clock size={16} />
            <span>En attente</span>
          </div>
        );
      case "Rejetée":
        return (
          <div className="gdc-status-badge gdc-status-rejected">
            <XCircle size={16} />
            <span>Refusé</span>
          </div>
        );
      default:
        return null;
    }
  };

  const renderTypeConge = (type) => {
    switch (type) {
      case "Annuel":
        return (
          <span className="gdc-badge gdc-badge-annuel">
            {type}
          </span>
        );
      case "Maladie":
        return (
          <span className="gdc-badge gdc-badge-maladie">
            {type}
          </span>
        );
      case "Exceptionnel":
        return (
          <span className="gdc-badge gdc-badge-exceptionnel">
            {type}
          </span>
        );
      case "Maternité":
        return (
          <span className="gdc-badge gdc-badge-maternite">
            {type}
          </span>
        );
      default:
        return (
          <span className="gdc-badge">
            {type}
          </span>
        );
    }
  };

  if (loading) {
    return (
      <>
        <style>{encapsulatedStyles}</style>
        <div className="gdc-loading">Chargement des demandes de congé...</div>
      </>
    );
  }

  return (
    <>
      <style>{encapsulatedStyles}</style>
      
      <div className="gdc-container">
        <div className="gdc-header-section">
          <h1 className="gdc-title">
            Gestion des Demandes de Congé
          </h1>
          
          <div className="gdc-search-filter-container">
            <div className="gdc-search-container">
              <div className="gdc-search-icon">
                <Search size={18} />
              </div>
              <input
                type="text"
                className="gdc-search-input"
                placeholder="Rechercher par nom, prénom ou type..."
                value={recherche}
                onChange={(e) => setRecherche(e.target.value)}
              />
            </div>
            
            <div className="gdc-filter-container">
              <select
                className="gdc-filter-select"
                value={filtreStatut}
                onChange={(e) => setFiltreStatut(e.target.value)}
              >
                <option value="Tous">Tous les statuts</option>
                <option value="En attente">En attente</option>
                <option value="Approuvée">Approuvée</option>
                <option value="Rejetée">Rejetée</option>
              </select>
              <div className="gdc-filter-icon">
                <Filter size={18} />
              </div>
            </div>

            <button 
              className="gdc-add-button"
              onClick={() => setShowAddModal(true)}
            >
              Ajouter une demande
            </button>
          </div>
        </div>

        {error && (
          <div className="gdc-error">
            {error}
          </div>
        )}

        <div className="gdc-table-container">
          <table className="gdc-table">
            <thead>
              <tr>
                <th className="gdc-th">Nom</th>
                <th className="gdc-th">Prénom</th>
                <th className="gdc-th">Date début</th>
                <th className="gdc-th">Date fin</th>
                <th className="gdc-th">Période</th>
                <th className="gdc-th">Statut</th>
                <th className="gdc-th">Motif</th>
                <th className="gdc-th">Actions</th>
              </tr>
            </thead>
            <tbody>
              {currentItems.length === 0 ? (
                <tr>
                  <td colSpan="8" className="gdc-td" style={{ textAlign: 'center' }}>
                    Aucune demande de congé trouvée
                  </td>
                </tr>
              ) : (
                currentItems.map((demande) => {
                  const dateDebutValide = !isDateInPast(demande.DateDebut);
                  const validation = validateDates(demande.DateDebut, demande.DateFin);
                  
                  return (
                    <tr key={demande._id} className="gdc-tr">
                      <td className="gdc-td">
                        <div className="gdc-employee-name">
                          {demande.nom}
                        </div>
                      </td>
                      <td className="gdc-td">
                        <div className="gdc-employee-name">
                          {demande.prenom}
                        </div>
                      </td>
                      <td className="gdc-td">
                        <div className={dateDebutValide ? "gdc-date" : "gdc-date gdc-date-invalid"}>
                          {formatDate(demande.DateDebut)}
                          {!dateDebutValide && (
                            <div className="gdc-date-warning">
                              <AlertCircle size={12} />
                              <span>Date passée</span>
                            </div>
                          )}
                        </div>
                      </td>
                      <td className="gdc-td">
                        <div className="gdc-date">
                          {formatDate(demande.DateFin)}
                        </div>
                      </td>
                      <td className="gdc-td">
                        <div className="gdc-period">
                          {calculerPeriode(demande.DateDebut, demande.DateFin)}
                        </div>
                      </td>
                      <td className="gdc-td">
                        {renderStatus(demande.statut)}
                      </td>
                      <td className="gdc-td">
                        <button
                          className="gdc-view-button"
                          onClick={() => ouvrirModal(demande)}
                        >
                          <Eye size={14} />
                          <span>Voir motif</span>
                        </button>
                      </td>
                      <td className="gdc-td">
                        <div className="gdc-actions">
                          {demande.statut === "En attente" && (
                            <>
                              <button 
                                className="gdc-action-button gdc-action-button-approve" 
                                onClick={() => handleApprove(demande._id)}
                                title={validation.isValid ? "Approuver la demande" : validation.errors}
                                disabled={!validation.isValid}
                              >
                                <Check size={16} />
                              </button>
                              <button 
                                className="gdc-action-button gdc-action-button-reject" 
                                onClick={() => handleReject(demande._id)}
                                title="Refuser la demande"
                              >
                                <X size={16} />
                              </button>
                            </>
                          )}
                        </div>
                      </td>
                    </tr>
                  );
                })
              )}
            </tbody>
          </table>
        </div>
        
        <div className="gdc-pagination">
          <div className="gdc-count">
            <CheckCircle2 className="gdc-count-icon" size={16} />
            <span>Affichage de {indexOfFirstItem + 1}-{Math.min(indexOfLastItem, demandesFiltrees.length)} sur {demandesFiltrees.length} demandes</span>
          </div>
          
          <div className="gdc-pagination-controls">
            <button 
              onClick={() => paginate(currentPage - 1)} 
              disabled={currentPage === 1}
              className="gdc-page-button"
            >
              Précédent
            </button>
            
            {Array.from({ length: totalPages }, (_, i) => i + 1).map(number => (
              <button
                key={number}
                onClick={() => paginate(number)}
                className={`gdc-page-button ${currentPage === number ? 'active' : ''}`}
              >
                {number}
              </button>
            ))}
            
            <button 
              onClick={() => paginate(currentPage + 1)} 
              disabled={currentPage === totalPages || totalPages === 0}
              className="gdc-page-button"
            >
              Suivant
            </button>
          </div>
        </div>
      </div>

      {/* Modal pour voir les détails d'une demande */}
      {modalVisible && demandeSelected && (
        <div className="gdc-modal-overlay" onClick={fermerModal}>
          <div className="gdc-modal" onClick={(e) => e.stopPropagation()}>
            <div className="gdc-modal-header">
              <h3 className="gdc-modal-title">Détails de la demande</h3>
              <button className="gdc-modal-close" onClick={fermerModal}>
                <X size={20} />
              </button>
            </div>
            <div className="gdc-modal-body">
              <div className="gdc-modal-info-grid">
                <div className="gdc-modal-info-item">
                  <span className="gdc-modal-info-label">Nom</span>
                  <span className="gdc-modal-info-value">
                    {demandeSelected.nom}
                  </span>
                </div>
                <div className="gdc-modal-info-item">
                  <span className="gdc-modal-info-label">Prénom</span>
                  <span className="gdc-modal-info-value">
                    {demandeSelected.prenom}
                  </span>
                </div>
                <div className="gdc-modal-info-item">
                  <span className="gdc-modal-info-label">Email</span>
                  <span className="gdc-modal-info-value">
                    {demandeSelected.email || "N/A"}
                  </span>
                </div>
                <div className="gdc-modal-info-item">
                  <span className="gdc-modal-info-label">Type de congé</span>
                  <span className="gdc-modal-info-value">
                    {renderTypeConge(demandeSelected.type)}
                  </span>
                </div>
                <div className="gdc-modal-info-item">
                  <span className="gdc-modal-info-label">Date début</span>
                  <span className="gdc-modal-info-value">
                    {formatDate(demandeSelected.DateDebut)}
                    {isDateInPast(demandeSelected.DateDebut) && (
                      <span style={{ color: 'red', marginLeft: '5px' }}>(Date passée)</span>
                    )}
                  </span>
                </div>
                <div className="gdc-modal-info-item">
                  <span className="gdc-modal-info-label">Date fin</span>
                  <span className="gdc-modal-info-value">{formatDate(demandeSelected.DateFin)}</span>
                </div>
                <div className="gdc-modal-info-item">
                  <span className="gdc-modal-info-label">Statut</span>
                  <span className="gdc-modal-info-value">
                    {renderStatus(demandeSelected.statut)}
                  </span>
                </div>
                <div className="gdc-modal-info-item">
                  <span className="gdc-modal-info-label">Durée</span>
                  <span className="gdc-modal-info-value">
                    {calculerPeriode(demandeSelected.DateDebut, demandeSelected.DateFin)}
                  </span>
                </div>
              </div>
              
              <div className="gdc-modal-section">
                <div className="gdc-modal-section-title">Motif</div>
                <div className="gdc-modal-section-content">
                  {demandeSelected.motif || "Aucun motif spécifié"}
                </div>
              </div>
            </div>
            <div className="gdc-modal-footer">
              <button 
                className="gdc-modal-button gdc-modal-button-secondary" 
                onClick={fermerModal}
              >
                Fermer
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Modal pour ajouter une demande */}
      {showAddModal && (
        <div className="gdc-modal-overlay" onClick={() => setShowAddModal(false)}>
          <div className="gdc-modal" onClick={(e) => e.stopPropagation()}>
            <div className="gdc-modal-header">
              <h3 className="gdc-modal-title">Nouvelle demande de congé</h3>
              <button className="gdc-modal-close" onClick={() => setShowAddModal(false)}>
                <X size={20} />
              </button>
            </div>
            <div className="gdc-modal-body">
              <div className="gdc-modal-section">
                <div className="gdc-modal-section-title">Informations personnelles</div>
                <div className="gdc-modal-section-content">
                  <div className="gdc-form-group">
                    <label className="gdc-form-label">Nom</label>
                    <input 
                      type="text" 
                      className="gdc-date-input" 
                      value={newDemande.nom}
                      onChange={(e) => setNewDemande({...newDemande, nom: e.target.value})}
                    />
                  </div>
                  <div className="gdc-form-group">
                    <label className="gdc-form-label">Prénom</label>
                    <input 
                      type="text" 
                      className="gdc-date-input" 
                      value={newDemande.prenom}
                      onChange={(e) => setNewDemande({...newDemande, prenom: e.target.value})}
                    />
                  </div>
                  <div className="gdc-form-group">
                    <label className="gdc-form-label">Email</label>
                    <input 
                      type="email" 
                      className="gdc-date-input" 
                      value={newDemande.email}
                      onChange={(e) => setNewDemande({...newDemande, email: e.target.value})}
                    />
                  </div>
                </div>
              </div>
              
              <div className="gdc-modal-section">
                <div className="gdc-modal-section-title">Détails du congé</div>
                <div className="gdc-modal-section-content">
                  <div className="gdc-form-group">
                    <label className="gdc-form-label">Type de congé</label>
                    <select 
                      className="gdc-date-input" 
                      value={newDemande.type}
                      onChange={(e) => setNewDemande({...newDemande, type: e.target.value})}
                    >
                      <option value="Annuel">Annuel</option>
                      <option value="Maladie">Maladie</option>
                      <option value="Exceptionnel">Exceptionnel</option>
                      <option value="Maternité">Maternité</option>
                    </select>
                  </div>
                  
                <div className="gdc-form-group">
  <label className="gdc-form-label">Date de début *</label>
  <input 
    type="date"
    className="gdc-date-input"
    min={getTodayDateString()} // Bloque les dates antérieures à aujourd'hui
    value={newDemande.DateDebut}
    onChange={(e) => {
      const selectedDate = e.target.value;
      const today = new Date(getTodayDateString());
      const selected = new Date(selectedDate);
      
      if (selected >= today) {
        setNewDemande({...newDemande, DateDebut: selectedDate, DateFin: ''}); // Réinitialise la date de fin si on change la date de début
      } else {
        showToast("La date de début doit être aujourd'hui ou ultérieure", "error");
        setNewDemande({...newDemande, DateDebut: ''}); // Réinitialise si date invalide
      }
    }}
    required
  />
</div>
                  
                  <div className="gdc-form-group">
                    <label className="gdc-form-label">Date de fin</label>
                    <input 
                      type="date" 
                      className="gdc-date-input" 
                      min={newDemande.DateDebut || getTodayDateString()}
                      value={newDemande.DateFin}
                      onChange={(e) => setNewDemande({...newDemande, DateFin: e.target.value})}
                    />
                  </div>
                  
                  <div className="gdc-form-group">
                    <label className="gdc-form-label">Motif</label>
                    <textarea 
                      className="gdc-date-input" 
                      style={{ minHeight: '80px' }}
                      value={newDemande.motif}
                      onChange={(e) => setNewDemande({...newDemande, motif: e.target.value})}
                    />
                  </div>
                </div>
              </div>
            </div>
            <div className="gdc-modal-footer">
              <button 
                className="gdc-modal-button gdc-modal-button-secondary" 
                onClick={() => setShowAddModal(false)}
              >
                Annuler
              </button>
              <button 
                className="gdc-modal-button" 
                style={{ backgroundColor: '#14b8a6', color: 'white' }}
                onClick={handleAddDemande}
              >
                Enregistrer
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Toast notifications */}
      <div className="gdc-toast-container">
        {toasts.map(toast => (
          <div 
            key={toast.id} 
            className={`gdc-toast gdc-toast-${toast.type}`}
            onClick={() => removeToast(toast.id)}
          >
            {toast.type === 'success' ? <Check size={14} /> : <X size={14} />}
            <span>{toast.message}</span>
          </div>
        ))}
      </div>
    </>
  );
}
