import { useState, useEffect } from "react";
import { Search, AlertCircle, Check, X, Eye } from "lucide-react";

// CSS encapsulé avec préfixe "gav-" (Gestion Avances)
const encapsulatedStyles = `
  .gav-container {
    background: white;
    border-radius: 8px;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
    overflow: hidden;
    position: relative;
    width: 100%;
  }
  
  .gav-header-section {
    padding: 1.5rem;
    border-bottom: 1px solid #e2e8f0;
  }
  
  .gav-title {
    font-size: 1.5rem;
    font-weight: 700;
    margin-bottom: 1.5rem;
    color: #1f2937;
  }
  
  .gav-search-container {
    position: relative;
    margin-bottom: 1rem;
  }
  
  .gav-search-icon {
    position: absolute;
    left: 10px;
    top: 50%;
    transform: translateY(-50%);
    color: #9ca3af;
  }
  
  .gav-search-input {
    width: 100%;
    padding: 0.5rem 0.75rem 0.5rem 2.5rem;
    border: 1px solid #d1d5db;
    border-radius: 0.375rem;
    transition: all 0.2s ease;
    font-size: 0.875rem;
  }
  
  .gav-search-input:focus {
    outline: none;
    border-color: #14b8a6;
    box-shadow: 0 0 0 2px rgba(20, 184, 166, 0.2);
  }
  
  .gav-table-container {
    width: 100%;
    overflow-x: auto;
  }
  
  .gav-table {
    width: 100%;
    border-collapse: collapse;
  }
  
  .gav-th {
    text-align: left;
    padding: 0.75rem 1rem;
    font-size: 0.75rem;
    font-weight: 600;
    text-transform: uppercase;
    color: #6b7280;
    background-color: #f9fafb;
    border-bottom: 1px solid #e5e7eb;
  }
  
  .gav-td {
    padding: 1rem;
    vertical-align: middle;
    color: #374151;
    border-bottom: 1px solid #f3f4f6;
  }
  
  .gav-tr {
    transition: background-color 0.2s ease;
  }
  
  .gav-tr:hover {
    background-color: #f9fafb;
  }
  
  .gav-employee-name {
    font-size: 0.875rem;
    font-weight: 500;
    color: #111827;
  }
  
  .gav-employee-dept {
    font-size: 0.75rem;
    color: #6b7280;
    margin-top: 0.25rem;
  }
  
  .gav-amount {
    font-size: 0.875rem;
    font-weight: 600;
    color: #111827;
  }
  
  .gav-date {
    font-size: 0.75rem;
    color: #6b7280;
    margin-top: 0.25rem;
  }
  
  .gav-badge {
    display: inline-flex;
    align-items: center;
    padding: 0.25rem 0.5rem;
    border-radius: 9999px;
    font-size: 0.75rem;
    font-weight: 500;
  }
  
  .gav-badge-standard {
    background-color: #dbeafe;
    color: #1e40af;
  }
  
  .gav-badge-urgent {
    background-color: #ede9fe;
    color: #5b21b6;
  }
  
  .gav-status-badge {
    display: flex;
    align-items: center;
    gap: 0.25rem;
    padding: 0.25rem 0.5rem;
    border-radius: 9999px;
    font-size: 0.75rem;
    font-weight: 500;
    width: fit-content;
  }
  
  .gav-status-approved {
    background-color: #dcfce7;
    color: #166534;
  }
  
  .gav-status-pending {
    background-color: #fef3c7;
    color: #92400e;
  }
  
  .gav-status-rejected {
    background-color: #fee2e2;
    color: #b91c1c;
  }
  
  .gav-view-button {
    display: inline-flex;
    align-items: center;
    gap: 0.25rem;
    padding: 0.375rem 0.75rem;
    border: 1px solid #d1d5db;
    border-radius: 0.375rem;
    font-size: 0.75rem;
    font-weight: 500;
    color: #4b5563;
    background-color: white;
    cursor: pointer;
    transition: all 0.2s ease;
  }
  
  .gav-view-button:hover {
    background-color: #f9fafb;
    border-color: #9ca3af;
  }
  
  .gav-view-button:focus {
    outline: none;
    border-color: #14b8a6;
    ring: 2px solid #14b8a6;
  }
  
  .gav-motif-popup {
    position: absolute;
    z-index: 10;
    margin-top: 0.5rem;
    width: 18rem;
    background-color: white;
    border: 1px solid #e5e7eb;
    border-radius: 0.375rem;
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
    padding: 1rem;
    font-size: 0.875rem;
    color: #374151;
  }
  
  .gav-motif-title {
    font-size: 0.875rem;
    font-weight: 600;
    margin-bottom: 0.5rem;
    color: #111827;
  }
  
  .gav-motif-text {
    font-size: 0.875rem;
    line-height: 1.25rem;
    color: #4b5563;
  }
  
  .gav-response-title {
    font-size: 0.875rem;
    font-weight: 600;
    margin-top: 0.75rem;
    margin-bottom: 0.25rem;
    color: #111827;
  }
  
  .gav-response-text {
    font-style: italic;
    font-size: 0.875rem;
    line-height: 1.25rem;
    color: #4b5563;
  }
  
  .gav-footer {
    padding: 1rem;
    background-color: #f9fafb;
    border-top: 1px solid #e5e7eb;
    font-size: 0.875rem;
    color: #6b7280;
  }
`;

export default function GestionAvances() {
  // Données fictives des demandes d'avance uniquement
  const [demandes, setDemandes] = useState([
    {
      id: 1,
      employeId: 2,
      nom: "Lambert",
      prenom: "Marie",
      departement: "RH",
      typeDemande: "Standard",
      statut: "En attente",
      dateDemande: "05/04/2025",
      montant: "1500",
      motif: "J'ai besoin d'une avance pour l'achat d'un ordinateur nécessaire à mon travail à distance.",
      reponse: "",
    },
    {
      id: 2,
      employeId: 5,
      nom: "Dubois",
      prenom: "Pierre",
      departement: "Ventes",
      typeDemande: "Urgente",
      statut: "Complété",
      dateDemande: "10/03/2025",
      montant: "800",
      motif: "Réparation urgente de ma voiture pour pouvoir continuer à me rendre au travail.",
      reponse: "Demande approuvée le 11/03/2025",
    },
    {
      id: 3,
      employeId: 8,
      nom: "Leroy",
      prenom: "Emilie",
      departement: "Technique",
      typeDemande: "Standard",
      statut: "Refusé",
      dateDemande: "22/03/2025",
      montant: "2000",
      motif: "Avance sur salaire pour frais médicaux imprévus.",
      reponse: "Demande refusée - montant supérieur à la limite autorisée",
    },
    {
      id: 4,
      employeId: 12,
      nom: "Moreau",
      prenom: "Thomas",
      departement: "Finance",
      typeDemande: "Standard",
      statut: "En attente",
      dateDemande: "12/04/2025",
      montant: "1200",
      motif: "Avance pour payer l'acompte de mon nouveau logement suite à ma mutation dans une autre ville.",
      reponse: "",
    },
    {
      id: 5,
      employeId: 7,
      nom: "Garcia",
      prenom: "Laura",
      departement: "Marketing",
      typeDemande: "Urgente",
      statut: "En attente",
      dateDemande: "15/04/2025",
      montant: "650",
      motif: "Achat de matériel pour le prochain événement marketing prévu la semaine prochaine.",
      reponse: "",
    },
  ]);

  const [demandesFiltrees, setDemandesFiltrees] = useState(demandes);
  const [recherche, setRecherche] = useState("");
  const [motifVisible, setMotifVisible] = useState(null);

  // Filtrage des demandes basé sur la recherche
  const filtrerDemandes = (terme) => {
    setRecherche(terme);
    if (!terme.trim()) {
      setDemandesFiltrees(demandes);
      return;
    }
    
    const termeLower = terme.toLowerCase();
    const resultats = demandes.filter(
      demande => 
        demande.nom.toLowerCase().includes(termeLower) ||
        demande.prenom.toLowerCase().includes(termeLower) ||
        demande.montant.includes(terme)
    );
    
    setDemandesFiltrees(resultats);
  };

  // Afficher ou cacher le motif
  const toggleMotif = (id) => {
    setMotifVisible(motifVisible === id ? null : id);
  };

  // Fermer le popup du motif si on clique ailleurs
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (motifVisible && !event.target.closest('.gav-view-button') && !event.target.closest('.gav-motif-popup')) {
        setMotifVisible(null);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [motifVisible]);

  // Rendu du statut avec l'icône appropriée
  const renderStatus = (statut) => {
    switch (statut) {
      case "Complété":
        return (
          <div className="gav-status-badge gav-status-approved">
            <Check size={14} />
            <span>Approuvé</span>
          </div>
        );
      case "En attente":
        return (
          <div className="gav-status-badge gav-status-pending">
            <AlertCircle size={14} />
            <span>En attente</span>
          </div>
        );
      case "Refusé":
        return (
          <div className="gav-status-badge gav-status-rejected">
            <X size={14} />
            <span>Refusé</span>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <>
      {/* Injection du CSS encapsulé */}
      <style>{encapsulatedStyles}</style>
      
      <div className="gav-container">
        {/* En-tête et recherche */}
        <div className="gav-header-section">
          <h1 className="gav-title">
            Gestion des Demandes d'Avances
          </h1>
          
          {/* Barre de recherche */}
          <div className="gav-search-container">
            <div className="gav-search-icon">
              <Search size={18} />
            </div>
            <input
              type="text"
              className="gav-search-input"
              placeholder="Rechercher par nom ou montant..."
              value={recherche}
              onChange={(e) => filtrerDemandes(e.target.value)}
            />
          </div>
        </div>

        {/* Tableau des demandes */}
        <div className="gav-table-container">
          <table className="gav-table">
            <thead>
              <tr>
                <th className="gav-th">Employé</th>
                <th className="gav-th">Montant</th>
                <th className="gav-th">Type</th>
                <th className="gav-th">Statut</th>
                <th className="gav-th">Motif</th>
              </tr>
            </thead>
            <tbody>
              {demandesFiltrees.map((demande) => (
                <tr key={demande.id} className="gav-tr">
                  <td className="gav-td">
                    <div className="gav-employee-name">
                      {demande.prenom} {demande.nom}
                    </div>
                    <div className="gav-employee-dept">{demande.departement}</div>
                  </td>
                  <td className="gav-td">
                    <div className="gav-amount">
                      {demande.montant} €
                    </div>
                    <div className="gav-date">{demande.dateDemande}</div>
                  </td>
                  <td className="gav-td">
                    <span className={`gav-badge ${demande.typeDemande === "Urgente" ? "gav-badge-urgent" : "gav-badge-standard"}`}>
                      {demande.typeDemande}
                    </span>
                  </td>
                  <td className="gav-td">
                    {renderStatus(demande.statut)}
                  </td>
                  <td className="gav-td">
                    <div style={{ position: "relative" }}>
                      <button
                        className="gav-view-button"
                        onClick={() => toggleMotif(demande.id)}
                      >
                        <Eye size={14} />
                        <span>Voir motif</span>
                      </button>
                      
                      {motifVisible === demande.id && (
                        <div className="gav-motif-popup">
                          <h4 className="gav-motif-title">Motif de la demande:</h4>
                          <p className="gav-motif-text">{demande.motif}</p>
                          {demande.reponse && (
                            <>
                              <h4 className="gav-response-title">Réponse:</h4>
                              <p className="gav-response-text">{demande.reponse}</p>
                            </>
                          )}
                        </div>
                      )}
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        
        {/* Information sur les résultats */}
        <div className="gav-footer">
          Affichage de {demandesFiltrees.length} sur {demandes.length} demandes d'avance
        </div>
      </div>
    </>
  );
}
