import React, { useState } from "react";

export default function ProfileForm() {
  const [formData, useState] = {
    username: "marine.durand",
    email: "marine@example.com",
    firstName: "Marine",
    lastName: "Durand",
    address: "15 Avenue des Roses, Apt 203",
    city: "Paris",
    country: "France",
    postalCode: "75008"
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("Profil mis √† jour avec succ√®s!");
  };

  return (
    <>
      <div className="form-container">
        <div className="form-header">
          <div className="header-content">
            <h3 className="header-title">Profil Utilisateur</h3>
            <span className="header-badge">Premium</span>
          </div>
        </div>
        
        <div className="form-body">
          <form onSubmit={handleSubmit}>
            <div className="form-section">
              <h4 className="section-heading">
                <span className="section-icon">üë§</span>
                Informations Personnelles
              </h4> 
              
              <div className="form-grid">
                <div className="form-column">
                  <div className="input-group">
                    <label className="input-label" htmlFor="username">
                      Nom d'utilisateur
                    </label>
                    <input
                      id="username"
                      type="text"
                      className="input-field"
                      defaultValue="marine.durand"
                    />
                  </div>
                </div>
                
                <div className="form-column">
                  <div className="input-group">
                    <label className="input-label" htmlFor="email">
                      Adresse email
                    </label>
                    <input
                      id="email"
                      type="email"
                      className="input-field"
                      defaultValue="marine@example.com"
                    />
                  </div>
                </div>
                
                <div className="form-column">
                  <div className="input-group">
                    <label className="input-label" htmlFor="firstName">
                      Pr√©nom
                    </label>
                    <input
                      id="firstName"
