import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const CardDemandeConge = () => {
  const history = useHistory();
  const [formData, setFormData] = useState({
    startDate: '',
    endDate: '',
    reason: '',
    type: 'congé payé',
    notes: ''
  });
  
  const [loading, setLoading] = useState(false);
  const [userData, setUserData] = useState(null);
  const [soldeConge, setSoldeConge] = useState(0);
  const [alert, setAlert] = useState({ show: false, message: '', type: '' });

  // Afficher une alerte pendant 5 secondes
  const showAlert = (message, type = 'error') => {
    setAlert({ show: true, message, type });
    setTimeout(() => setAlert({ ...alert, show: false }), 5000);
  };

  // Vérifier l'authentification et récupérer les données
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const token = document.cookie
          .split('; ')
          .find(row => row.startsWith('jwt_token_ubcirh='))
          ?.split('=')[1];

        if (!token) {
          showAlert('Veuillez vous connecter');
          history.push('/login');
          return;
        }

        const response = await axios.get('/api/personnel/me', {
          headers: { 'Authorization': `Bearer ${token}` },
          withCredentials: true
        });

        setUserData(response.data);
        setSoldeConge(response.data.soldedeconge || 0);
      } catch (error) {
        showAlert('Session expirée, veuillez vous reconnecter');
        history.push('/login');
      }
    };

    fetchUserData();
  }, [history]);

  const calculateDays = () => {
    if (!formData.startDate || !formData.endDate) return 0;
    const start = new Date(formData.startDate);
    const end = new Date(formData.endDate);
    return Math.ceil((end - start) / (1000 * 60 * 60 * 24)) + 1;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const token = document.cookie
      .split('; ')
      .find(row => row.startsWith('jwt_token_ubcirh='))
      ?.split('=')[1];

    if (!token) {
      showAlert('Session expirée, veuillez vous reconnecter');
      history.push('/login');
      return;
    }

    // Validation
    if (new Date(formData.startDate) > new Date(formData.endDate)) {
      showAlert('La date de fin doit être postérieure à la date de début');
      return;
    }

    const joursDemandes = calculateDays();

    if (formData.type === 'congé payé' && joursDemandes > soldeConge) {
      showAlert(`Solde insuffisant: ${soldeConge} jours disponibles`);
      return;
    }

    setLoading(true);

    try {
      await axios.post('/api/demandes-conge', {
        DateDebut: formData.startDate,
        DateFin: formData.endDate,
        motif: formData.reason,
        type: formData.type,
        notes: formData.notes,
        Duree: joursDemandes
      }, {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        withCredentials: true
      });

      showAlert('Demande envoyée avec succès!', 'success');
      setFormData({
        startDate: '',
        endDate: '',
        reason: '',
        type: 'congé payé',
        notes: ''
      });
    } catch (error) {
      if (error.response?.status === 401) {
        showAlert('Session expirée, veuillez vous reconnecter');
        history.push('/login');
      } else {
        showAlert(error.response?.data?.message || 'Erreur lors de la soumission');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-4">
      {alert.show && (
        <div className={`mb-4 p-3 rounded ${alert.type === 'success' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}`}>
          {alert.message}
          <button 
            onClick={() => setAlert({ ...alert, show: false })}
            className="float-right font-bold"
          >
            ×
          </button>
        </div>
      )}

      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-2xl font-bold text-blue-800 mb-6 text-center">
          Demande de Congé
        </h2>
        
        {userData && (
          <div className="mb-6 p-4 bg-blue-50 rounded-lg">
            <p className="text-lg">
              <span className="font-semibold">Bonjour </span>
              {userData.prenom} {userData.nom}
            </p>
            <p className="mt-2 text-blue-700 font-medium">
              Solde de congé disponible: {soldeConge} jours
            </p>
          </div>
        )}

        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Type de congé *
              </label>
              <select
                name="type"
                value={formData.type}
                onChange={handleChange}
                className="w-full p-2 border border-gray-300 rounded-md"
                required
              >
                <option value="congé payé">Congé payé</option>
                <option value="rtt">RTT</option>
                <option value="congé maladie">Congé maladie</option>
                <option value="congé sans solde">Congé sans solde</option>
              </select>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Date de début *
              </label>
              <input
                type="date"
                name="startDate"
                value={formData.startDate}
                onChange={handleChange}
                className="w-full p-2 border border-gray-300 rounded-md"
                required
                min={new Date().toISOString().split('T')[0]}
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Date de fin *
              </label>
              <input
                type="date"
                name="endDate"
                value={formData.endDate}
                onChange={handleChange}
                className="w-full p-2 border border-gray-300 rounded-md"
                required
                min={formData.startDate || new Date().toISOString().split('T')[0]}
              />
            </div>
          </div>

          {formData.startDate && formData.endDate && (
            <div className="p-3 bg-gray-100 rounded">
              <p>Durée: {calculateDays()} jours</p>
              {formData.type === 'congé payé' && (
                <p>Solde restant: {soldeConge - calculateDays()} jours</p>
              )}
            </div>
          )}

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Motif *
            </label>
            <input
              type="text"
              name="reason"
              value={formData.reason}
              onChange={handleChange}
              className="w-full p-2 border border-gray-300 rounded-md"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Notes (optionnel)
            </label>
            <textarea
              name="notes"
              value={formData.notes}
              onChange={handleChange}
              className="w-full p-2 border border-gray-300 rounded-md"
              rows="3"
            />
          </div>

          <button
            type="submit"
            disabled={loading}
            className={`w-full py-2 px-4 rounded text-white ${loading ? 'bg-blue-400' : 'bg-blue-600 hover:bg-blue-700'}`}
          >
            {loading ? 'Envoi en cours...' : 'Soumettre la demande'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default CardDemandeConge;
