import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

const CardDemandeConge = () => {
  // États du formulaire
  const [formData, setFormData] = useState({
    startDate: '',
    endDate: '',
    reason: '',
    type: 'congé payé',
    notes: '',
    medicalFile: null
  });
  
  const [fileName, setFileName] = useState('');
  const fileInputRef = useRef(null);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [confirmationMessage, setConfirmationMessage] = useState('');
  const [submitError, setSubmitError] = useState(null);
  const [loading, setLoading] = useState(false);
  
  // États d'authentification (comme dans CardDemandeAvance)
  const [token, setToken] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userData, setUserData] = useState({
    nom: '',
    prenom: '',
    email: '',
    soldeConge: 0
  });

  // Styles (identique à votre version originale)
  const styles = {
    container: {
      width: '100%',
      maxWidth: '600px',
      margin: '0 auto',
      padding: '20px',
      backgroundColor: '#f5f7fa',
      fontFamily: 'Arial, sans-serif'
    },
    header: {
      textAlign: 'center',
      marginBottom: '20px',
      padding: '20px 0' 
    },
    title: {
      fontSize: '28px',
      fontWeight: '700',
      color: '#1e40af',
      margin: '0 0 10px 0',
      position: 'relative',
      display: 'inline-block',
      paddingBottom: '15px'
    },
    titleAfter: {
      content: '""',
      position: 'absolute',
      bottom: 0,
      left: '50%',
      transform: 'translateX(-50%)',
      width: '60%',
      height: '4px',
      background: 'linear-gradient(90deg, #1e40af, #3b82f6)',
      borderRadius: '2px'
    },
    subtitle: {
      fontSize: '16px',
      color: '#64748b',
      margin: '10px 0 0 0',
      letterSpacing: '0.3px'
    },
    form: {
      backgroundColor: '#ffffff',
      borderRadius: '8px',
      padding: '25px',
      boxShadow: '0 2px 6px rgba(0,0,0,0.1)'
    },
    formGroup: {
      marginBottom: '20px'
    },
    label: {
      display: 'block',
      marginBottom: '8px',
      fontWeight: '500',
      fontSize: '14px',
      color: '#334155'
    },
    input: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      boxSizing: 'border-box'
    },
    select: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      backgroundColor: '#fff',
      boxSizing: 'border-box'
    },
    textarea: {
      width: '100%',
      padding: '10px',
      borderRadius: '4px',
      border: '1px solid #e2e8f0',
      fontSize: '14px',
      minHeight: '100px',
      resize: 'vertical',
      boxSizing: 'border-box'
    },
    infoText: {
      fontSize: '12px',
      color: '#64748b',
      marginTop: '5px',
      fontStyle: 'italic'
    },
    submitBtn: {
      width: '100%',
      padding: '12px',
      backgroundColor: '#2563eb',
      color: '#ffffff',
      borderRadius: '4px',
      fontWeight: '500',
      border: 'none',
      cursor: 'pointer',
      fontSize: '16px',
      transition: 'background-color 0.3s',
      marginTop: '10px'
    },
    submitBtnHover: {
      backgroundColor: '#1d4ed8'
    },
    loading: {
      display: 'inline-block',
      marginLeft: '10px'
    },
    alert: {
      padding: '15px',
      borderRadius: '6px',
      marginBottom: '20px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      transition: 'opacity 0.3s',
      opacity: 1
    },
    alertError: {
      backgroundColor: '#fee2e2',
      color: '#b91c1c',
      borderLeft: '4px solid #b91c1c'
    },
    alertSuccess: {
      backgroundColor: '#dcfce7',
      color: '#16a34a',
      borderLeft: '4px solid #16a34a'
    },
    alertIcon: {
      fontSize: '18px',
      marginRight: '10px'
    },
    alertContent: {
      flex: 1
    },
    alertClose: {
      backgroundColor: 'transparent',
      border: 'none',
      cursor: 'pointer',
      fontSize: '18px',
      color: 'inherit'
    },
    statusIndicator: {
      display: 'inline-block',
      padding: '4px 8px',
      borderRadius: '12px',
      fontSize: '12px',
      fontWeight: '500',
      marginLeft: '8px'
    },
    statusPending: {
      backgroundColor: '#fef3c7',
      color: '#d97706'
    },
    statusApproved: {
      backgroundColor: '#dcfce7',
      color: '#16a34a'
    },
    statusRejected: {
      backgroundColor: '#fee2e2',
      color: '#b91c1c'
    }
  };

  // Types de congés disponibles
  const congeTypes = [ /* ... (vos types de congés existants) ... */ ];

  // Vérification initiale comme dans CardDemandeAvance
  useEffect(() => {
    const userDataStr = localStorage.getItem('userData');
    const authToken = localStorage.getItem('token') || localStorage.getItem('authToken');

    if (userDataStr && authToken) {
      try {
        const userData = JSON.parse(userDataStr);
        setUserData({
          nom: userData.nom || '',
          prenom: userData.prenom || '',
          email: userData.email || '',
          soldeConge: userData.soldeConge || 0
        });
        setToken(authToken);
        setIsAuthenticated(true);
      } catch (e) {
        console.error("Erreur de parsing userData:", e);
        localStorage.removeItem('userData');
        localStorage.removeItem('token');
      }
    } else {
      console.warn("Données d'authentification manquantes");
      setIsAuthenticated(false);
    }
  }, []);

  // Gestion des changements de formulaire
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    setSubmitError(null);
  };
  
  // Gestion des fichiers joints
  const handleFileChange = (e) => {
    if (e.target.files?.[0]) {
      setFormData(prev => ({ ...prev, medicalFile: e.target.files[0] }));
      setFileName(e.target.files[0].name);
    }
  };
  
  // Suppression du fichier joint
  const removeFile = () => {
    setFormData(prev => ({ ...prev, medicalFile: null }));
    setFileName('');
    if (fileInputRef.current) fileInputRef.current.value = '';
  };

  // Calcul du nombre de jours de congé demandés
  const calculateDays = () => {
    if (!formData.startDate || !formData.endDate) return 0;
    const start = new Date(formData.startDate);
    const end = new Date(formData.endDate);
    return Math.ceil((end - start) / (1000 * 60 * 60 * 24)) + 1;
  };

  // Soumission du formulaire (adaptée comme CardDemandeAvance)
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!isAuthenticated || !token) {
      setSubmitError("Session expirée. Veuillez vous reconnecter.");
      return;
    }

    if (formData.type === 'congé maladie' && !formData.medicalFile) {
      setSubmitError("Un justificatif médical est requis pour les congés maladie");
      return;
    }

    setLoading(true);

    try {
      const formDataToSend = new FormData();
      formDataToSend.append('startDate', formData.startDate);
      formDataToSend.append('endDate', formData.endDate);
      formDataToSend.append('type', formData.type);
      formDataToSend.append('reason', formData.reason || '');
      formDataToSend.append('notes', formData.notes || '');
      
      if (formData.medicalFile) {
        formDataToSend.append('medicalFile', formData.medicalFile);
      }

      const response = await axios.post('/demandes-conge', formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'Authorization': `Bearer ${token}`
        }
      });

      setConfirmationMessage(`Demande enregistrée (Réf: ${response.data.data._id || ''})`);
      setShowConfirmation(true);
      
      // Réinitialisation
      setFormData({
        startDate: '',
        endDate: '',
        reason: '',
        type: 'congé payé',
        notes: '',
        medicalFile: null
      });
      setFileName('');

    } catch (error) {
      if (error.response?.status === 401) {
        setSubmitError("Session expirée. Veuillez vous reconnecter.");
        localStorage.removeItem('token');
        setIsAuthenticated(false);
      } else {
        setSubmitError(error.response?.data?.message || "Erreur lors de la soumission");
      }
    } finally {
      setLoading(false);
    }
  };

  // Si non authentifié, afficher un message (comme CardDemandeAvance)
  if (!isAuthenticated) {
    return (
      <div style={styles.container}>
        <div style={{ 
          ...styles.formContainer,
          textAlign: 'center',
          padding: '40px'
        }}>
          <h3 style={{ color: '#ef4444', marginBottom: '20px' }}>
            Authentification requise
          </h3>
          <p>Veuillez vous connecter pour accéder à cette fonctionnalité.</p>
          <button 
            style={{ 
              ...styles.button,
              marginTop: '20px',
              backgroundColor: '#2563eb'
            }}
            onClick={() => window.location.href = '/signin'}
          >
            Se connecter
          </button>
        </div>
      </div>
    );
  }

  // Rendu normal si authentifié
  const selectedCongeType = congeTypes.find(conge => conge.type === formData.type);
  const isMaladieType = formData.type === 'congé maladie';
  const needsJustification = ['congé maladie', 'congé familial'].includes(formData.type);
  
  return (
    <div style={styles.container}>
      <div style={styles.mainContent}>
        <div style={styles.header}>
          <h1 style={styles.title}>
            Demande de Congé
            <span style={styles.titleAfter}></span>
          </h1>
          <p style={styles.subtitle}>
            Bonjour {userData.prenom} {userData.nom}, découvrez et soumettez vos demandes de congés
          </p>
        </div>

        <div style={styles.formContainer}>
          <div style={styles.formHeader}>
            <h2 style={styles.formTitle}>Formulaire de demande de congé</h2>
            <p style={styles.formDescription}>Veuillez remplir tous les champs obligatoires</p>
          </div>
          
          {submitError && (
            <div style={styles.errorMessage}>
              {submitError}
            </div>
          )}

          <form onSubmit={handleSubmit}>
            {/* ... (conservez tout votre JSX existant ici) ... */}
            
            <div style={styles.buttonContainer}>
              <button 
                type="submit" 
                style={styles.button}
                disabled={loading}
              >
                {loading ? 'Envoi en cours...' : 'Soumettre la demande'}
              </button>
            </div>
          </form>
        </div>
      </div>

      {/* Toast de confirmation */}
      {showConfirmation && (
        <div style={styles.toastMessage}>
          <div style={styles.toastContent}>
            <span style={styles.toastIcon}>✓</span>
            <p>{confirmationMessage}</p>
          </div>
        </div>
      )}

      <style>
        {`
        @keyframes slideUp {
          from {
            transform: translate(-50%, 100%);
            opacity: 0;
          }
          to {
            transform: translate(-50%, 0);
            opacity: 1;
          }
        }

        @keyframes fadeOut {
          from {
            opacity: 1;
          }
          to {
            opacity: 0;
          }
        }
        `}
      </style>
    </div>
  );
};

export default CardDemandeConge;
