        afficherToast("Employé supprimé avec succès", "error");
        fetchEmployes(); // Recharger la liste
        fermerSuppressionModal();
      } catch (error) {
        console.error("Erreur:", error);
        afficherToast("Erreur lors de la suppression", "error");
      } finally {
        setIsLoading(false);
      }
    }
  };

  return (
    <>
      {/* CSS et UI inchangés */}
      <style>{encapsulatedStyles}</style>
      
      <div className="relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded">
        <div className="gp-personnel-container">
          {/* En-tête, recherche, etc. */}
          {/* ... code inchangé ... */}
          
          {/* Afficher un indicateur de chargement */}
          {isLoading && (
            <div style={{ 
              display: 'flex', 
              justifyContent: 'center', 
              padding: '1rem',
              color: '#14b8a6'
            }}>
              Chargement...
            </div>
          )}
          
          {/* Tableau des employés */}
          <div className="gp-table-container">
            {/* ... code inchangé ... */}
          </div>
          
          {/* ... reste du code UI inchangé ... */}
        </div>
        
        {/* Modals et toast (inchangés) */}
      </div>
    </>
  );
}
