import React, { useState, useEffect } from "react";
import { FaEye, FaEdit, FaTrash } from "react-icons/fa";

const CardDepartment = () => {
  const [departments, setDepartments] = useState([
    { id: 1, nom: "Département A", departement: "Finance" },
    { id: 2, nom: "Département B", departement: "Marketing" },
  ]);
  const [searchTerm, setSearchTerm] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [newDepartment, setNewDepartment] = useState({ nom: "", departement: "" });
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [selectedDepartment, setSelectedDepartment] = useState(null);
  const [isViewModalOpen, setIsViewModalOpen] = useState(false);
  const [isDialogOpen, setIsDialogOpen] = useState(false);

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === "Escape") {
        setIsModalOpen(false);
        setIsEditModalOpen(false);
        setIsViewModalOpen(false);
        setIsDialogOpen(false);
      }
    };

    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, []);

  const handleAddDepartment = () => {
    if (!validateForm()) return;

    const newId = departments.length ? Math.max(...departments.map(d => d.id)) + 1 : 1;
    setDepartments([...departments, { id: newId, ...newDepartment }]);
    setIsModalOpen(false);
    setNewDepartment({ nom: "", departement: "" });
  };

  const handleEditSubmit = () => {
    if (!validateForm()) return;

    const updatedDepartments = departments.map(department =>
      department.id === selectedDepartment.id ? { ...selectedDepartment } : department
    );

    setDepartments(updatedDepartments);
    setIsEditModalOpen(false);
    setSelectedDepartment(null);
  };

  const handleDelete = (id) => {
    setDepartments(departments.filter((department) => department.id !== id));
    setIsDialogOpen(false);
  };

  const validateForm = () => {
    return newDepartment.nom.trim() !== "" && newDepartment.departement.trim() !== "";
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Rechercher un département"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <button onClick={() => setIsModalOpen(true)}>Ajouter Département</button>

      <div>
        {departments.filter((department) => {
          const term = searchTerm.toLowerCase().trim();
          return department.nom.toLowerCase().includes(term) || department.departement.toLowerCase().includes(term);
        }).map((department) => (
          <div key={department.id}>
            <h3>{department.nom}</h3>
            <p>{department.departement}</p>
            <button onClick={() => { setSelectedDepartment(department); setIsViewModalOpen(true); }}><FaEye /></button>
            <button onClick={() => { setSelectedDepartment(department); setIsEditModalOpen(true); }}><FaEdit /></button>
            <button onClick={() => { setSelectedDepartment(department); setIsDialogOpen(true); }}><FaTrash /></button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CardDepartment;
