import React, { useState } from 'react';

const LeaveRequestForm = () => {
  const [formData, setFormData] = useState({
    startDate: '',
    endDate: '',
    reason: '',
    type: 'cong√© pay√©',
    notes: ''
  });
  
  const [submitted, setSubmitted] = useState(false);
  
  const styles = {
    container: {
      backgroundColor: '#f0f4f8',
      minHeight: '100vh',
      padding: '20px',
      fontFamily: 'Arial, sans-serif'
    },
    logo: {
      height: '50px',
      marginBottom: '20px'
    },
    card: {
      maxWidth: '1000px',
      margin: '0 auto',
      backgroundColor: '#FFFFFF',
      borderRadius: '8px',
      boxShadow: '0 2px 6px rgba(0, 0, 0, 0.1)',
      overflow: 'hidden'
    },
    header: {
      backgroundColor: '#1e3a8a',
      padding: '20px',
      color: '#FFFFFF',
      textAlign: 'center',
      borderBottom: '3px solid #3b82f6'
    },
    title: {
      fontSize: '28px',
      fontWeight: 'bold',
      margin: '0'
    },
    subtitle: {
      color: '#e0e7ff',
      marginTop: '10px',
      fontSize: '16px'
    },
    formContainer: {
      padding: '30px'
    },
    search: {
      position: 'relative',
      marginBottom: '30px'
    },
    searchIcon: {
      position: 'absolute',
      left: '12px',
      top: '12px',
      color: '#6b7280'
    },
    searchInput: {
      width: '100%',
      padding: '12px 12px 12px 40px',
      border: '1px solid #e5e7eb',
      borderRadius: '8px',
      fontSize: '16px',
      boxShadow: '0 1px 2px rgba(0, 0, 0, 0.05)'
    },
    categories: {
      display: 'flex',
      flexWrap: 'wrap',
      gap: '10px',
      marginBottom: '30px'
    },
    category: {
      padding: '8px 16px',
      borderRadius: '6px',
      fontSize: '14px',
      fontWeight: '500',
      cursor: 'pointer',
      transition: 'all 0.2s ease'
    },
    activeCategory: {
      backgroundColor: '#3b82f6',
      color: '#FFFFFF'
    },
    inactiveCategory: {
      backgroundColor: '#f3f4f6',
      color: '#4b5563'
    },
    requestCard: {
      border: '1px solid #e5e7eb',
      borderRadius: '8px',
      padding: '24px',
      marginBottom: '20px',
      backgroundColor: '#f0f9ff'
    },
    requestTitle: {
      fontSize: '20px',
      fontWeight: '600',
      color: '#1e40af',
      marginBottom: '16px'
    },
    formGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(2, 1fr)',
      gap: '20px',
      marginBottom: '24px'
    },
    formGroup: {
      marginBottom: '16px'
    },
    label: {
      display: 'block',
      marginBottom: '6px',
      fontSize: '14px',
      fontWeight: '500',
      color: '#374151'
    },
    input: {
      width: '100%',
      padding: '10px 12px',
      border: '1px solid #d1d5db',
      borderRadius: '6px',
      fontSize: '14px',
      transition: 'border-color 0.2s ease',
      backgroundColor: '#FFFFFF'
    },
    select: {
      width: '100%',
      padding: '10px 12px',
      border: '1px solid #d1d5db',
      borderRadius: '6px',
      fontSize: '14px',
      backgroundColor: '#FFFFFF'
    },
    textarea: {
      width: '100%',
      padding: '10px 12px',
      border: '1px solid #d1d5db',
      borderRadius: '6px',
      fontSize: '14px',
      minHeight: '100px',
      resize: 'vertical',
      backgroundColor: '#FFFFFF'
    },
    infoSection: {
      display: 'flex',
      gap: '20px',
      marginBottom: '20px'
    },
    infoItem: {
      display: 'flex',
      alignItems: 'center',
      gap: '8px'
    },
    icon: {
      color: '#6b7280',
      fontSize: '16px'
    },
    infoText: {
      fontSize: '14px',
      color: '#4b5563'
    },
    spots: {
      display: 'inline-block',
      padding: '4px 12px',
      backgroundColor: '#dbeafe',
      color: '#1e40af',
      borderRadius: '16px',
      fontSize: '12px',
      fontWeight: '500'
    },
    alert: {
      backgroundColor: '#dbeafe',
      borderRadius: '6px',
      padding: '12px 16px',
      marginBottom: '20px',
      display: 'flex',
      alignItems: 'center',
      gap: '8px'
    },
    alertIcon: {
      color: '#3b82f6',
      fontSize: '18px'
    },
    alertText: {
      color: '#1e40af',
      fontSize: '14px',
      margin: '0'
    },
    buttonContainer: {
      display: 'flex',
      justifyContent: 'center',
      marginTop: '24px'
    },
    submitButton: {
      padding: '12px 24px',
      backgroundColor: '#2563eb',
      color: '#FFFFFF',
      border: 'none',
      borderRadius: '6px',
      cursor: 'pointer',
      fontSize: '16px',
      fontWeight: '500',
      transition: 'background-color 0.2s ease',
      width: '100%',
      maxWidth: '300px'
    },
    successAlert: {
      backgroundColor: '#dcfce7',
      borderRadius: '6px',
      padding: '16px',
      marginBottom: '24px',
      display: 'flex',
      alignItems: 'center',
      gap: '12px'
    },
    successIcon: {
      color: '#16a34a',
      fontSize: '20px'
    },
    successText: {
      color: '#166534',
      fontSize: '16px',
      fontWeight: '500',
      margin: '0'
    },
    summaryTitle: {
      fontSize: '20px',
      fontWeight: '600',
      color: '#1e40af',
      marginBottom: '16px',
      textAlign: 'center'
    },
    summaryCard: {
      backgroundColor: '#f8fafc',
      padding: '24px',
      borderRadius: '8px',
      marginBottom: '24px',
      border: '1px solid #e2e8f0'
    },
    summaryGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(2, 1fr)',
      gap: '16px'
    },
    summaryLabel: {
      fontSize: '13px',
      color: '#64748b',
      marginBottom: '4px'
    },
    summaryValue: {
      fontSize: '15px',
      fontWeight: '500',
      color: '#334155'
    },
    colSpan2: {
      gridColumn: 'span 2'
    }
  };
  
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };
  
  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
  };
  
  const calculateDays = () => {
    if (!formData.startDate || !formData.endDate) return 0;
    
    const start = new Date(formData.startDate);
    const end = new Date(formData.endDate);
    const diffTime = Math.abs(end - start);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24)) + 1;
    
    return diffDays;
  };
  
  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <div style={styles.header}>
          <h1 style={styles.title}>Demande de Cong√©</h1>
          <p style={styles.subtitle}>Remplissez le formulaire ci-dessous pour soumettre votre demande</p>
        </div>
        
        {submitted ? (
          <div style={styles.formContainer}>
            <div style={styles.successAlert}>
              <span style={styles.successIcon}>‚úì</span>
              <p style={styles.successText}>
                Votre demande de cong√© a √©t√© soumise avec succ√®s!
              </p>
            </div>
            
            <h2 style={styles.summaryTitle}>R√©capitulatif de votre demande</h2>
            
            <div style={styles.summaryCard}>
              <div style={styles.summaryGrid}>
                <div>
                  <p style={styles.summaryLabel}>Type de cong√©</p>
                  <p style={styles.summaryValue}>{formData.type}</p>
                </div>
                <div>
                  <p style={styles.summaryLabel}>Nombre de jours</p>
                  <p style={styles.summaryValue}>{calculateDays()} jours</p>
                </div>
                <div>
                  <p style={styles.summaryLabel}>Date de d√©but</p>
                  <p style={styles.summaryValue}>{new Date(formData.startDate).toLocaleDateString('fr-FR')}</p>
                </div>
                <div>
                  <p style={styles.summaryLabel}>Date de fin</p>
                  <p style={styles.summaryValue}>{new Date(formData.endDate).toLocaleDateString('fr-FR')}</p>
                </div>
                <div style={styles.colSpan2}>
                  <p style={styles.summaryLabel}>Motif</p>
                  <p style={styles.summaryValue}>{formData.reason}</p>
                </div>
                {formData.notes && (
                  <div style={styles.colSpan2}>
                    <p style={styles.summaryLabel}>Notes compl√©mentaires</p>
                    <p style={styles.summaryValue}>{formData.notes}</p>
                  </div>
                )}
              </div>
            </div>
            
            <div style={styles.buttonContainer}>
              <button 
                onClick={() => setSubmitted(false)} 
                style={styles.submitButton}
              >
                Nouvelle demande
              </button>
            </div>
          </div>
        ) : (
          <div style={styles.formContainer}>
            <div style={styles.search}>
              <span style={styles.searchIcon}>üîç</span>
              <input 
                type="text" 
                placeholder="Rechercher un type de cong√©..." 
                style={styles.searchInput}
              />
            </div>
            
            <div style={styles.categories}>
              <span style={{...styles.category, ...styles.activeCategory}}>Tous</span>
              <span style={{...styles.category, ...styles.inactiveCategory}}>Cong√©s pay√©s</span>
              <span style={{...styles.category, ...styles.inactiveCategory}}>Cong√©s maladie</span>
              <span style={{...styles.category, ...styles.inactiveCategory}}>Autres types</span>
            </div>
            
            <div style={styles.requestCard}>
              <h2 style={styles.requestTitle}>Formulaire de demande de cong√©</h2>
              
              <form onSubmit={handleSubmit}>
                <div style={styles.formGrid}>
                  <div style={styles.formGroup}>
                    <label style={styles.label} htmlFor="type">
                      Type de cong√©
                    </label>
                    <select
                      id="type"
                      name="type"
                      value={formData.type}
                      onChange={handleChange}
                      style={styles.select}
                      required
                    >
                      <option value="cong√© pay√©">Cong√© pay√©</option>
                      <option value="cong√© sans solde">Cong√© sans solde</option>
                      <option value="cong√© maladie">Cong√© maladie</option>
                      <option value="cong√© maternit√©">Cong√© maternit√©/paternit√©</option>
                      <option value="autre">Autre</option>
                    </select>
                  </div>
                  
                  <div style={styles.formGroup}>
                    <label style={styles.label} htmlFor="reason">
                      Motif
                    </label>
                    <input
                      type="text"
                      id="reason"
                      name="reason"
                      value={formData.reason}
                      onChange={handleChange}
                      style={styles.input}
                      required
                    />
                  </div>
                  
                  <div style={styles.formGroup}>
                    <label style={styles.label} htmlFor="startDate">
                      Date de d√©but
                    </label>
                    <input
                      type="date"
                      id="startDate"
                      name="startDate"
                      value={formData.startDate}
                      onChange={handleChange}
                      style={styles.input}
                      required
                    />
                  </div>
                  
                  <div style={styles.formGroup}>
                    <label style={styles.label} htmlFor="endDate">
                      Date de fin
                    </label>
                    <input
                      type="date"
                      id="endDate"
                      name="endDate"
                      value={formData.endDate}
                      onChange={handleChange}
                      style={styles.input}
                      required
                    />
                  </div>
                </div>
                
                {formData.startDate && formData.endDate && (
                  <div style={styles.alert}>
                    <span style={styles.alertIcon}>‚ÑπÔ∏è</span>
                    <p style={styles.alertText}>
                      Dur√©e du cong√©: <strong>{calculateDays()} jours</strong>
                    </p>
                  </div>
                )}
                
                <div style={styles.formGroup}>
                  <label style={styles.label} htmlFor="notes">
                    Notes compl√©mentaires (optionnel)
                  </label>
                  <textarea
                    id="notes"
                    name="notes"
                    value={formData.notes}
                    onChange={handleChange}
                    style={styles.textarea}
                  ></textarea>
                </div>
                
                <div style={styles.buttonContainer}>
                  <button
                    type="submit"
                    style={styles.submitButton}
                  >
                    Demander ce cong√©
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default LeaveRequestForm;
