              <input
                type="text"
                name="title"
                placeholder="Titre de l'offre"
                className="modal-input"
                value={newOffer.title}
                onChange={handleInputChange}
              />
              <input
                type="text"
                name="location"
                placeholder="Lieu"
                className="modal-input"
                value={newOffer.location}
                onChange={handleInputChange}
              />
              <input
                type="date"
                name="date"
                className="modal-input"
                value={newOffer.date}
                onChange={handleInputChange}
              />
              <button type="submit" className="modal-button">Ajouter</button>
              <button type="button" className="modal-button" onClick={handleCloseModal}>Annuler</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;*/}

import React from 'react';

const Home = () => {
    // Styles CSS en tant qu'objets JavaScript
    const styles = {
        container: {
            position: 'relative',
            width: '100%',
            maxWidth: '800px',
            minHeight: '480px',
            backgroundColor: '#fff',
            borderRadius: '10px',
            overflow: 'hidden',
            boxShadow: '0 14px 28px rgba(0,0,0,0.25), 0 10px 10px rgba(0,0,0,0.22)',
        },
        formContainer: {
            position: 'absolute',
            top: 0,
            height: '100%',
            transition: 'all 0.6s ease-in-out',
        },
        signInContainer: {
            left: 0,
            width: '50%',
            zIndex: 2,
        },
        signUpContainer: {
            left: 0,
            width: '50%',
            opacity: 0,
            zIndex: 1,
        },
        overlayContainer: {
            position: 'absolute',
            top: 0,
            left: '50%',
            width: '50%',
            height: '100%',
            overflow: 'hidden',
            transition: 'transform 0.6s ease-in-out',
            zIndex: 100,
        },
        overlay: {
            background: 'linear-gradient(to right, #141E30, #243B55)',
            backgroundRepeat: 'no-repeat',
            backgroundSize: 'cover',
            backgroundPosition: '0 0',
            color: '#fff',
            position: 'relative',
            left: '-100%',
            height: '100%',
            width: '200%',
            transform: 'translateX(0)',
            transition: 'transform 0.6s ease-in-out',
        },
        overlayPanel: {
            position: 'absolute',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            flexDirection: 'column',
            padding: '0 40px',
            textAlign: 'center',
            top: 0,
            height: '100%',
            width: '50%',
            transform: 'translateX(0)',
            transition: 'transform 0.6s ease-in-out',
        },
        overlayLeft: {
            transform: 'translateX(-20%)',
        },
        overlayRight: {
            right: 0,
            transform: 'translateX(0)',
        },
        socialContainer: {
            margin: '20px 0',
        },
        socialLink: {
            border: '1px solid #DDD',
            borderRadius: '50%',
            display: 'inline-flex',
            justifyContent: 'center',
            alignItems: 'center',
            margin: '0 5px',
            height: '40px',
            width: '40px',
        },
        footer: {
            position: 'absolute',
            left: '50%',
            bottom: '30px',
            transform: 'translateX(-50%)',
        },
        footerMark: {
            padding: '8px 30px',
            borderRadius: '7px',
        },
        footerLink: {
            textDecoration: 'none',
            fontSize: '18px',
            fontWeight: 'bold',
            color: '#003',
        },
    };

    return (
        <div style={styles.container} id="container">
            <div style={{ ...styles.formContainer, ...styles.signUpContainer }}>
                <form action="#">
                    <h1>Create Account</h1>
                    <div style={styles.socialContainer}>
                        <a href="#" style={styles.socialLink}><i className="fab fa-facebook-f"></i></a>
                        <a href="#" style={styles.socialLink}><i className="fab fa-google-plus-g"></i></a>
                        <a href="#" style={styles.socialLink}><i className="fab fa-linkedin-in"></i></a>
                    </div>
                    <span>or use your email for registration</span>
                    <div className="infield">
                        <input type="text" placeholder="Name" />
                        <label></label>
                    </div>
                    <div className="infield">
                        <input type="email" placeholder="Email" name="email" />
                        <label></label>
                    </div>
                    <div className="infield">
                        <input type="password" placeholder="Password" />
                        <label></label>
                    </div>
                    <button>Sign Up</button>
                </form>
            </div>
            <div style={{ ...styles.formContainer, ...styles.signInContainer }}>
                <form action="#">
                    <h1>Sign in</h1>
                    <div style={styles.socialContainer}>
                        <a href="#" style={styles.socialLink}><i className="fab fa-facebook-f"></i></a>
                        <a href="#" style={styles.socialLink}><i className="fab fa-google-plus-g"></i></a>
                        <a href="#" style={styles.socialLink}><i className="fab fa-linkedin-in"></i></a>
                    </div>
                    <span>or use your account</span>
                    <div className="infield">
                        <input type="email" placeholder="Email" name="email" />
                        <label></label>
                    </div>
                    <div className="infield">
                        <input type="password" placeholder="Password" />
                        <label></label>
                    </div>
                    <a href="#" className="forgot">Forgot your password?</a>
                    <button>Sign In</button>
                </form>
            </div>
            <div style={styles.overlayContainer} id="overlayCon">
                <div style={styles.overlay}>
                    <div style={{ ...styles.overlayPanel, ...styles.overlayLeft }}>
                        <h1>Welcome Back!</h1>
                        <p>To keep connected with us please login with your personal info</p>
                        <button>Sign In</button>
                    </div>
                    <div style={{ ...styles.overlayPanel, ...styles.overlayRight }}>
                        <h1>Hello, Friend!</h1>
                        <p>Enter your personal details and start journey with us</p>
                        <button>Sign Up</button>
