import React, { useState } from 'react';

const Home = ({ addOffer }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [newOffer, setNewOffer] = useState({ title: '', location: '', date: '' });

  const handleAddOffer = () => setIsModalOpen(true);
  const handleCloseModal = () => setIsModalOpen(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewOffer({ ...newOffer, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (newOffer.title && newOffer.location && newOffer.date) {
      addOffer(newOffer); // Appelle la fonction passée en prop
      setNewOffer({ title: '', location: '', date: '' });
      setIsModalOpen(false);
    } else {
      alert('Veuillez remplir tous les champs !');
    }
  };

  const styles = {
    homeContainer: { textAlign: 'center', padding: '20px' },
    addButton: { backgroundColor: '#007bff', color: 'white', padding: '10px 20px', border: 'none', cursor: 'pointer', borderRadius: '5px' },
    modalOverlay: { position: 'fixed', top: 0, left: 0, width: '100%', height: '100%', backgroundColor: 'rgba(0,0,0,0.5)', display: 'flex', justifyContent: 'center', alignItems: 'center' },
    modalContent: { backgroundColor: 'white', padding: '20px', borderRadius: '5px', width: '300px' },
    modalInput: { width: '100%', padding: '10px', margin: '10px 0', borderRadius: '5px', border: '1px solid #ccc' },
    modalButton: { backgroundColor: '#007bff', color: 'white', border: 'none', padding: '10px 20px', borderRadius: '5px', cursor: 'pointer', marginRight: '10px' },
  };

  return (
    <div style={styles.homeContainer}>
      <button style={styles.addButton} onClick={handleAddOffer}>Ajouter une offre</button>

      {isModalOpen && (
        <div style={styles.modalOverlay}>
          <div style={styles.modalContent}>
            <h2>Ajouter une nouvelle offre</h2>
            <form onSubmit={handleSubmit}>
              <input type="text" name="title" placeholder="Titre de l'offre" style={styles.modalInput} value={newOffer.title} onChange={handleInputChange} />
              <input type="text" name="location" placeholder="Lieu" style={styles.modalInput} value={newOffer.location} onChange={handleInputChange} />
              <input type="text" name="date" placeholder="Date" style={styles.modalInput} value={newOffer.date} onChange={handleInputChange} />
              <button type="submit" style={styles.modalButton}>Ajouter</button>
              <button type="button" style={styles.modalButton} onClick={handleCloseModal}>Annuler</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
