import React, { useState } from "react";
import PropTypes from "prop-types";
import { MdDelete, MdVisibility, MdEdit } from "react-icons/md"; // Importation des icônes

export default function CardPersonnels({ color = "light" }) {
  const [searchTerm, setSearchTerm] = useState("");
  const [sortOption, setSortOption] = useState("");
  const [personnels, setPersonnels] = useState([
    { id: 1, nom: "Jean Dupont", departement: "Informatique" },
    { id: 2, nom: "Sophie Martin", departement: "Ressources Humaines" },
    { id: 3, nom: "Ali Ben Salah", departement: "Finance" },
  ]);

  const [showModal, setShowModal] = useState(false); // État pour contrôler l'affichage du modal
  const [newPersonnel, setNewPersonnel] = useState({
    nom: "",
    departement: "",
  });

  const filteredPersonnels = personnels
    .filter((personnel) => {
      if (!isNaN(searchTerm)) {
        return personnel.id.toString().includes(searchTerm);
      }
      return personnel.nom.toLowerCase().includes(searchTerm.toLowerCase());
    })
    .sort((a, b) => {
      if (!sortOption) return 0;
      if (sortOption === "id-asc") return a.id - b.id;
      if (sortOption === "id-desc") return b.id - a.id;
      if (sortOption === "departement") return a.departement.localeCompare(b.departement);
      return 0;
    });

  const handleAction = (action, id) => {
    switch (action) {
      case "voir":
        alert(`Voir le personnel avec l'ID ${id}`);
        break;
      case "modifier":
        alert(`Modifier le personnel avec l'ID ${id}`);
        break;
      case "supprimer":
        setPersonnels(personnels.filter((personnel) => personnel.id !== id));
        break;
      default:
        break;
    }
  };

  const handleAddPersonnel = () => {
    const newId = personnels.length + 1;
    const newPerson = {
      id: newId,
      nom: newPersonnel.nom,
      departement: newPersonnel.departement,
    };
    setPersonnels([...personnels, newPerson]);
    setShowModal(false); // Fermer le modal après l'ajout
    setNewPersonnel({ nom: "", departement: "" }); // Réinitialiser le formulaire
  };

  return (
    <div className={"relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded " + (color === "light" ? "bg-white" : "bg-lightBlue-900 text-white")}>
      <div className="rounded-t mb-0 px-4 py-3 border-0">
        <div className="flex flex-wrap items-center justify-between">
          <h3 className={"font-semibold text-lg " + (color === "light" ? "text-blueGray-700" : "text-white")}>
            Liste des personnels
          </h3>
        </div>
        <div className="flex flex-wrap items-center justify-between mt-4">
          <div className="flex items-center space-x-6">
            <select
              value={sortOption}
              onChange={(e) => setSortOption(e.target.value)}
              className="px-3 py-2 border rounded-lg focus:outline-none btn-rech-id text-blueGray-700"
            >
              <option value="" disabled>Filtrer par</option>
              <option value="id-asc">ID Ascendant</option>
              <option value="id-desc">ID Descendant</option>
              <option value="departement">Par Département</option>
            </select>
            <input
              type="text"
              placeholder="Rechercher par nom ou ID..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="px-3 py-2 border rounded-lg focus:outline-none border-blueGray-300 text-blueGray-700 ml-4"
            />
          </div>
          <button
            onClick={() => setShowModal(true)} // Ouvrir le modal
            className="px-4 py-2 rounded-lg font-semibold text-btn-ajouterp bg-blue-500 hover:bg-blue-600 focus:outline-none"
          >
            Ajouter un personnel
          </button>
        </div>
      </div>

      {/* Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-gray-800 bg-opacity-50 flex justify-center items-center z-50">
          <div className="bg-white p-8 rounded-lg shadow-lg w-96">
            <h2 className="text-lg font-semibold mb-4">Ajouter un nouveau personnel</h2>
            <form onSubmit={(e) => e.preventDefault()}>
              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700">Nom</label>
                <input
                  type="text"
                  value={newPersonnel.nom}
                  onChange={(e) => setNewPersonnel({ ...newPersonnel, nom: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md"
                  placeholder="Nom du personnel"
                />
              </div>
              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700">Département</label>
                <input
                  type="text"
                  value={newPersonnel.departement}
                  onChange={(e) => setNewPersonnel({ ...newPersonnel, departement: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md"
                  placeholder="Département"
                />
              </div>
              <div className="flex justify-end space-x-4">
                <button
                  type="button"
                  onClick={() => setShowModal(false)} // Fermer le modal
                  className="px-4 py-2 text-sm font-semibold text-gray-700 bg-gray-200 rounded-md"
                >
                  Annuler
                </button>
                <button
                  type="submit"
                  onClick={handleAddPersonnel} // Ajouter le personnel
                  className="px-4 py-2 text-sm font-semibold text-white bg-blue-500 rounded-md"
                >
                  Ajouter
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="block w-full overflow-x-auto">
        <table className="items-center w-full bg-transparent border-collapse">
          <thead>
            <tr>
              {["Id", "Nom", "Département", "Action"].map((header, index) => (
                <th key={index} className="px-6 py-3 text-xs uppercase font-semibold text-left bg-blueGray-50 text-blueGray-500 border-blueGray-100">
                  {header}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {filteredPersonnels.map((personnel) => (
              <tr key={personnel.id}>
                <td className="px-6 py-4 border-b border-gray-200 text-sm">{personnel.id}</td>
                <td className="px-6 py-4 border-b border-gray-200 text-sm">{personnel.nom}</td>
                <td className="px-6 py-4 border-b border-gray-200 text-sm">{personnel.departement}</td>
                <td className="px-6 py-4 border-b border-gray-200 text-sm">
                  <button onClick={() => handleAction("voir", personnel.id)} className="bg-green-500 text-white p-2 rounded mr-2">
                    <MdVisibility />
                  </button>
                  <button onClick={() => handleAction("modifier", personnel.id)} className="bg-yellow-500 text-white p-2 rounded mr-2">
                    <MdEdit />
                  </button>
                  <button onClick={() => handleAction("supprimer", personnel.id)} className="bg-red-500 text-white p-2 rounded">
                    <MdDelete />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

CardPersonnels.propTypes = {
  color: PropTypes.string,
};
