import React, { useState } from "react";
import Modal from "react-modal";

const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    padding: "20px",
    borderRadius: "10px",
    backgroundColor: "#fff",
  },
};

// Lier le modal à l'élément racine de l'application
Modal.setAppElement("#root");

export default function AjoutPersonnels({ onClose, setPersonnels, personnels }) {
  const [nom, setNom] = useState("");
  const [departement, setDepartement] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const newPersonnel = {
      id: personnels.length + 1,
      nom,
      departement,
    };
    setPersonnels([...personnels, newPersonnel]);
    onClose(); // Fermer le modal après l'ajout
  };

  return (
    <Modal isOpen={true} onRequestClose={onClose} style={customStyles} contentLabel="Ajouter un personnel">
      <h2 className="text-lg font-bold mb-4">Ajouter un personnel</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="block text-gray-700">Nom:</label>
          <input
            type="text"
            value={nom}
            onChange={(e) => setNom(e.target.value)}
            className="border p-2 w-full rounded"
            required
          />
        </div>

        <div className="mb-3">
          <label className="block text-gray-700">Département:</label>
          <input
            type="text"
            value={departement}
            onChange={(e) => setDepartement(e.target.value)}
            className="border p-2 w-full rounded"
            required
          />
        </div>

        <div className="flex justify-end space-x-4 mt-4">
          <button type="button" onClick={onClose} className="bg-gray-500 text-white px-4 py-2 rounded">
            Annuler
          </button>
          <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">
            Ajouter
          </button>
        </div>
      </form>
    </Modal>
  );
}
