          <h1>Postes à pourvoir actuellement</h1>

          {hasSearched && (
            <div className="search-results-info">
              <p>
                {filteredJobs.length} offre(s) d'emploi trouvée(s){" "}
                {searchKeyword && `pour "${searchKeyword}"`}
              </p>
              {filteredJobs.length === 0 && (
                <p className="no-results">
                  Aucun résultat trouvé. Veuillez essayer d'autres critères de
                  recherche.
                </p>
              )}
            </div>
          )}

          <div className="job-cards">
            {displayedJobs.map((job) => (
              <div className="job-card" key={job.id}>
                <div className="job-card-content">
                  <h3 className="job-title">{job.title}</h3>
                  <div className="job-details">
                    <p className="job-location">{job.location}</p>
                    <p className="job-date">{job.date}</p>
                  </div>
                </div>
                <button 
                  className="postuler-button"
                  onClick={() => openApplicationModal(job)}
                >
                  Postuler
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Modal for job application */}
      {isModalOpen && (
        <div className="modal-overlay" onClick={closeModal}>
          <div 
            className="modal-content" 
            onClick={(e) => e.stopPropagation()}
          >
            <button className="modal-close" onClick={closeModal}>×</button>
            {/* <h2>Candidature pour {selectedJob.title}</h2> */}
            <form>
              <div className="form-section">
              <label> Nom complet</label> 
                <div className="name-inputs">
                  <input type="text" placeholder="Prénom" />
                  
                  <input type="text" placeholder="Nom de famille" />
                </div>
              </div>

              <div className="form-section">
                <label>Adresse actuelle</label>
                <input type="text" placeholder="Adresse" />
              
                <div className="address-details">
                  
                </div>
              </div>

              <div className="form-section">
                <label>Adresse E-mail</label>
                <input type="email" placeholder="Prénom@gmail.com" />
              </div>

              <div className="form-section">
                <label>Numéro de téléphone</label>
                <div className="phone-input">
                  <input type="text" placeholder="Indicatif" />
                  <input type="text" placeholder="Numéro de téléphone" />
                </div>
              </div>

              <div className="form-section">
                <label>Candidature au poste</label>
                <input 
                  type="text" 
                  value={selectedJob.title} 
                  readOnly 
                />
              </div>

              <div className="form-section">
                <label>Type de contrat</label>
                <select>
                  <option>Please Select</option>
                </select>
              </div>

              <div className="form-section">
                <label>Disponible à partir de</label>
                <input type="date" />
              </div>

              <div className="form-section">
                <label>Prétentions salariales</label>
                <input type="number" placeholder="32 000" />
              </div>

              <div className="form-section">
                <label>Déposez votre CV et lettre de motivation</label>
                <div className="file-upload">
                  <span>Parcourir les fichiers</span>
                  <input type="file" multiple />
                </div>
              </div>

              <button type="submit" className="submit-application">
                Postuler
              </button>
            </form>
          </div>
        </div>
      )}

