// Suppression de la colonne 'Nom' uniquement, sans modifier le reste du code

import React, { useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Table, TableHeader, TableRow, TableHead, TableBody, TableCell } from "@/components/ui/table";

const CardDepartment = () => {
  const [departments, setDepartments] = useState([
    { id: 1, code: "D001" },
    { id: 2, code: "D002" },
    { id: 3, code: "D003" },
  ]);
  const [searchTerm, setSearchTerm] = useState("");

  const filteredDepartments = departments.filter((department) =>
    department.code.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <Card>
      <CardContent>
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-lg font-semibold">Liste des Départements</h2>
          <input
            type="text"
            placeholder="Rechercher un département..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="border p-2 rounded"
          />
        </div>
        <Table>
          <TableHeader>
            <TableRow>
              <TableHead>Code</TableHead>
              <TableHead>Actions</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {filteredDepartments.map((department) => (
              <TableRow key={department.id}>
                <TableCell>{department.code}</TableCell>
                <TableCell>
                  <button className="mr-2 border px-2 py-1 rounded">Modifier</button>
                  <button className="border px-2 py-1 rounded bg-red-500 text-white">Supprimer</button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </CardContent>
    </Card>
  );
};

export default CardDepartment;
