import React from "react";
import { Slide } from "react-slideshow-image";
import "react-slideshow-image/dist/styles.css";

const spanStyle = {
  padding: "15px 25px",
  background: "rgba(0, 0, 0, 0.6)", // Fond semi-transparent pour lisibilité
  color: "#fff",
  borderRadius: "8px",
  fontSize: "24px",
  fontWeight: "bold",
};

const divStyle = {
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  backgroundSize: "cover",
  backgroundPosition: "center",
  width: "100vw", // Prend toute la largeur de l'écran
  height: "100vh", // Prend toute la hauteur de l'écran (tu peux modifier)
};

const slideImages = [
  {
    url: "https://images.unsplash.com/photo-1509721434272-b79147e0e708?ixlib=rb-1.2.1&auto=format&fit=crop&w=1500&q=80",
    caption: "Gestion RH",
  },
  {
    url: "https://images.unsplash.com/photo-1506710507565-203b9f24669b?ixlib=rb-1.2.1&auto=format&fit=crop&w=1536&q=80",
    caption: "Collaboration en entreprise",
  },
  {
    url: "https://images.unsplash.com/photo-1536987333706-fc9adfb10d91?ixlib=rb-1.2.1&auto=format&fit=crop&w=1500&q=80",
    caption: "Technologie et Finance",
  },
];

export default function Home() {
  return (
    <div className="slide-container">
      <Slide autoplay={true} duration={3000} arrows={false} transitionDuration={800}>
        {slideImages.map((slideImage, index) => (
          <div key={index}>
            <div style={{ ...divStyle, backgroundImage: `url(${slideImage.url})` }}>
              <span style={spanStyle}>{slideImage.caption}</span>
            </div>
          </div>
        ))}
      </Slide>
    </div>
  );
}
