import React from "react";
import { Switch, Route, Redirect } from "react-router-dom";

// Components
import Navbar from "components/Navbars/AuthNavbar.js";
import FooterSmall from "components/Footers/FooterSmall.js";

// Views
import Login from "views/auth/Login.js";
import Forget from "views/auth/forget.js";
import Register from "views/auth/Register.js";

export default function Auth() {
  return (
    <>
      <Navbar transparent />
      <main>
        <section className="relative w-full h-full py-40 min-h-screen flex items-center justify-center">
          {/* Background with subtle animation */}
          <div
            className="absolute top-0 w-full h-full bg-gray-50 bg-no-repeat bg-full"
            style={{
              background: "linear-gradient(135deg, #f9fafb 0%, #e5e7eb 100%)",
            }}
          ></div>

          {/* Form Container */}
          <div className="relative w-full max-w-md px-4">
            <div className="bg-white rounded-xl shadow-lg p-8 transform transition-all hover:shadow-xl hover:scale-105">
              <Switch>
                <Route path="/auth/login" exact component={Login} />
                <Route path="/auth/forget" exact component={Forget} />
                <Route path="/auth/register" exact component={Register} />
                <Redirect from="/auth" to="/auth/login" />
              </Switch>
            </div>
          </div>

          {/* Footer */}
          <FooterSmall absolute />
        </section>
      </main>
    </>
  );
}
