import React, { useState } from "react";
import Navbar from "components/Navbars/IndexNavbar";

const Postule = () => {
  // Données d'exemple pour les offres d'emploi
  const [jobListings, setJobListings] = useState([
    {
      id: 1,
      title: "Chef de Produit Marketing Junior",
      location: "Tunisie",
      date: "05/03/2025",
    },
    {
      id: 2,
      title: "IT Business Analyst (AMOA)",
      location: "Tunisie",
      date: "17/02/2025",
    },
    {
      id: 3,
      title: "Chef de Produit Marketing Junior",
      location: "Tunisie",
      date: "05/03/2022",
    },
  ]);

  // État pour la modal de candidature
  const [applicationModal, setApplicationModal] = useState({
    isOpen: false,
    job: null
  });

  // État pour le formulaire de candidature
  const [applicationForm, setApplicationForm] = useState({
    fullName: '',
    email: '',
    phone: '',
    address: '',
    educationLevel: '',
    studyField: '',
    graduationYear: '',
    coverLetter: '',
    cv: null,
    diplomaCertificate: null
  });

  // États de recherche et filtres existants
  const [dateFilter, setDateFilter] = useState("N'importe quand");
  const [isDateExpanded, setIsDateExpanded] = useState(true);
  const [searchKeyword, setSearchKeyword] = useState("");
  const [filteredJobs, setFilteredJobs] = useState([]);
  const [hasSearched, setHasSearched] = useState(false);

  // Ouvrir la modal de candidature
  const openApplicationModal = (job) => {
    setApplicationModal({
      isOpen: true,
      job: job
    });
  };

  // Fermer la modal de candidature
  const closeApplicationModal = () => {
    setApplicationModal({
      isOpen: false,
      job: null
    });
    // Réinitialiser le formulaire
    setApplicationForm({
      fullName: '',
      email: '',
      phone: '',
      address: '',
      educationLevel: '',
      studyField: '',
      graduationYear: '',
      coverLetter: '',
      cv: null,
      diplomaCertificate: null
    });
  };

  // Gérer les changements dans le formulaire
  const handleApplicationFormChange = (e) => {
    const { name, value, files } = e.target;
    setApplicationForm(prev => ({
      ...prev,
      [name]: files ? files[0] : value
    }));
  };

  // Soumettre la candidature
  const handleApplicationSubmit = (e) => {
    e.preventDefault();
    // Validation de base
    const requiredFields = ['fullName', 'email', 'phone', 'educationLevel', 'studyField'];
    const isValid = requiredFields.every(field => applicationForm[field]);

    if (isValid) {
      // Logique d'envoi de candidature (à remplacer par un appel API réel)
      console.log('Candidature soumise:', applicationForm);
      alert('Votre candidature a été envoyée avec succès !');
      closeApplicationModal();
    } else {
      alert('Veuillez remplir tous les champs obligatoires');
    }
  };

  // Méthodes de recherche et filtres existantes restent inchangées
  // (Elles sont copiées du composant original)
  const toggleDateSection = () => {
    setIsDateExpanded(!isDateExpanded);
  };

  const resetFilters = () => {
    setDateFilter("N'importe quand");
    setSearchKeyword("");
    setFilteredJobs([]);
    setHasSearched(false);
  };

  const exactMatch = (text, query) => {
    if (!query || query.trim() === "") return true;

    const formattedText = text.toLowerCase();
    const formattedQuery = query.toLowerCase().trim();

    const queryWords = formattedQuery.split(/\s+/);

    return queryWords.every((word) => {
      const regex = new RegExp(`\\b${word}\\b`, "i");
      return regex.test(formattedText);
    });
  };

  const handleSearch = (e) => {
    e.preventDefault();

    let results = [...jobListings];

    if (searchKeyword && searchKeyword.trim() !== "") {
      results = results.filter((job) => exactMatch(job.title, searchKeyword));
    }

    if (dateFilter === "Sous 30 jours") {
      const today = new Date();
      const thirtyDaysAgo = new Date(today.getFullYear(), today.getMonth(), today.getDate() - 30);

      results = results.filter((job) => {
        const [day, month, year] = job.date.split("/");
        const jobDate = new Date(year, month - 1, day);

        return jobDate >= thirtyDaysAgo && jobDate <= today;
      });
    }

    setFilteredJobs(results);
    setHasSearched(true);
  };

  const displayedJobs = hasSearched ? filteredJobs : jobListings;

  return (
    <div className="careers-page">
      <Navbar />
      
      {/* Existing content remains the same */}
      {/* ... (previous banner and search bar code) ... */}

      <div className="content-container">
        {/* Existing filters container */}
        {/* ... (previous filters code) ... */}

        <div className="job-listings-container">
          <h1>Postes à pourvoir actuellement</h1>

          {hasSearched && (
            <div className="search-results-info">
              <p>
                {filteredJobs.length} offre(s) d'emploi trouvée(s){" "}
                {searchKeyword && `pour "${searchKeyword}"`}
              </p>
              {filteredJobs.length === 0 && (
                <p className="no-results">
                  Aucun résultat trouvé. Veuillez essayer d'autres critères de
                  recherche.
                </p>
              )}
            </div>
          )}

          <div className="job-cards">
            {displayedJobs.map((job) => (
              <div className="job-card" key={job.id}>
                <h3 className="job-title">{job.title}</h3>
                <div className="job-details">
                  <p className="job-location">{job.location}</p>
                  <p className="job-date">{job.date}</p>
                </div>
                <button 
                  className="apply-button" 
                  onClick={() => openApplicationModal(job)}
                >
                  Postuler
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Modal de candidature */}
      {applicationModal.isOpen && (
        <div className="application-modal-overlay">
          <div className="application-modal">
            <h2>Candidature pour : {applicationModal.job.title}</h2>
            <form onSubmit={handleApplicationSubmit}>
              <div className="form-group">
                <label>Nom Complet *</label>
                <input
                  type="text"
                  name="fullName"
                  value={applicationForm.fullName}
                  onChange={handleApplicationFormChange}
                  required
                />
              </div>

              <div className="form-group">
                <label>Email *</label>
                <input
                  type="email"
                  name="email"
                  value={applicationForm.email}
                  onChange={handleApplicationFormChange}
                  required
                />
              </div>

              <div className="form-group">
                <label>Téléphone *</label>
                <input
                  type="tel"
                  name="phone"
                  value={applicationForm.phone}
                  onChange={handleApplicationFormChange}
                  required
                />
              </div>

              <div className="form-group">
                <label>Adresse</label>
                <input
                  type="text"
                  name="address"
                  value={applicationForm.address}
                  onChange={handleApplicationFormChange}
                />
              </div>

              <div className="form-group">
                <label>Niveau d'études *</label>
                <select
                  name="educationLevel"
                  value={applicationForm.educationLevel}
                  onChange={handleApplicationFormChange}
                  required
                >
                  <option value="">Sélectionnez votre niveau</option>
                  <option value="bac">Baccalauréat</option>
                  <option value="licence">Licence</option>
                  <option value="master">Master</option>
                  <option value="doctorat">Doctorat</option>
                  <option value="autre">Autre</option>
                </select>
              </div>

              <div className="form-group">
                <label>Domaine d'études *</label>
                <input
                  type="text"
                  name="studyField"
                  value={applicationForm.studyField}
                  onChange={handleApplicationFormChange}
                  required
                />
              </div>

              <div className="form-group">
                <label>Année de diplomation</label>
                <input
                  type="number"
                  name="graduationYear"
                  value={applicationForm.graduationYear}
                  onChange={handleApplicationFormChange}
                  min="1950"
                  max={new Date().getFullYear()}
                />
              </div>

              <div className="form-group">
                <label>Lettre de motivation</label>
                <textarea
                  name="coverLetter"
                  value={applicationForm.coverLetter}
                  onChange={handleApplicationFormChange}
                  placeholder="Expliquez pourquoi vous êtes le candidat idéal"
                />
              </div>

              <div className="form-group">
                <label>CV *</label>
                <input
                  type="file"
                  name="cv"
                  accept=".pdf,.doc,.docx"
                  onChange={handleApplicationFormChange}
                  required
                />
              </div>

              <div className="form-group">
                <label>Diplôme</label>
                <input
                  type="file"
                  name="diplomaCertificate"
                  accept=".pdf,.doc,.docx,.jpg,.png"
                  onChange={handleApplicationFormChange}
                />
              </div>

              <div className="form-actions">
                <button type="submit" className="submit-button">
                  Envoyer ma candidature
                </button>
                <button 
                  type="button" 
                  className="cancel-button"
                  onClick={closeApplicationModal}
                >
                  Annuler
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Existing styles */}
      <style jsx>{`
        /* ... (previous styles remain the same) ... */

        /* Styles for job card apply button */
        .apply-button {
          background-color: #1a73e8;
          color: white;
          border: none;
          padding: 8px 15px;
          border-radius: 4px;
          cursor: pointer;
          margin-top: 10px;
          transition: background-color 0.3s;
        }

        .apply-button:hover {
          background-color: #1557b0;
        }

        /* Styles for application modal */
        .application-modal-overlay {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          background-color: rgba(0, 0, 0, 0.5);
          display: flex;
          justify-content: center;
          align-items: center;
          z-index: 1000;
        }

        .application-modal {
          background-color: white;
          padding: 30px;
          border-radius: 8px;
          width: 100%;
          max-width: 600px;
          max-height: 90vh;
          overflow-y: auto;
        }

        .application-modal h2 {
          margin-bottom: 20px;
          color: #333;
        }

        .form-group {
          margin-bottom: 15px;
        }

        .form-group label {
          display: block;
          margin-bottom: 5px;
          color: #555;
        }

        .form-group input,
        .form-group select,
        .form-group textarea {
          width: 100%;
          padding: 10px;
          border: 1px solid #ddd;
          border-radius: 4px;
        }

        .form-group textarea {
          min-height: 100px;
        }

        .form-actions {
          display: flex;
          justify-content: space-between;
          margin-top: 20px;
        }

        .submit-button,
        .cancel-button {
          padding: 10px 15px;
          border: none;
          border-radius: 4px;
          cursor: pointer;
          transition: background-color 0.3s;
        }

        .submit-button {
          background-color: #1a73e8;
          color: white;
        }

        .cancel-button {
          background-color: #f1f3f4;
          color: #1a73e8;
        }

        .submit-button:hover {
          background-color: #1557b0;
        }

        .cancel-button:hover {
          background-color: #e6e6e6;
        }
      `}</style>
    </div>
  );
};

export default Postule;
