import React, { useState } from "react";
import "../assets/styles/Style.css";

const Home = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [newOffer, setNewOffer] = useState({
    title: "",
    location: "",
    date: "",
  });
  const [offers, setOffers] = useState([
    {
      title: "Chef de Produit Marketing Junior",
      location: "Tunisie",
      date: "2023-05-06",
    },
  ]);

  // Liste des gouvernorats de la Tunisie
  const gouvernorats = [
    "Ariana",
    "Béja",
    "Ben Arous",
    "Bizerte",
    "Gabès",
    "Gafsa",
    "Jendouba",
    "Kairouan",
    "Kasserine",
    "Kébili",
    "Le Kef",
    "Mahdia",
    "La Manouba",
    "Médenine",
    "Monastir",
    "Nabeul",
    "Sfax",
    "Sidi Bouzid",
    "Siliana",
    "Sousse",
    "Tataouine",
    "Tozeur",
    "Tunis",
    "Zaghouan",
  ];

  const handleAddOffer = () => {
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewOffer({ ...newOffer, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (newOffer.title && newOffer.location && newOffer.date) {
      setOffers([...offers, newOffer]);
      setNewOffer({ title: "", location: "", date: "" });
      setIsModalOpen(false);
    } else {
      alert("Veuillez remplir tous les champs !");
    }
  };

  return (
    <div className="home-container">
      <button className="add-button" onClick={handleAddOffer}>
        Ajouter une offre
      </button>

      {offers.map((offer, index) => (
        <div key={index} className="job-listing">
          <h2 className="job-title">{offer.title}</h2>
          <p className="job-detail">
            <strong>Lieu:</strong> {offer.location}
          </p>
          <p className="job-detail">
            <strong>Date:</strong> {offer.date}
          </p>
        </div>
      ))}

      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Ajouter une nouvelle offre</h2>
            <form onSubmit={handleSubmit}>
              <input
                type="text"
                name="title"
                placeholder="Titre de l'offre"
                className="modal-input"
                value={newOffer.title}
                onChange={handleInputChange}
              />
              {/* Remplacement du champ input par un select */}
              <select
                name="location"
                className="modal-input"
                value={newOffer.location}
                onChange={handleInputChange}
              >
                <option value="">Sélectionnez un gouvernorat</option>
                {gouvernorats.map((gouvernorat, index) => (
                  <option key={index} value={gouvernorat}>
                    {gouvernorat}
                  </option>
                ))}
              </select>
              <input
                type="date"
                name="date"
                className="modal-input"
                value={newOffer.date}
                onChange={handleInputChange}
              />
              <button type="submit" className="modal-button">
                Ajouter
              </button>
              <button
                type="button"
                className="modal-button"
                onClick={handleCloseModal}
              >
                Annuler
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
