import React, { useState } from "react";
import PropTypes from "prop-types";
import { MdDelete, MdVisibility, MdEdit } from "react-icons/md";
import "./CardPersonnels.css"; // Importation du fichier CSS classique

export default function CardPersonnels({ color = "light" }) {
  const [searchTerm, setSearchTerm] = useState("");
  const [sortOption, setSortOption] = useState("");
  const [personnels, setPersonnels] = useState([
    { id: 1, nom: "Jean Dupont", departement: "Informatique" },
    { id: 2, nom: "Sophie Martin", departement: "Ressources Humaines" },
    { id: 3, nom: "Ali Ben Salah", departement: "Finance" },
  ]);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const filteredPersonnels = personnels
    .filter((personnel) => {
      if (!isNaN(searchTerm)) {
        return personnel.id.toString().includes(searchTerm);
      }
      return personnel.nom.toLowerCase().includes(searchTerm.toLowerCase());
    })
    .sort((a, b) => {
      if (!sortOption) return 0;
      if (sortOption === "id-asc") return a.id - b.id;
      if (sortOption === "id-desc") return b.id - a.id;
      if (sortOption === "departement") return a.departement.localeCompare(b.departement);
      return 0;
    });

  const handleAction = (action, id) => {
    switch (action) {
      case "voir":
        alert(`Voir le personnel avec l'ID ${id}`);
        break;
      case "modifier":
        alert(`Modifier le personnel avec l'ID ${id}`);
        break;
      case "supprimer":
        setPersonnels(personnels.filter((personnel) => personnel.id !== id));
        break;
      default:
        break;
    }
  };

  return (
    <div className={`card-container ${color === "light" ? "light-theme" : "dark-theme"}`}>
      <div className="card-header">
        <h3>Liste des personnels</h3>
      </div>
      <div className="card-controls">
        <select
          value={sortOption}
          onChange={(e) => setSortOption(e.target.value)}
          className="dropdown"
        >
          <option value="" disabled>Filtrer par</option>
          <option value="id-asc">ID Ascendant</option>
          <option value="id-desc">ID Descendant</option>
          <option value="departement">Par DÃ©partement</option>
        </select>
        <input
          type="text"
          placeholder="Rechercher par nom ou ID..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="search-input"
        />
        <button onClick={() => setIsModalOpen(true)} className="add-button">Ajouter un personnel</button>
      </div>
      <div className="table-container">
        <table className="personnels-table">
          <thead>
            <tr>
              {["Id", "Nom", "DÃ©partement", "Action"].map((header, index) => (
                <th key={index}>{header}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {filteredPersonnels.map((personnel) => (
              <tr key={personnel.id}>
                <td>{personnel.id}</td>
                <td>{personnel.nom}</td>
                <td>{personnel.departement}</td>
                <td>
                  <button onClick={() => handleAction("voir", personnel.id)} className="btn view"><MdVisibility /></button>
                  <button onClick={() => handleAction("modifier", personnel.id)} className="btn edit"><MdEdit /></button>
                  <button onClick={() => handleAction("supprimer", personnel.id)} className="btn delete"><MdDelete /></button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Ajouter un personnel</h2>
            <button onClick={() => setIsModalOpen(false)} className="close-button">X</button>
            <form>
              <input type="text" placeholder="Nom" className="modal-input" />
              <input type="text" placeholder="DÃ©partement" className="modal-input" />
              <button type="submit" className="modal-button">Enregistrer</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

CardPersonnels.propTypes = {
  color: PropTypes.string,
};
