import React from "react";
import { Slide } from "react-slideshow-image";
import "react-slideshow-image/dist/styles.css";

const divStyle = {
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  backgroundSize: "cover",
  backgroundPosition: "center",
  width: "100vw", // Prend toute la largeur
  height: "500px", // Hauteur ajustable
};

const spanStyle = {
  padding: "15px 25px",
  background: "rgba(0, 0, 0, 0.6)",
  color: "#fff",
  borderRadius: "8px",
  fontSize: "20px",
  fontWeight: "bold",
};

const slideImages = [
  {
    url: "https://www.ubci.tn/wp-content/blogs.dir/2023/12/bancassur.jpg",
    caption: "Bancassurance UBCI",
  },
  {
    url: "https://www.ubci.tn/wp-content/blogs.dir/2025/02/tpe2000X500.jpg",
    caption: "Solution de paiement FlexPay",
  },
  {
    url: "https://www.ubci.tn/wp-content/blogs.dir/2025/02/Banniere1030X320.jpg",
    caption: "Solutions financières innovantes",
  },
];

export default function Home() {
  return (
    <div className="slide-container">
      <Slide autoplay={true} duration={3000} arrows={false} transitionDuration={800}>
        {slideImages.map((slideImage, index) => (
          <div key={index}>
            <div style={{ ...divStyle, backgroundImage: `url(${slideImage.url})` }}>
              <span style={spanStyle}>{slideImage.caption}</span>
            </div>
          </div>
        ))}
      </Slide>
    </div>
  );
}
