import React, { useState } from "react";

const Home = () => {
  const [selectedJob, setSelectedJob] = useState(null);
  const [formData, setFormData] = useState({ name: "", email: "", cv: "" });

  const jobs = [
    { title: "Chef de Produit Marketing Junior", date: "2025-03-05" },
    { title: "IT Business Analyst (AMOA)", date: "2025-02-17" },
    { title: "Chargé Commercial Crédit Conso", date: "2025-02-17" },
    { title: "Chargé de Clientèle Avec Caisse sur Medenine", date: "2025-02-06" },
  ];

  const handleApply = (job) => {
    setSelectedJob(job);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Postulation envoyée pour ${selectedJob.title}`);
    setSelectedJob(null);
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Offres d'emploi</h2>
      <ul style={styles.jobList}>
        {jobs.map((job, index) => (
          <li key={index} style={styles.jobItem} onClick={() => handleApply(job)}>
            <strong>{job.title}</strong>
            <br />
            {job.date}
          </li>
        ))}
      </ul>
      {selectedJob && (
        <div style={styles.modalOverlay}>
          <div style={styles.modal}>
            <h3>Postuler pour {selectedJob.title}</h3>
            <form onSubmit={handleSubmit}>
              <label>Nom:</label>
              <input type="text" name="name" value={formData.name} onChange={handleChange} required />

              <label>Email:</label>
              <input type="email" name="email" value={formData.email} onChange={handleChange} required />

              <label>CV (URL ou fichier):</label>
              <input type="text" name="cv" value={formData.cv} onChange={handleChange} required />

              <button type="submit">Envoyer</button>
              <button type="button" style={styles.closeBtn} onClick={() => setSelectedJob(null)}>Fermer</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

const styles = {
  container: { maxWidth: "800px", margin: "auto", fontFamily: "Arial, sans-serif", textAlign: "center" },
  title: { fontSize: "24px", fontWeight: "bold", marginBottom: "20px" },
  jobList: { listStyle: "none", padding: 0 },
  jobItem: { background: "#f8f8f8", padding: "15px", margin: "10px 0", borderRadius: "8px", cursor: "pointer", transition: "background 0.3s" },
  modalOverlay: { position: "fixed", top: 0, left: 0, width: "100%", height: "100%", background: "rgba(0, 0, 0, 0.5)", display: "flex", justifyContent: "center", alignItems: "center" },
  modal: { background: "white", padding: "20px", borderRadius: "10px", width: "400px" },
  closeBtn: { background: "red", marginTop: "10px", padding: "10px", color: "white", border: "none", borderRadius: "5px", cursor: "pointer" },
};

export default Home;
