import React, { useState } from "react";

const PersonnelManagement = () => {
  const [personnels, setPersonnels] = useState([
    { id: 1, nom: "Dupont", email: "dupont@example.com" },
    { id: 2, nom: "Martin", email: "martin@example.com" },
  ]);

  const [selectedPersonnel, setSelectedPersonnel] = useState(null);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);

  // Fonction pour ouvrir la modale avec les infos du personnel sélectionné
  const openEditModal = (personnel) => {
    setSelectedPersonnel(personnel);
    setIsEditModalOpen(true);
  };

  // Fonction pour fermer la modale
  const closeEditModal = () => {
    setIsEditModalOpen(false);
    setSelectedPersonnel(null);
  };

  // Gestion de la soumission du formulaire
  const handleEditSubmit = (e) => {
    e.preventDefault();

    if (!selectedPersonnel.nom || !selectedPersonnel.email) {
      alert("Veuillez remplir tous les champs !");
      return;
    }

    // Mise à jour de la liste
    setPersonnels((prevPersonnels) =>
      prevPersonnels.map((p) =>
        p.id === selectedPersonnel.id ? selectedPersonnel : p
      )
    );

    // Fermeture de la modale
    closeEditModal();
  };

  return (
    <div className="container mx-auto mt-10 p-4">
      <h2 className="text-xl font-bold mb-4">Gestion des Personnels</h2>

      {/* Tableau d'affichage */}
      <table className="w-full border-collapse border border-gray-300">
        <thead>
          <tr className="bg-gray-200">
            <th className="border p-2">ID</th>
            <th className="border p-2">Nom</th>
            <th className="border p-2">Email</th>
            <th className="border p-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {personnels.map((personnel) => (
            <tr key={personnel.id} className="border">
              <td className="border p-2 text-center">{personnel.id}</td>
              <td className="border p-2 text-center">{personnel.nom}</td>
              <td className="border p-2 text-center">{personnel.email}</td>
              <td className="border p-2 text-center">
                <button
                  className="bg-blue-500 text-white px-3 py-1 rounded"
                  onClick={() => openEditModal(personnel)}
                >
                  Modifier
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Modale d'édition */}
      {isEditModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded shadow-lg w-1/3">
            <h3 className="text-lg font-bold mb-4">Modifier Personnel</h3>
            <form onSubmit={handleEditSubmit}>
              <label className="block mb-2">Nom :</label>
              <input
                type="text"
                value={selectedPersonnel.nom}
                onChange={(e) =>
                  setSelectedPersonnel({ ...selectedPersonnel, nom: e.target.value })
                }
                className="w-full p-2 border rounded mb-4"
              />

              <label className="block mb-2">Email :</label>
              <input
                type="email"
                value={selectedPersonnel.email}
                onChange={(e) =>
                  setSelectedPersonnel({ ...selectedPersonnel, email: e.target.value })
                }
                className="w-full p-2 border rounded mb-4"
              />

              <div className="flex justify-end space-x-2">
                <button
                  type="button"
                  onClick={closeEditModal}
                  className="bg-gray-400 text-white px-4 py-2 rounded"
                >
                  Annuler
                </button>
                <button
                  type="submit"
                  className="bg-green-500 text-white px-4 py-2 rounded"
                >
                  Enregistrer
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default PersonnelManagement;
