import React, { useState } from 'react';

const CardRecrutement = () => {
  // State for job offers
  const [offers, setOffers] = useState([
    { title: 'Chef de Produit Marketing Junior', location: 'Tunisie', date: '2023-05-06' }
  ]);

  // State for add offer modal
  const [isAddOfferModalOpen, setIsAddOfferModalOpen] = useState(false);

  // State for application modal
  const [applicationModal, setApplicationModal] = useState({
    isOpen: false,
    offerTitle: ''
  });

  // State for new offer input
  const [newOffer, setNewOffer] = useState({ title: '', location: '', date: '' });

  // State for application form
  const [applicationForm, setApplicationForm] = useState({
    name: '',
    email: '',
    phone: '',
    cv: null
  });

  // Handler to open add offer modal
  const handleAddOffer = () => {
    setIsAddOfferModalOpen(true);
  };

  // Handler to close add offer modal
  const handleCloseAddOfferModal = () => {
    setIsAddOfferModalOpen(false);
  };

  // Handler for input changes in new offer form
  const handleNewOfferInputChange = (e) => {
    const { name, value } = e.target;
    setNewOffer({ ...newOffer, [name]: value });
  };

  // Handler to submit new offer
  const handleSubmitNewOffer = (e) => {
    e.preventDefault();
    if (newOffer.title && newOffer.location && newOffer.date) {
      setOffers([...offers, newOffer]);
      setNewOffer({ title: '', location: '', date: '' });
      setIsAddOfferModalOpen(false);
    } else {
      alert("Veuillez remplir tous les champs !");
    }
  };

  // Handler to open application modal
  const handleApply = (offerTitle) => {
    setApplicationModal({
      isOpen: true,
      offerTitle: offerTitle
    });
  };

  // Handler to close application modal
  const handleCloseApplicationModal = () => {
    setApplicationModal({ isOpen: false, offerTitle: '' });
    // Reset form data
    setApplicationForm({ name: '', email: '', phone: '', cv: null });
  };

  // Handler for application form input changes
  const handleApplicationInputChange = (e) => {
    const { name, value, files } = e.target;
    setApplicationForm(prev => ({
      ...prev,
      [name]: files ? files[0] : value
    }));
  };

  // Handler to submit application
  const handleSubmitApplication = (e) => {
    e.preventDefault();
    if (applicationForm.name && applicationForm.email && applicationForm.phone) {
      // Here you would typically send the application data to a backend
      console.log('Application submitted:', applicationForm);
      alert('Votre candidature a été envoyée avec succès !');
      handleCloseApplicationModal();
    } else {
      alert("Veuillez remplir tous les champs !");
    }
  };

  // Existing styles from the original component
  const styles = {
    homeContainer: {
      fontFamily: 'Arial, sans-serif',
      padding: '6rem',  
      margin: '0 auto',
    },
    heading: {
      color: '#333',
      textAlign: 'center',
    },
    jobListing: {
      backgroundColor: '#f9f9f9',
      borderLeft: '5px solid #007bff',
      margin: '10px 0',  
      padding: '10px',  
      borderRadius: '5px',
    },
    jobTitle: {
      color: '#007bff',
      marginTop: '0',
    },
    jobDetail: {
      color: '#666',
      lineHeight: '1.6',
    },
    addButton: {
      backgroundColor: '#007bff',
      color: '#fff',
      border: 'none',
      padding: '8px 16px', 
      borderRadius: '5px',
      cursor: 'pointer',
      marginBottom: '15px', 
    },
    modalOverlay: {
      position: 'fixed',
      top: '0',
      left: '0',
      width: '100%',
      height: '100%',
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
    },
    modalContent: {
      backgroundColor: '#fff',
      padding: '20px',
      borderRadius: '5px',
      width: '300px',
    },
    modalInput: {
      width: '100%',
      padding: '8px', 
      margin: '8px 0', 
      borderRadius: '5px',
      border: '1px solid #ccc',
    },
    modalButton: {
      backgroundColor: '#007bff',
      color: '#fff',
      border: 'none',
      padding: '8px 16px', 
      borderRadius: '5px',
      cursor: 'pointer',
      marginRight: '10px',
    },
    // New styles for apply button and application modal
    applyButton: {
      backgroundColor: '#28a745',
      color: '#fff',
      border: 'none',
      padding: '6px 12px',
      borderRadius: '5px',
      cursor: 'pointer',
      marginLeft: '10px',
    },
  };

  return (
    <div style={styles.homeContainer}>
      <button style={styles.addButton} onClick={handleAddOffer}>Ajouter une offre</button>

      {offers.map((offer, index) => (
        <div key={index} style={styles.jobListing}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <div>
              <h2 style={styles.jobTitle}>{offer.title}</h2>
              <p style={styles.jobDetail}><strong>Lieu:</strong> {offer.location}</p>
              <p style={styles.jobDetail}><strong>Date:</strong> {offer.date}</p>
            </div>
            <button 
              style={styles.applyButton} 
              onClick={() => handleApply(offer.title)}
            >
              Postuler
            </button>
          </div>
        </div>
      ))}

      {/* Add Offer Modal - Existing Implementation */}
      {isAddOfferModalOpen && (
        <div style={styles.modalOverlay}>
          <div style={styles.modalContent}>
            <h2>Ajouter une nouvelle offre</h2>
            <form onSubmit={handleSubmitNewOffer}>
              <input
                type="text"
                name="title"
                placeholder="Titre de l'offre"
                style={styles.modalInput}
                value={newOffer.title}
                onChange={handleNewOfferInputChange}
              />
              <input
                type="text"
                name="location"
                placeholder="Lieu"
                style={styles.modalInput}
                value={newOffer.location}
                onChange={handleNewOfferInputChange}
              />
              <input
                type="date"
                name="date"
                style={styles.modalInput}
                value={newOffer.date}
                onChange={handleNewOfferInputChange}
              />
              <button type="submit" style={styles.modalButton}>Ajouter</button>
              <button type="button" style={styles.modalButton} onClick={handleCloseAddOfferModal}>Annuler</button>
            </form>
          </div>
        </div>
      )}

      {/* Application Modal */}
      {applicationModal.isOpen && (
        <div style={styles.modalOverlay}>
          <div style={styles.modalContent}>
            <h2>Postuler pour : {applicationModal.offerTitle}</h2>
            <form onSubmit={handleSubmitApplication}>
              <input
                type="text"
                name="name"
                placeholder="Nom Complet"
                style={styles.modalInput}
                value={applicationForm.name}
                onChange={handleApplicationInputChange}
              />
              <input
                type="email"
                name="email"
                placeholder="Email"
                style={styles.modalInput}
                value={applicationForm.email}
                onChange={handleApplicationInputChange}
              />
              <input
                type="tel"
                name="phone"
                placeholder="Téléphone"
                style={styles.modalInput}
                value={applicationForm.phone}
                onChange={handleApplicationInputChange}
              />
              <input
                type="file"
                name="cv"
                accept=".pdf,.doc,.docx"
                style={styles.modalInput}
                onChange={handleApplicationInputChange}
              />
              <button type="submit" style={styles.modalButton}>Envoyer</button>
              <button 
                type="button" 
                style={styles.modalButton} 
                onClick={handleCloseApplicationModal}
              >
                Annuler
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default CardRecrutement;
