import React, { useState } from 'react';
import './auth/Home.css';

const Home = () => {
  const [credentials, setCredentials] = useState({
    username: '',
    password: ''
  });
  const [rememberMe, setRememberMe] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials({
      ...credentials,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');
    
    if (!credentials.username || !credentials.password) {
      setError('Please enter both username and password.');
      return;
    }
    
    // Simulate login request
    setIsLoading(true);
    
    // Mock API call
    setTimeout(() => {
      setIsLoading(false);
      // For demonstration purposes. In real app, you would handle auth logic here
      console.log('Login attempt:', credentials, 'Remember me:', rememberMe);
    }, 1500);
  };

  const handleSocialLogin = (provider) => {
    console.log(`Attempting to login with ${provider}`);
    // Here you would implement the actual social login logic
    // For example, using Firebase Auth, Auth0, or custom OAuth implementation
  };

  return (
    <div className="login-container">
      <div className="login-form-container">
        <div className="login-header">
          <div className="logo-container">
            <div className="logo">UBCI</div>
            <div className="social-login-text">Connect with</div>
          </div>
        </div>
        
        <form className="login-form" onSubmit={handleSubmit}>
          <h1>Sign In</h1>
          
          {error && <div className="error-message">{error}</div>}
          
          <div className="social-login-buttons">
            <button 
              type="button" 
              className="social-login-button linkedin"
              onClick={() => handleSocialLogin('linkedin')}
            >
              <span className="social-icon">in</span>
              <span>Connect with LinkedIn</span>
            </button>
            
            <button 
              type="button" 
              className="social-login-button gmail"
              onClick={() => handleSocialLogin('gmail')}
            >
              <span className="social-icon">G</span>
              <span>Connect with Gmail</span>
            </button>
          </div>
          
          <div className="separator">
            <span>OR</span>
          </div>
          
          <div className="form-group">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              name="username"
              value={credentials.username}
              onChange={handleChange}
              placeholder="Enter your username"
              disabled={isLoading}
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={credentials.password}
              onChange={handleChange}
              placeholder="Enter your password"
              disabled={isLoading}
            />
          </div>
          
          <div className="form-options">
            <div className="remember-me">
              <input
                type="checkbox"
                id="rememberMe"
                checked={rememberMe}
                onChange={() => setRememberMe(!rememberMe)}
                disabled={isLoading}
              />
              <label htmlFor="rememberMe">Remember me</label>
            </div>
            <a href="/forgot-password" className="forgot-password">Forgot password?</a>
          </div>
          
          <button 
            type="submit" 
            className={`login-button ${isLoading ? 'loading' : ''}`}
            disabled={isLoading}
          >
            {isLoading ? 'Signing In...' : 'Sign In'}
          </button>
          
          <div className="help-section">
            <p>Need help? Contact <a href="mailto:it-support@ubci.com">IT Support</a></p>
          </div>
        </form>
        
        <div className="login-footer">
          <p>&copy; {new Date().getFullYear()} UBCI. All rights reserved.</p>
        </div>
      </div>
    </div>
  );
};

export default Home;
