import { useState } from "react";

const jobs = [
  { title: "Chef de Produit Marketing Junior", date: "2025-03-05" },
  { title: "IT Business Analyst (AMOA)", date: "2025-02-17" },
  { title: "Chargé Commercial Crédit Conso", date: "2025-02-17" },
  { title: "Chargé de Clientèle Avec Caisse sur Medenine", date: "2025-02-06" },
  { title: "Assistant Formation RH", date: "2025-03-05" },
  { title: "Assistant Commercial Corporate sur le Grand Tunis", date: "2025-02-04" },
  { title: "Chargé de Clientèle Avec Caisse sur Monastir", date: "2024-12-12" },
  { title: "Opérateur de marché ALMT", date: "2024-12-11" },
];

const Home = () => {
  const [filter, setFilter] = useState("all");

  const filteredJobs = jobs.filter((job) => {
    if (filter === "last15") {
      return new Date(job.date) >= new Date(new Date().setDate(new Date().getDate() - 15));
    }
    if (filter === "last30") {
      return new Date(job.date) >= new Date(new Date().setDate(new Date().getDate() - 30));
    }
    return true;
  });

  return (
    <div className="max-w-2xl mx-auto p-6">
      <h1 className="text-2xl font-bold text-center mb-4">Offres d'emploi</h1>

      <div className="mb-4">
        <label className="block text-gray-700 font-medium mb-2">Filtrer par date :</label>
        <select
          className="w-full p-2 border rounded"
          onChange={(e) => setFilter(e.target.value)}
          value={filter}
        >
          <option value="all">N'importe quand</option>
          <option value="last15">Sous 15 jours</option>
          <option value="last30">Sous 30 jours</option>
        </select>
      </div>

      <div className="bg-white shadow rounded-lg p-4">
        {filteredJobs.length > 0 ? (
          filteredJobs.map((job, index) => (
            <div key={index} className="p-3 border-b last:border-none">
              <h2 className="text-lg font-semibold">{job.title}</h2>
              <p className="text-gray-500">{job.date}</p>
            </div>
          ))
        ) : (
          <p className="text-center text-gray-500">Aucune offre trouvée.</p>
        )}
      </div>
    </div>
  );
};

export default Home;
