import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Pagination, Autoplay } from "swiper/modules";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";

const Home = () => {
  const images = [
    "https://tse3.mm.bing.net/th?id=OIP.qAGMu5BR4RH5101IHGU3CgHaD0&pid=Api", // UBCI
    "https://tse2.mm.bing.net/th?id=OIP.3oWQtQuGZjMPNLNFYp8qEAHaEe&pid=Api", // Ressources humaines
    "https://tse1.mm.bing.net/th?id=OIP.a_lob_EEYzCxp08yVW8r0QHaE-&pid=Api", // Gestion RH
    "https://tse3.mm.bing.net/th?id=OIP.T5VHd_qnhf3nadLQFWxZUAHaDt&pid=Api", // Finance
    "https://tse4.mm.bing.net/th?id=OIP.Kzxfcvh7KyPgOneF17Yo5gHaEK&pid=Api", // Digitalisation RH
  ];

  return (
    <div className="w-full h-screen flex items-center justify-center bg-gray-100">
      <div className="w-3/4 h-[500px]">
        <Swiper
          modules={[Navigation, Pagination, Autoplay]}
          navigation
          pagination={{ clickable: true }}
          autoplay={{ delay: 3000 }}
          loop={true}
          className="h-full"
        >
          {images.map((src, index) => (
            <SwiperSlide key={index}>
              <img
                src={src}
                alt={`Slide ${index + 1}`}
                className="w-full h-full object-cover rounded-lg shadow-lg"
              />
            </SwiperSlide>
          ))}
        </Swiper>
      </div>
    </div>
  );
};

export default Home;
