import React, { useState } from 'react';

const LeaveRequestForm = () => {
  const [formData, setFormData] = useState({
    startDate: '',
    endDate: '',
    reason: '',
    type: 'congé payé',
    notes: ''
  });
  
  const [submitted, setSubmitted] = useState(false);
  
  const styles = {
    container: {
      backgroundColor: '#F0F2F5',
      minHeight: '100vh',
      padding: '20px',
      fontFamily: 'Arial, sans-serif'
    },
    card: {
      maxWidth: '800px',
      margin: '0 auto',
      backgroundColor: '#FFFFFF',
      borderRadius: '8px',
      boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
      overflow: 'hidden'
    },
    header: {
      backgroundColor: '#1A56DB',
      padding: '20px',
      color: '#FFFFFF',
      textAlign: 'center'
    },
    title: {
      fontSize: '24px',
      fontWeight: 'bold',
      margin: '0'
    },
    subtitle: {
      color: '#E0F0FF',
      marginTop: '5px',
      fontSize: '14px'
    },
    formContainer: {
      padding: '24px'
    },
    formGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(2, 1fr)',
      gap: '20px',
      marginBottom: '24px'
    },
    formGroup: {
      marginBottom: '16px'
    },
    label: {
      display: 'block',
      marginBottom: '6px',
      fontSize: '14px',
      fontWeight: '500',
      color: '#333333'
    },
    input: {
      width: '100%',
      padding: '10px',
      border: '1px solid #DDDDDD',
      borderRadius: '4px',
      fontSize: '14px'
    },
    select: {
      width: '100%',
      padding: '10px',
      border: '1px solid #DDDDDD',
      borderRadius: '4px',
      fontSize: '14px',
      backgroundColor: '#FFFFFF'
    },
    textarea: {
      width: '100%',
      padding: '10px',
      border: '1px solid #DDDDDD',
      borderRadius: '4px',
      fontSize: '14px',
      minHeight: '100px',
      resize: 'vertical'
    },
    alert: {
      backgroundColor: '#E1F5FE',
      borderLeft: '4px solid #039BE5',
      padding: '12px',
      marginBottom: '20px'
    },
    alertText: {
      color: '#0277BD',
      fontSize: '14px',
      margin: '0'
    },
    buttonContainer: {
      display: 'flex',
      justifyContent: 'center',
      gap: '12px'
    },
    cancelButton: {
      padding: '10px 16px',
      backgroundColor: '#FFFFFF',
      color: '#333333',
      border: '1px solid #DDDDDD',
      borderRadius: '4px',
      cursor: 'pointer',
      fontSize: '14px',
      fontWeight: '500'
    },
    submitButton: {
      padding: '10px 16px',
      backgroundColor: '#2563EB',
      color: '#FFFFFF',
      border: 'none',
      borderRadius: '4px',
      cursor: 'pointer',
      fontSize: '14px',
      fontWeight: '500',
      width: '100%',
      maxWidth: '250px',
      textAlign: 'center'
    },
    successAlert: {
      backgroundColor: '#E8F5E9',
      borderLeft: '4px solid #43A047',
      padding: '12px',
      marginBottom: '20px',
      display: 'flex',
      alignItems: 'center'
    },
    successIcon: {
      color: '#43A047',
      marginRight: '8px'
    },
    successText: {
      color: '#2E7D32',
      fontSize: '14px',
      margin: '0'
    },
    summaryTitle: {
      fontSize: '18px',
      fontWeight: '600',
      color: '#333333',
      marginBottom: '16px'
    },
    summaryCard: {
      backgroundColor: '#F5F5F5',
      padding: '16px',
      borderRadius: '4px',
      marginBottom: '24px'
    },
    summaryGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(2, 1fr)',
      gap: '16px'
    },
    summaryLabel: {
      fontSize: '12px',
      color: '#757575',
      marginBottom: '4px'
    },
    summaryValue: {
      fontSize: '14px',
      fontWeight: '500',
      color: '#333333'
    },
    colSpan2: {
      gridColumn: 'span 2'
    },
    cardSection: {
      padding: '20px',
      margin: '10px',
      backgroundColor: '#FFFFFF',
      borderRadius: '8px',
      boxShadow: '0 2px 5px rgba(0, 0, 0, 0.1)',
    },
    categoryTabs: {
      display: 'flex',
      justifyContent: 'center',
      marginBottom: '20px',
      gap: '10px'
    },
    categoryTab: {
      padding: '8px 16px',
      borderRadius: '4px',
      cursor: 'pointer',
      fontSize: '14px',
      fontWeight: '500'
    },
    activeTab: {
      backgroundColor: '#2563EB',
      color: '#FFFFFF'
    },
    inactiveTab: {
      backgroundColor: '#F3F4F6',
      color: '#4B5563'
    },
    leaveTypeCard: {
      padding: '20px',
      borderRadius: '8px',
      boxShadow: '0 2px 5px rgba(0, 0, 0, 0.05)',
      border: '1px solid #EAEAEA',
      marginBottom: '20px'
    },
    leaveTypeTitle: {
      fontSize: '16px',
      fontWeight: 'bold',
      color: '#333333',
      marginBottom: '8px'
    },
    leaveTypeSubtitle: {
      fontSize: '14px',
      color: '#666666',
      marginBottom: '16px'
    },
    availableDays: {
      fontSize: '13px',
      padding: '4px 10px',
      borderRadius: '12px',
      display: 'inline-block',
      backgroundColor: '#EBF3FE',
      color: '#2563EB',
      marginTop: '10px'
    },
    searchBar: {
      display: 'flex',
      alignItems: 'center',
      padding: '8px 12px',
      borderRadius: '4px',
      border: '1px solid #DDDDDD',
      marginBottom: '20px'
    },
    searchIcon: {
      marginRight: '10px',
      color: '#9CA3AF'
    },
    searchInput: {
      border: 'none',
      outline: 'none',
      width: '100%',
      fontSize: '14px'
    }
  };
  
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };
  
  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
  };
  
  const calculateDays = () => {
    if (!formData.startDate || !formData.endDate) return 0;
    
    const start = new Date(formData.startDate);
    const end = new Date(formData.endDate);
    const diffTime = Math.abs(end - start);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24)) + 1;
    
    return diffDays;
  };
  
  // Display the 3 types of leave requests as shown in the interface
  const renderLeaveTypes = () => {
    const leaveTypes = [
      {
        title: '🏖️ Congé Payé Standard',
        subtitle: 'Congé annuel rémunéré',
        availableDays: '15 jours disponibles',
        type: 'congé payé'
      },
      {
        title: '📋 Congé Sans Solde',
        subtitle: 'Absence autorisée non rémunérée',
        availableDays: 'Illimité',
        type: 'congé sans solde'
      },
      {
        title: '🏥 Congé Maladie',
        subtitle: 'Absence pour raison médicale',
        availableDays: '10 jours disponibles',
        type: 'congé maladie'
      }
    ];
    
    return (
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px', justifyContent: 'space-between' }}>
        {leaveTypes.map((leave, index) => (
          <div key={index} style={{ 
            ...styles.leaveTypeCard, 
            flex: '1 0 calc(33% - 20px)',
            minWidth: '200px'
          }}>
            <h3 style={styles.leaveTypeTitle}>{leave.title}</h3>
            <p style={styles.leaveTypeSubtitle}>{leave.subtitle}</p>
            
            <div style={styles.formGroup}>
              <label style={styles.label}>Motif</label>
              <input 
                type="text" 
                placeholder="Précisez le motif de votre demande"
                style={styles.input}
              />
            </div>
            
            <div style={{ display: 'flex', gap: '10px' }}>
              <div style={styles.formGroup}>
                <label style={styles.label}>Date de début</label>
                <input type="date" style={styles.input} />
              </div>
              <div style={styles.formGroup}>
                <label style={styles.label}>Date de fin</label>
                <input type="date" style={styles.input} />
              </div>
            </div>
            
            <div style={styles.availableDays}>{leave.availableDays}</div>
            
            <button style={{
              ...styles.submitButton,
              marginTop: '15px', 
              backgroundColor: '#2563EB',
              width: '100%',
              maxWidth: '100%'
            }}>
              Demander ce congé
            </button>
          </div>
        ))}
      </div>
    );
  };
  
  return (
    <div style={styles.container}>
      <div style={{
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center',
        maxWidth: '1200px',
        margin: '0 auto',
        padding: '0 20px 30px'
      }}>
        <div style={{color: '#333', fontWeight: 'bold', fontSize: '24px'}}>
          Demande de Congé
        </div>
        <div style={{color: '#666', fontSize: '14px'}}>
          Découvrez et soumettez vos demandes de congés en quelques clics
        </div>
      </div>

      <div style={{
        maxWidth: '1200px',
        margin: '0 auto',
        padding: '0 20px'
      }}>
        <div style={styles.searchBar}>
          <span style={styles.searchIcon}>🔍</span>
          <input 
            type="text" 
            placeholder="Rechercher un type de congé..." 
            style={styles.searchInput}
          />
        </div>
        
        <div style={styles.categoryTabs}>
          <div style={{
            ...styles.categoryTab, 
            ...styles.activeTab
          }}>Tous</div>
          <div style={{
            ...styles.categoryTab, 
            ...styles.inactiveTab
          }}>Payés</div>
          <div style={{
            ...styles.categoryTab, 
            ...styles.inactiveTab
          }}>Sans solde</div>
          <div style={{
            ...styles.categoryTab, 
            ...styles.inactiveTab
          }}>Maladie</div>
        </div>
        
        {renderLeaveTypes()}
        
        {submitted && (
          <div style={{
            ...styles.cardSection,
            marginTop: '30px'
          }}>
            <div style={styles.successAlert}>
              <span style={styles.successIcon}>✓</span>
              <p style={styles.successText}>
                Votre demande de congé a été soumise avec succès!
              </p>
            </div>
            
            <h2 style={styles.summaryTitle}>Récapitulatif de votre demande</h2>
            
            <div style={styles.summaryCard}>
              <div style={styles.summaryGrid}>
                <div>
                  <p style={styles.summaryLabel}>Type de congé</p>
                  <p style={styles.summaryValue}>{formData.type}</p>
                </div>
                <div>
                  <p style={styles.summaryLabel}>Nombre de jours</p>
                  <p style={styles.summaryValue}>{calculateDays()} jours</p>
                </div>
                <div>
                  <p style={styles.summaryLabel}>Date de début</p>
                  <p style={styles.summaryValue}>{new Date(formData.startDate).toLocaleDateString('fr-FR')}</p>
                </div>
                <div>
                  <p style={styles.summaryLabel}>Date de fin</p>
                  <p style={styles.summaryValue}>{new Date(formData.endDate).toLocaleDateString('fr-FR')}</p>
                </div>
                <div style={styles.colSpan2}>
                  <p style={styles.summaryLabel}>Motif</p>
                  <p style={styles.summaryValue}>{formData.reason}</p>
                </div>
                {formData.notes && (
                  <div style={styles.colSpan2}>
                    <p style={styles.summaryLabel}>Notes complémentaires</p>
                    <p style={styles.summaryValue}>{formData.notes}</p>
                  </div>
                )}
              </div>
            </div>
            
            <button 
              onClick={() => setSubmitted(false)} 
              style={{...styles.submitButton, marginTop: '20px'}}
            >
              Nouvelle demande
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default LeaveRequestForm;
