import React from "react";
import { Switch, Route, Redirect } from "react-router-dom";

// components
import Navbar from "components/Navbars/AuthNavbar.js";
import FooterSmall from "components/Footers/FooterSmall.js";

// views
import Login from "views/auth/Login.js";
import Forget from "views/auth/Forget";
import Register from "views/auth/Register.js";

export default function Auth() {
  return (
    <>
      <Navbar transparent />
      <main className="relative w-full h-full min-h-screen flex items-center justify-center bg-gray-100">
        {/* Background Image */}
        <div
          className="absolute top-0 left-0 w-full h-full bg-cover bg-center"
          style={{
            backgroundImage: "url('/path-to-high-res-image.jpg')",
            backgroundSize: "cover",
            backgroundPosition: "center",
            filter: "brightness(50%)"
          }}
        ></div>

        {/* Auth Container */}
        <div className="relative z-10 w-full max-w-md bg-white shadow-lg rounded-lg p-8">
          <Switch>
            <Route path="/auth/login" exact component={Login} />
            <Route path="/auth/forget" exact component={Forget} />
            <Route path="/auth/register" exact component={Register} />
            <Redirect from="/auth" to="/auth/login" />
          </Switch>
        </div>
      </main>
      <FooterSmall absolute />
    </>
  );
}
